INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(4, 'MP', 'A : Definisi', '1', 'Ahli Majlis ertinya ahli-ahli yang dilantik mengikut Seksyen 11 Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003'),
(5, 'MP', 'A : Definisi', '2', 'Akaun Wakaf adalah kumpulan wang yang ditubuhkan di bawah Seksyen 39, Enakmen Wakaf (Negeri Selangor) 2003: '),
(6, 'MP', 'A : Definisi', '2.1', 'Kumpulan Wang itu hendaklah terdiri daripada wang dan harta yang diwakafkan atau apa-apa manfaat dan faedah yang diperolehi daripada mawquf atau melalui istibdal. '),
(7, 'MP', 'A : Definisi', '2.2', 'Segala hasil atau perolehan atau keuntungan yang diperolehi daripada mana-mana harta atau penjualan harta wakaf yang diakaunkan di dalam Kumpulan Wang Wakaf. '),
(8, 'MP', 'A : Definisi', '3', 'Akaun Zakat terbahagi kepada 2 akaun: '),
(9, 'MP', 'A : Definisi', '3.1', 'Akaun Korporat merujuk kepada aktiviti perolehan bagi bekalan/perkhidmatan/kerja yang menggunakan peruntukan asnaf amil atau dana yang berkembang melalui pelaburan atau simpanan atau aktiviti perniagaan. '),
(10, 'MP', 'A : Definisi', '3.2', 'Akaun Agihan merujuk kepada aktiviti Perolehan bagi bekalan/perkhidmatan/kerja yang menggunakan peruntukan asnaf Fakir, Miskin, Muallaf, Gharimin, Riqab, Ibnu Sabil dan Fisabilillah. '),
(11, 'MP', 'A : Definisi', '4', 'Baitulmal Majlis ertinya kumpulan wang yang ditubuhkan di bawah Seksyen 81 Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003. '),
(12, 'MP', 'A : Definisi', '4.1', 'Baitulmal adalah terdiri daripada semua wang dan harta, alih atau tak alih yang menurut hukum Syarak atau sumbangan oleh mana-mana orang'),
(13, 'MP', 'A : Definisi', '4.2', 'Mana-mana pelaburan, aset, kumpulan wang atau hasil daripada pelaburan semasa yang terletak hak ke atas Majlis. '),
(14, 'MP', 'A : Definisi', '5', 'Bekalan adalah barangan/peralatan yang diperlukan oleh Majlis. '),
(15, 'MP', 'A : Definisi', '6', 'Enakmen ertinya Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003. '),
(16, 'MP', 'A : Definisi', '7', 'Kerja adalah pembinaan/pembaikan melibatkan kerja kejuruteraan awam, elektrikal dan mekanikal. '),
(17, 'MP', 'A : Definisi', '8', 'Jawatankuasa Pembuka Sebut Harga/Tender adalah suatu jawatankuasa yang bertanggungjawab membuka peti tawaran sebut harga/tender selepas sesuatu sebut harga/tender ditutup. '),
(18, 'MP', 'A : Definisi', '9', 'Jawatankuasa Penilaian Harga Sebut Harga/Tender adalah suatu jawatankuasa yang bertanggungjawab membuat penilaian berdasarkan faktor-faktor seperti syarat pembayaran, kadar pembayaran, tempoh, diskaun, harga pasaran terbuka, keupayaan penyebutharga/petender dan sebagainya. Perbandingan antara satu tawaran penyebutharga/petender dengan tawaran penyebutharga/petender yang lain hendaklah dibuat berasaskan kepada faktor atau unsur yang sama (equal footing). '),
(19, 'MP', 'A : Definisi', '10', 'Jawatankuasa Penilaian Teknikal Sebut Harga/Tender adalah suatu jawatankuasa yang bertanggungjawab membuat penilaian berasaskan kepada spesifikasi dalam dokumen sebut harga/tender. Penilaian hendaklah dibuat berdasarkan dokumen-dokumen yang dikemukakan, pernyataan pematuhan (statement of compliance) dan/atau penilaian fizikal.'),
(20, 'MP', 'A : Definisi', '11', 'Jawatankuasa Sebut Harga/Tender adalah suatu jawatankuasa yang menimbang dan memilih sebut harga/tender yang paling menguntungkan Majlis.'),
(21, 'MP', 'A : Definisi', '12', 'Majlis ertinya Majlis Agama Islam Selangor ditubuhkan di bawah subseksyen 4(1) mengikut Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003. '),
(22, 'MP', 'A : Definisi', '13', 'Panel Pembekal dan Kontraktor bermaksud pembekal-pembekal yang telah disenaraikan oleh Majlis dan diluluskan oleh Mesyuarat Jawatankuasa Sebut Harga MAIS (bekalan, perkhidmatan dan kerja yang nilainya kurang daripada RM500,000.00). '),
(23, 'MP', 'A : Definisi', '14', 'Pegawai Majlis ertinya semua kategori pegawai yang sedang berkhidmat dalam perkhidmatan Majlis yang dilantik oleh Jawatankuasa Sumber Manusia secara tetap, sementara atau kontrak. '),
(24, 'MP', 'A : Definisi', '15', 'Pegawai Pengawal ertinya Setiausaha Majlis yang dilantik di bawah seksyen 16 Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003. '),
(25, 'MP', 'A : Definisi', '16', 'Pegawai Perolehan adalah merujuk kepada mana-mana Pegawai Majlis yang menguruskan perolehan Majlis. '),
(26, 'MP', 'A : Definisi', '17', 'Perkhidmatan adalah sesuatu perkhidmatan   dan/atau kemudahan\nyang  diberikan  dan/atau  disediakan  melalui  kepakaran  dan/atau kemahiran yang diperolehi dalam melaksanakan arahan Majlis. Perkhidmatan boleh dibahagikan kepada perkhidmatan perunding, bukan perunding dan pengamal persendirian.'),
(27, 'MP', 'A : Definisi', '18', 'Perolehan adalah suatu kaedah bagi mendapatkan bekalan, perkhidmatan dan kerja yang diperlukan untuk melaksanakan aktiviti Majlis. Perolehan yang dibuat daripada wang runcit adalah dikecualikan. '),
(28, 'MP', 'B : Am', '1', 'OBJEKTIF'),
(29, 'MP', 'B : Am', '1.1', 'Menguruskan perolehan secara cekap, berkesan dan paling menguntungkan (best value for money). '),
(30, 'MP', 'B : Am', '1.2', 'Mendapatkan harga yang paling mengguntungkan dengan mengambilkira kualiti, kuantiti dan tempoh penyerahan/siap yang ditetapkan. '),
(31, 'MP', 'B : Am', '1.3', 'Mengalakkan pertumbuhan industri tempatan (keutamaan bumiputera beragama Islam dan/atau bukan bumiputera yang beragama Islam) dan penggunaan bekalan dan perkhidmatan tempatan. '),
(32, 'MP', 'B : Am', '2', 'PRINSIP-PRINSIP PEROLEHAN '),
(33, 'MP', 'B : Am', '2.1', 'Akauntabiliti Awam '),
(34, 'MP', 'B : Am', '2.1.1', 'Untuk mencapai satu kawalan yang berkesan bagi memastikan bahawa pengurusan wang Majlis adalah dibuat mengikut undang-undang dan peraturan yang berkaitan. '),
(35, 'MP', 'B : Am', '2.1.2', 'Untuk menyediakan satu sistem berkesan bagi pegawai-pegawai Majlis melaksanakan tanggungjawab yang telah diamanahkan. '),
(36, 'MP', 'B : Am', '2.2', 'Diuruskan secara telus (transparent) \nSemua dasar, peraturan, tatacara dan proses perolehan itu sendiri hendaklah jelas, diketahui dan difahami umum. '),
(37, 'MP', 'B : Am', '2.3', 'Nilai Faedah Terbaik (Best Value For Money) \nPengurusan perolehan hendaklah memberikan pulangan yang terbaik bagi setiap ringgit yang dibelanjakan. '),
(38, 'MP', 'B : Am', '2.4', 'Saingan Terbuka \nProses perolehan hendaklah memberi peluang terbuka kepada semua yang layak untuk bersaing. '),
(39, 'MP', 'B : Am', '2.5', 'Adil Dan Saksama \nSesuatu perolehan itu dipelawa, diproses dan dipertimbangkan dengan adil dan saksama berasaskan dasar dan peraturan yang berkaitan. '),
(40, 'MP', 'B : Am', '3', 'PERANCANGAN PEROLEHAN BEKALAN/ PERKHIDMATAN/ KERJA '),
(41, 'MP', 'B : Am', '3.1', 'Setiap permohonan perolehan bekalan/ perkhidmatan/kerja hendaklah dirancang awal bagi memastikan proses perolehan dilaksanakan mengikut Manual Perolehan. '),
(42, 'MP', 'B : Am', '3.2', 'Pegawai Majlis hendaklah memastikan terlebih dahulu anggaran peruntukkan belanja mengurus atau pembangunan diluluskan oleh mana-mana jawatakuasa Majlis yang berkaitan. '),
(43, 'MP', 'B : Am', '3.3', 'Perolehan hendaklah tertakluk kepada tatacara, peraturan dan kaedah-kaedah yang ditetapkan dan dilaksanakan mengikut had-had dan bidang kuasa yang ditentukan. '),
(44, 'MP', 'B : Am', '3.4', 'Majlis adalah dilarang memecah kecilkan perolehan bekalan, perkhidmatan atau kerja bagi mengelakkan pelawaan sebut harga/tender. '),
(45, 'MP', 'B : Am', '3.5', 'Syarikat yang dimiliki sepenuhnya oleh bumiputera/bukan bumiputera yang beragama Islam juga perlu dipertimbangkan sebagaimana polisi agar dapat membantu syarikat-syarikat yang dimiliki oleh bumiputera/bukan bumiputera yang beragama Islam. '),
(46, 'MP', 'B : Am', '3.6', 'Selaras dengan dasar Majlis untuk mempertingkatkan penggunaan barang-barang tempatan dan usaha ke arah mengurangkan pengaliran wang keluar negeri. Jika munasabah, Majlis hendaklah melaksanakan dasar mengutamakan penggunaan barang-barang buatan tempatan di dalam perolehan bekalan/perkhidmatan/kerja. '),
(47, 'MP', 'B : Am', '3.7', 'Kos pembelian adalah disifatkan meliputi duti kastam, tambang, insuran, pemasangan dan kos-kos lain yang berkaitan. '),
(48, 'MP', 'B : Am', '4', 'AKTIVITI PEROLEHAN '),
(49, 'MP', 'B : Am', '4.1', 'Memastikan peruntukan mencukupi dan/atau mendapat kelulusan berbelanja. '),
(50, 'MP', 'B : Am', '4.2', 'Menentukan jenis dan bilangan bekalan yang dikehendaki. '),
(51, 'MP', 'B : Am', '4.3', 'Membuat perbandingan harga mengenai barangan tersebut (sekiranya perlu). '),
(52, 'MP', 'B : Am', '4.4', 'Menguruskan perolehan. '),
(53, 'MP', 'B : Am', '4.5', 'Memastikan penerimaan dan penyerahan bekalan dan perkhidmatan dilaksanakan mengikut prosedur. '),
(54, 'MP', 'B : Am', '4.6', 'Melantik dan mengurus penyediaan kontrak bagi kerja dan perkhidmatan.'),
(55, 'MP', 'B : Am', '4.7', 'Mengurus tuntutan bayaran bagi bekalan dan perkhidmatan. '),
(56, 'MP', 'B : Am', '5', 'KAJIAN DALAM PASARAN \nMana-mana pegawai Majlis yang menguruskan perolehan bertanggungjawab membuat kajian pasaran yang berpatutan supaya perolehan itu adalah yang paling sesuai dan menguntungkan. Kajian hendaklah mengambil kira aspek kualiti, harga, kegunaan, kos penyelenggaraan dan faktor-faktor lain yang berkaitan. '),
(57, 'MP', 'B : Am', '6', 'JENIS PEROLEHAN \nPerolehan Majlis dibahagikan kepada tiga jenis iaitu bekalan, perkhidmatan dan kerja. '),
(58, 'MP', 'B : Am', '6.1', 'Bekalan \nPerolehan bekalan merujuk kepada alatulis, barangan/peralatan/kelengkapan pejabat, kenderaan, makanan/minuman dan lain-lain yang berkaitan. '),
(59, 'MP', 'B : Am', '6.2', 'Perkhidmatan \nBidang perkhidmatan terbahagi kepada dua iaitu Perkhidmatan Perunding dan Perkhidmatan Bukan Perunding. '),
(60, 'MP', 'B : Am', '6.2.1', 'Perkhidmatan Perunding adalah perkhidmatan kepakaran yang disediakan oleh firma perunding yang berdaftar dengan Majlis dan Perakuan Bumiputera dari Kementerian Kewangan Malaysia/kad perakuan beragama Islam bagi bukan bumiputera dan dikategorikan seperti berikut: '),
(61, 'MP', 'B : Am', '6.2.1(a)', 'Perunding Pembangunan Fizikal'),
(62, 'MP', 'B : Am', '6.2.1(a)i', 'Arkitek, kejuruteraan awam dan struktur, kejuruteraan mekanikal dan elektrikal, ukur bahan, perancang bandar dan desa, landskap, hiasan dalaman, akustik, pengurusan projek, alam sekitar dan lain- \nlain perkhidmatan teknikal yang berkaitan dengan kejuruteraan dan/atau pembinaan;'),
(63, 'MP', 'B : Am', '6.2.1(a)ii', 'kerja-kerja ukur tanah dan kerja ukur khusus seperti hydrography, aerial photogrammetry, remote sensing dan lain-lain; '),
(64, 'MP', 'B : Am', '6.2.1(a)iii.', 'perkhidmatan penilaian fizikal seperti penilaian harta, audit & assessment dan lain-lain; dan '),
(65, 'MP', 'B : Am', '6.2.1(a)iv.', 'kajian-kajian fizikal yang berkaitan dengan kerja-kerja kejuruteraan dan/ atau pembinaan. '),
(66, 'MP', 'B : Am', '6.2.1(b)', 'Perunding Pembangunan Fizikal'),
(67, 'MP', 'B : Am', '6.2.1(b)i', 'Kajian kemungkinan, kajian ekonomi, kajian penswastaan dan kajian-kajian lain; '),
(68, 'MP', 'B : Am', '6.2.1(b)ii', 'Perkhidmatan pengurusan sumber manusia, kursus, latihan, kewangan; '),
(69, 'MP', 'B : Am', '6.2.1(b)iii.', 'Perakaunan, percukaian, pengurusan kualiti, kerja-kerja audit kewangan, kerja-kerja audit pengurusan, kerja penyenggaraan dan pembaikan, teknologi maklumat dan komunikasi serta bidang-bidang pengurusan lain; '),
(70, 'MP', 'B : Am', '6.2.1(b)iv.', ' Perkhidmatan perundangan; '),
(71, 'MP', 'B : Am', '6.2.1(b)v.', 'Pencucian dan pembersihan, penyewaan dan pengurusan bangunan; '),
(72, 'MP', 'B : Am', '6.2.1(b)vi.', 'Kawalan keselamatan; dan '),
(73, 'MP', 'B : Am', '6.2.1(b)vii.', 'Perkhidmatan-perkhidmatan lain. '),
(74, 'MP', 'B : Am', '6.2.2', 'Perkhidmatan Bukan Perunding meliputi perkhidmatan seperti berikut: '),
(75, 'MP', 'B : Am', '6.2.2(a)', 'Pengendalian kursus dan latihan. '),
(76, 'MP', 'B : Am', '6.2.2(b)', 'Penyelenggaraan dan pembaikan. '),
(77, 'MP', 'B : Am', '6.2.2(c)', 'Pencucian dan pembersihan. '),
(78, 'MP', 'B : Am', '6.2.2(d)', 'Penyewaan dan pengurusan bangunan. '),
(79, 'MP', 'B : Am', '6.2.2(e)', 'Perkhidmatan-perkhidmatan lain. '),
(80, 'MP', 'B : Am', '6.3', 'Kerja \nPerolehan kerja melibatkan kerja-kerja pembinaan/ penyelenggaraan/ pengubahsuaian/ pembaikan/ menaiktaraf yang meliputi struktur bangunan, pendawaian elektrik, sistem perpaipan dan sistem pembentungan. Ia juga termasuk kerja-kerja penyelenggaraan tanah yang meliputi pembersihan, pemasangan pagar dan pemasangan papan tanda. '),
(81, 'MP', 'B : Am', '7', 'KAEDAH-KAEDAH PEROLEHAN\n(BEKALAN/PERKHIDMATAN/KERJA) '),
(82, 'MP', 'B : Am', '7.1', 'Pembelian Terus'),
(83, 'MP', 'B : Am', '7.2', 'Sebut harga '),
(84, 'MP', 'B : Am', '7.3', 'Tender '),
(85, 'MP', 'B : Am', '7.4', 'Perolehan Darurat '),
(86, 'MP', 'B : Am', '7.5', 'Perolehan melalui Perlanjutan/Perubahan Kontrak Kerja '),
(87, 'MP', 'B : Am', '8', 'SYARAT-SYARAT PELANTIKAN PEMBEKAL/KONTRAKTOR/PERUNDING MAJLIS AGAMA ISLAM SELANGOR '),
(88, 'MP', 'B : Am', '8.1', 'Berdaftar dengan Majlis. '),
(89, 'MP', 'B : Am', '8.2', 'Berdaftar dengan Kementerian Kewangan Malaysia. '),
(90, 'MP', 'B : Am', '8.3', 'Dalam tempoh sah pengiktirafan pendaftaran syarikat Suruhanjaya Syarikat Malaysia (SSM). '),
(91, 'MP', 'B : Am', '8.4', 'Berdaftar dengan Lembaga Pembangunan Industri Pembinaan Malaysia (CIDB) dan mempunyai tempoh sah bagi perbekalan yang berkaitan. '),
(92, 'MP', 'B : Am', '8.5', 'Syarikat dan/atau perniagaan tidak pernah diisytihar bankrap atau digulungkan oleh mahkamah. '),
(93, 'MP', 'B : Am', '8.6', 'Keutamaan diberikan kepada pembekal bumiputera beragama Islam dan bukan bumiputera beragama Islam. '),
(94, 'MP', 'B : Am', '9', 'SYARAT-SYARAT PELANTIKAN PANEL PEMBEKAL/ KONTRAKTOR/ PERUNDING MAJLIS AGAMA ISLAM SELANGOR '),
(95, 'MP', 'B : Am', '9.1', 'Pembekal mesti berdaftar dengan Majlis dan mematuhi syarat-syarat pelantikan oleh Majlis seperti di perkara 8. '),
(96, 'MP', 'B : Am', '9.2', 'Panel mestilah dilantik secara rasmi oleh Majlis'),
(97, 'MP', 'B : Am', '10', 'PETI TAWARAN SEBUT HARGA/TENDER '),
(98, 'MP', 'B : Am', '10.1', 'Peti tawaran hendaklah disediakan dengan dilabelkan tajuk/rujukan sebut harga/tender, tarikh dan waktu tawaran ditutup. '),
(99, 'MP', 'B : Am', '10.2', 'Peti tawaran hendaklah dikunci dengan dua kunci yang berbeza dan anak kuncinya hendaklah dipegang secara berasingan oleh dua orang pegawai Majlis yang dilantik secara bertulis oleh Pegawai Pengawal. '),
(100, 'MP', 'B : Am', '10.3', 'Peti tawaran hendaklah ditutup pada tarikh dan waktu yang ditetapkan. Waktu sebut harga/tender ditutup hendaklah ditetapkan pada jam 12.00 tengah hari pada hari bekerja. Bagi tawaran yang diterima melalui pos, Majlis hendaklah memastikan langkah-langkah diambil supaya tawaran dimasukkan ke dalam peti tawaran sebaik sahaja tawaran diterima dan hendaklah sebelum tarikh dan waktu ditutup. Dokumen sebut harga yang lewat diterima dari waktu dan tarikh yang ditetapkan tidak boleh dipertimbangkan. '),
(101, 'MP', 'B : Am', '11', 'JAWATANKUASA PEMBUKA SEBUT HARGA/TENDER '),
(102, 'MP', 'B : Am', '11.1', 'Pelantikan Jawatankuasa Pembuka Sebut Harga/Tender \nJawatankuasa Pembuka Sebut Harga/Tender yang dilantik oleh Pegawai Pengawal secara bertulis. '),
(103, 'MP', 'B : Am', '11.2', 'Keanggotaan Jawatankuasa Pembuka Sebut Harga/Tender '),
(104, 'MP', 'B : Am', '11.2.1', 'Majlis hendaklah melantik Ahli Jawatankuasa Pembuka Sebut Harga/Tender yang terdiri daripada sekurang-kurangnya dua pegawai yang mana seorang daripadanya hendaklah terdiri daripada pegawai Kumpulan Pengurusan dan Profesional. Majlis boleh melantik seberapa banyak ahli yang dikehendaki. '),
(105, 'MP', 'B : Am', '11.2.2', 'Kuorum bagi Jawatankuasa Pembuka Sebut Harga/Tender bersidang hendaklah terdiri daripada sekurang-kurangnya dua pegawai yang mana seorang pengerusi dan ahli. '),
(106, 'MP', 'B : Am', '11.2.3', 'Sekiranya pengerusi tidak dapat hadir ketika Jawatankuasa Pembuka Sebut Harga/Tender bersidang dengan sebab-sebab yang kukuh, maka salah seorang ahli hendaklah dipertanggungjawabkan untuk menggantikan tempat pengerusi dan memastikan kuorum mencukupi. '),
(107, 'MP', 'B : Am', '11.3', 'Tugas-Tugas Jawatankuasa Pembuka Sebut Harga '),
(108, 'MP', 'B : Am', '11.3.1', 'Membuka peti tawaran pada tarikh dan waktu yang ditetapkan. Jika ini tidak dapat diuruskan, Urus setia Jawatankuasa Pembuka Sebut Harga hendaklah menguruskan supaya Peti Sebut Harga/Tender itu dilakri pada waktu tutup yang ditetapkan dan selepas itu membuat urusan supaya peti itu dibuka oleh Jawatankuasa Pembuka Jawatankuasa Sebut Harga/Tender secepat mungkin dan selewat-lewatnya sehari.'),
(109, 'MP', 'B : Am', '11.3.2', 'Memberi nombor kod untuk setiap dokumen tawaran sebut harga '),
(110, 'MP', 'B : Am', '11.3.3', 'Menandatangani ringkas pada lembaran yang menyatakan harga dan pada apa-apa pindaan tulisan atau taipan bertindih dalam tawaran Sebut Harga. '),
(111, 'MP', 'B : Am', '11.3.4', 'Merekod pada Jadual Pembuka Sebut Harga dengan mencatatkan kod penyebutharga, nama penyebutharga, harga yang ditawarkan, kod bidang berkaitan, penyerahan dokumen, penyata bulanan bank, menghadiri lawatan tapak, tempoh siap/tempoh penyerahan dan menandatangani borang tersebut. '),
(112, 'MP', 'B : Am', '11.3.5', 'Memastikan bahawa tawaran harga dan tawaran teknikal telah dikemukakan dalam dua sampul surat yang berasingan dan berlakri bagi bekalan/perkhidmatan. '),
(113, 'MP', 'B : Am', '11.3.6', 'Mengesahkan penyebutharga telah mengemukakan semua dokumen yang diperlukan seperti dalam Senarai Semakan. '),
(114, 'MP', 'B : Am', '11.4', 'Tugas-Tugas Jawatankuasa Pembuka Tender '),
(115, 'MP', 'B : Am', '11.4.1', 'Peti tawaran dibuka secepat mungkin selepas tender ditutup. '),
(116, 'MP', 'B : Am', '11.4.2', 'Setiap tawaran tender yang diterima, dibuka dan disemak nombor siri dengan rekod penjualan dokumen. '),
(117, 'MP', 'B : Am', '11.4.3', 'Nombor kod hendaklah diberi untuk setiap dokumen dan senarai harga, dokumen spesifikasi teknikal dan katalog/brosur petender serta ditandatangani ringkas. '),
(118, 'MP', 'B : Am', '11.4.4', 'Lembaran yang menyatakan harga dan apa-apa pindaan yang terdapat dalam tawaran tender hendaklah ditandatangani ringkas. '),
(119, 'MP', 'B : Am', '11.4.5', 'Nama petender dipastikan tidak tercatat di dalam dokumen spesifikasi teknikal petender dan sekiranya terdapat nama/tanda syarikat dalam dokumen tersebut, maka ianya hendaklah dicatatkan dalam borang jadual tender. '),
(120, 'MP', 'B : Am', '11.4.6', 'Dokumen tawaran harga dijadualkan dengan menggunakan nombor kod yang diberikan. '),
(121, 'MP', 'B : Am', '11.4.7', 'Menyedia dan melengkapkan senarai semakan dan borang jadual tender dengan mencatatkan nama petender, kod petender, amaun yang di tender, tempoh siap atau tempoh penyerahan. Nama dan jawatan semua ahli Pembuka Tender hendaklah dicatatkan dalam borang jadual tender dan ditandatangani oleh setiap ahli.'),
(122, 'MP', 'B : Am', '11.4.8', 'Menyediakan Jadual Harga Tender untuk dipamerkan di papan kenyataan Majlis seberapa segera yang boleh, dengan tidak menzahirkan nama petender yang mengambil bahagian. Bagi tender yang melibatkan keselamatan, Majlis tidak diwajibkan mempamerkan jadual harga tersebut. '),
(123, 'MP', 'B : Am', '12', 'JAWATANKUASA PENILAIAN SEBUT HARGA/TENDER Penilaian sebut harga bekalan/perkhidmatan/kerja hendaklah dibuat oleh dua jawatankuasa yang berasingan seperti berikut: \n(a) Jawatankuasa Penilaian Teknikal \n(b) Jawatankuasa Penilaian Harga '),
(124, 'MP', 'B : Am', '12.1', 'Pelantikan Jawatankuasa Penilaian Sebut Harga/Tender \nPelantikan ahli Jawatankuasa Penilaian Sebut Harga/Tender hendaklah dibuat secara bertulis oleh Pegawai Pengawal. '),
(125, 'MP', 'B : Am', '12.2', 'Keanggotaan Jawatankuasa Penilaian Teknikal Sebut Harga/Tender '),
(126, 'MP', 'B : Am', '12.2.1', 'Majlis hendaklah melantik Ahli Jawatankuasa Penilaian Teknikal yang terdiri daripada sekurang-kurangnya tiga pegawai, iaitu seorang pengerusi dan dua orang ahli. Majlis boleh melantik seberapa banyak ahli yang dikehendaki. '),
(127, 'MP', 'B : Am', '12.2.2', 'Kuorum bagi Jawatankuasa Penilaian Teknikal bersidang hendaklah terdiri daripada sekurang-kurangnya tiga pegawai yang mana seorang pengerusi dan dua orang ahli. '),
(128, 'MP', 'B : Am', '12.2.3', 'Sekiranya pengerusi tidak dapat hadir ketika Jawatankuasa Penilaian Teknikal Sebut Harga/Tender bersidang dengan sebab-sebab yang kukuh, maka salah seorang ahli hendaklah dipertanggungjawabkan untuk menggantikan tempat pengerusi dan memastikan kuorum mencukupi. '),
(129, 'MP', 'B : Am', '12.2.4', 'Ahli-ahli Jawatankuasa Penilaian Teknikal hendaklah terdiri daripada pegawai yang mahir, berpengalaman dan berkelayakan tentang barangan/perkhidmatan tersebut. '),
(130, 'MP', 'B : Am', '12.2.5', 'Sekiranya Majlis tidak mempunyai kemahiran dalaman, kepakaran dari Jabatan Teknik atau Jabatan lain boleh digunakan. '),
(131, 'MP', 'B : Am', '12.3', 'Tugas-Tugas Jawatankuasa Penilaian Teknikal Sebut Harga/Tender \nJawatankuasa Penilaian Teknikal hendaklah membuat penilaian berasaskan kepada spesifikasi perolehan dalam dokumen Sebut Harga. Penilaian hendaklah dibuat berdasarkan dokumen-dokumen yang dikemukakan, pernyataan pematuhan (statement of compliance) dan/atau penilaian fizikal. Walau bagaimanapun, maklumat dan teknologi semasa mengenai barang/perkhidmatan tersebut boleh diambil kira. '),
(132, 'MP', 'B : Am', '12.4', 'Keanggotaan   Jawatankuasa Penilaian   Harga   Sebut\nHarga/Tender'),
(133, 'MP', 'B : Am', '12.4.1', 'Majlis hendaklah melantik Ahli Jawatankuasa Penilaian Harga yang terdiri daripada sekurang-kurangnya tiga pegawai, iaitu seorang pengerusi dan dua orang ahli. Majlis boleh melantik seberapa banyak ahli yang dikehendaki. '),
(134, 'MP', 'B : Am', '12.4.2', 'Kuorum bagi Jawatankuasa Penilaian Harga bersidang hendaklah terdiri daripada sekurang-kurangnya tiga pegawai yang mana seorang pengerusi dan dua orang ahli. '),
(135, 'MP', 'B : Am', '12.4.3', 'Sekiranya pengerusi tidak dapat hadir ketika Jawatankuasa Penilaian Harga Sebut Harga/Tender bersidang dengan sebab-sebab yang kukuh, maka salah seorang ahli hendaklah dipertanggungjawabkan untuk menggantikan tempat pengerusi dan memastikan kuorum mencukupi.'),
(136, 'MP', 'B : Am', '12.5', 'Tugas-Tugas Jawatankuasa Penilaian Harga Sebut Harga/Tender '),
(137, 'MP', 'B : Am', '12.5.1', 'Penilaian harga sesuatu Sebut Harga hendaklah mengambil kira faktor-faktor seperti syarat pembayaran, kadar pembayaran, tempoh, diskaun, harga pasaran terbuka, keupayaan penyebut harga dan sebagainya. Perbandingan antara satu tawaran penyebut harga dengan tawaran penyebut harga yang lain hendaklah dibuat berasaskan kepada faktor atau unsur yang sama (equal footing). '),
(138, 'MP', 'B : Am', '12.5.2', 'Penilaian bagi pembelian harta modal seperti kenderaan, jentera berat, sistem maklumat dan lain-lain hendaklah berdasarkan kepada prinsip tawaran harga menyeluruh (total cost bid), yang bermaksud mengambil kira harga barangan, alat ganti, latihan, pentauliahan/pemasangan, jaminan, penyelenggaraan dan lain-lain. '),
(139, 'MP', 'B : Am', '12.5.3', 'Bagi perolehan bekalan dan perkhidmatan yang tidak kompleks seperti perolehan pakaian sukan, kasut, perabot, ubat, rangsum atau catuan basah/kering, beg, alat tulis, peralatan pejabat dan sebagainya, memadai Majlis bersidang pada satu Jawatankuasa Penilaian sahaja.'),
(140, 'MP', 'B : Am', '13', 'JAWATANKUASA SEBUT HARGA'),
(141, 'MP', 'B : Am', '13.1', 'Keanggotaan Jawatankuasa Sebut Harga \nJawatankuasa Sebut Harga Majlis hendaklah terdiri daripada yang berikut: \na) Pengerusi : Setiausaha Majlis\nb) Ahli : Dua orang Pengarah/Ketua\n   Bahagian/jawatan  yang\n   setara\nc) Setiausaha  :  Ketua Bahagian \nMesyuarat Pentadbiran/jawatan yang\nsetara'),
(142, 'MP', 'B : Am', '13.2', 'Tugas-Tugas Jawatankuasa Sebut Harga '),
(143, 'MP', 'B : Am', '13.2.1', 'Bagi perolehan kerja seorang daripada Ahli Jawatankuasa Sebut Harga hendaklah terdiri daripada Pegawai Teknikal. '),
(144, 'MP', 'B : Am', '13.2.2', '13.2.2 Jawatankuasa Sebut Harga hendaklah bersidang secepat mungkin supaya keputusan dapat dibuat dengan segera. Jawatankuasa Sebut Harga hendaklah seboleh-bolehnya bersidang dalam tempoh 14 hari bekerja selepas penilaian dibuat. '),
(145, 'MP', 'B : Am', '13.2.3', 'Ahli Jawatankuasa Sebut Harga tidak boleh menganggotai Jawatankuasa Pembuka Sebut Harga atau mana-mana Jawatankuasa Penilaian. '),
(146, 'MP', 'B : Am', '14', 'JAWATANKUASA TENDER'),
(147, 'MP', 'B : Am', '14.1', 'Keanggotaan Jawatankuasa Tender \nJawatankuasa Tender Majlis hendaklah terdiri daripada yang berikut: \na) Pengerusi : Pengerusi Majlis\nb) Ahli :  Tiga   orang   Ahli   Majlis   dan\n   Setiausaha MAIS\nc) Setiausaha : Pengarah Pengurusan\n Mesyuarat  Korporat/jawatan yang setara'),
(148, 'MP', 'B : Am', '14.2', '14.2 Tugas-Tugas Jawatankuasa Tender '),
(149, 'MP', 'B : Am', '14.2.1', 'Tugas Ahli Jawatankuasa Tender MAIS ialah menimbang dan menyokong sesuatu perolehan bekalan/perkhidmatan/kerja yang paling menguntungkan dengan mengambil kira harga, kualiti, perkhidmatan, kegunaan barang itu dan faktor-faktor lain yang bersangkutan. '),
(150, 'MP', 'B : Am', '14.2.2', 'Ahli-Ahli Jawatankuasa hendaklah memeriksa salinan iklan yang mempelawa tender, penentuan, jadual tender dan di mana berkenaan laporan ujian dan pemeriksaan. '),
(151, 'MP', 'B : Am', '14.2.3', 'Ahli Jawatankuasa hendaklah berpuashati bahawa petender mempunyai keupayaan teknik dan kewangan bagi melaksanakan perkhidmatan yang ditendernya itu dengan memuaskan. '),
(152, 'MP', 'B : Am', '14.2.4', 'Jika Ahli Jawatankuasa berpuashati bahawa acara pembelian tidak diikuti dengan sepatutnya atau mengesyaki perkara luar aturan, ia boleh mengesyorkan panggilan semula atau cara-cara perolehan lain. '),
(153, 'MP', 'B : Am', '14.2.5', 'Jika   tender   yang   lewat   diterima   dan   Ahli \nJawatankuasa Tender berpuas hati bahawa kelewatan tender itu diterima adalah disebabkan oleh kelewatan Pejabat Pos menyerahkan, maka tender itu boleh ditimbangkan bersama dengan tender-tender yang lain.'),
(154, 'MP', 'B : Am', '15', 'URUS SETIA SEBUT HARGA/TENDER \nUrus Setia Sebut Harga/Tender bertanggungjawab menguruskan semua urusan berkaitan sebut harga/tender dari mula sehingga proses selesai. Tugas urus setia adalah seperti berikut: '),
(155, 'MP', 'B : Am', '15(a)', 'Menguruskan perancangan awal dan jadual pelaksanaan tender. '),
(156, 'MP', 'B : Am', '15(b)', 'Mengurus dan menyediakan dokumen sebut harga/tender. '),
(157, 'MP', 'B : Am', '15( c )', 'Menguruskan iklan atau pelawaan sebut harga/tender. '),
(158, 'MP', 'B : Am', '15(d)', 'Menyediakan rekod penjualan dokumen tawaran dan mengatur lawatan tapak/premis (jika berkenaan). '),
(159, 'MP', 'B : Am', '15(e)', 'Menutup peti sebut harga/tender pada tarikh dan masa tutup yang ditetapkan. '),
(160, 'MP', 'B : Am', '15(f)', 'Menjaga keselamatan peti sebut harga/tender dan dokumen sebut harga/tender. '),
(161, 'MP', 'B : Am', '15(g)', 'Mengatur pembukaan sebut harga/tender. '),
(162, 'MP', 'B : Am', '15(h)', 'Mengatur penilaian sebut harga/tender. '),
(163, 'MP', 'B : Am', '15(i)', 'Mengatur mesyuarat Jawatankuasa Sebut Harga MAIS dan mesyuarat Jawatankuasa Tender MAIS. '),
(164, 'MP', 'B : Am', '16', 'ANGGARAN HARGA MAJLIS '),
(165, 'MP', 'B : Am', '16.1', 'Penyediaan Anggaran Harga Sebut Harga dan Tender '),
(166, 'MP', 'B : Am', '16.1.1', 'Majlis hendaklah menyediakan anggaran harga bagi setiap sebut harga dan tender berdasarkan kepada kajian pasaran serta Jadual Kadar Kerja. '),
(167, 'MP', 'B : Am', '16.1.2', 'Bagi perolehan Bekalan/Perkhidmatan yang sukar ditentukan harga anggaran, Majlis boleh menggunakan harga pembelian lampau perolehan berkenaan sebagai rujukan. '),
(168, 'MP', 'B : Am', '17', 'BON PELAKSANAAN \nBon Pelaksanaan adalah merupakan suatu aku janji tidak bersyarat (unconditional undertaking) oleh sesuatu institusi kewangan yang dibenarkan oleh Baitulmal untuk dibayar kepada Majlis suatu amaun tertentu atas tuntutan (on demand) sekiranya kontraktor tidak atau gagal mematuhi dan melaksanakan obligasinya di bawah kontrak. '),
(169, 'MP', 'C : Tatacara', '1', 'PEMBELIAN TERUS '),
(170, 'MP', 'C : Tatacara', '1.1', 'Bekalan/Perkhidmatan '),
(171, 'MP', 'C : Tatacara', '1.1.1', 'Pembelian terus bagi bekalan/perkhidmatan yang melibatkan jumlah perbelanjaan tahunan tidak melebihi daripada RM50,000.00 hendaklah dibuat dengan pesanan secara terus kepada syarikat pilihan yang mengenakan harga yang berpatutan. '),
(172, 'MP', 'C : Tatacara', '1.1.2', 'Pembelian terus bagi bekalan ini hendaklah mempelawa tiga sebut harga bagi tujuan perbandingan sekiranya perlu. Hanya satu sebut harga memadai jika mempelawa kepada Panel. '),
(173, 'MP', 'C : Tatacara', '1.1.3', 'Pembelian terus bagi perkhidmatan dengan nilai melebihi RM10,000.00 ke atas sebaik-baiknya mempelawa lebih dari satu Sebut Harga. '),
(174, 'MP', 'C : Tatacara', '1.2', 'Kerja '),
(175, 'MP', 'C : Tatacara', '1.2.1', 'Lantikan terus bagi kerja yang melibatkan jumlah tidak lebih daripada RM20,000.00 boleh dibuat dengan pesanan terus kepada syarikat pilihan yang diketahui sentiasa mengenakan harga yang berpatutan. '),
(176, 'MP', 'C : Tatacara', '1.2.2', 'Lantikan terus bagi kerja hendaklah mempelawa sekurang-kurangya satu Sebut Harga dari kontraktor yang berdaftar dengan MAIS atau Panel Kontraktor. '),
(177, 'MP', 'C : Tatacara', '1.2.3', 'Kerja-kerja yang bernilai tidak melebihi RM20,000.00 boleh dibuat secara lantikan terus dikalangan kontraktor gred G1 yang bedaftar dengan CIDB. '),
(178, 'MP', 'C : Tatacara', '1.3', 'Syarat-Syarat Pembelian Terus (Bekalan/Perkhidmatan) '),
(179, 'MP', 'C : Tatacara', '1.3.1', 'Pegawai perolehan hendaklah memastikan pemohon mengisi borang perolehan sebelum sesuatu perolehan dibuat. '),
(180, 'MP', 'C : Tatacara', '1.3.2', 'Pegawai perolehan hendaklah mendapatkan Sebut Harga daripada pembekal yang berdaftar dengan Majlis melalui pemberitahuan secara terus/telefon/emel/faks/mel. '),
(181, 'MP', 'C : Tatacara', '1.3.3', 'Pembekal hendaklah diberi tempoh tujuh hari bekerja dari tarikh pemberitahuan dihantar untuk mengemukakan sebut harga. '),
(182, 'MP', 'C : Tatacara', '1.3.4', 'Pegawai Perolehan hendaklah membuat pemilihan bagi sebut harga yang berpatutan, berkualiti, menguntungkan dan menepati keperluan untuk disyorkan kepada Pegawai Pengawal. '),
(183, 'MP', 'C : Tatacara', '1.3.5', 'Pembelian Terus hendaklah melalui Pesanan Tempatan (LO). Pesanan Tempatan mestilah disediakan dalam tiga salinan pada masa barang dipesan, salinan asal dan pendua dihantar kepada pembekal:'),
(184, 'MP', 'C : Tatacara', '1.3.5(a)', 'Salinan asal dan pendua dihantar kepada pembekal. Salinan asal hendaklah dikembalikan oleh pembekal bersama-sama bil atau invois asal. Pembayaran tidak boleh dibuat jika salinan asal pesanan tempatan tidak dikembarkan bil asal atau invois. '),
(185, 'MP', 'C : Tatacara', '1.3.5(b)', 'Salinan kedua boleh disimpan oleh pembekal dan salinan ketiga hendaklah ditinggalkan dalam buku untuk rekod pejabat. '),
(186, 'MP', 'C : Tatacara', '1.3.6', 'Pesanan Tempatan hendaklah ditandatangani oleh pegawai pengawal atau pegawai yang diberi kuasa. '),
(187, 'MP', 'C : Tatacara', '1.4', 'Syarat-Syarat Pembelian Terus (Kerja) '),
(188, 'MP', 'C : Tatacara', '1.4.1', 'Sesuatu perolehan kerja boleh dilaksanakan setelah mendapat pengesahan Jurutera dan kelulusan Pegawai Pengawal. '),
(189, 'MP', 'C : Tatacara', '1.4.2', 'Lantikan terus hendaklah melalui Inden Kerja yang diluluskan oleh Pegawai Pengawal atau pegawai yang diberi kuasa. '),
(190, 'MP', 'C : Tatacara', '1.4.3', 'Inden Kerja mestilah disediakan dalam tiga salinan pada masa kerja ditawarkan, salinan pendua diserahkan kepada kontraktor.'),
(191, 'MP', 'C : Tatacara', '1.4.4', 'Pegawai Pengawal atau pegawai yang diberi kuasa hendaklah meluluskan Inden Kerja selepas kerja-kerja disahkan siap untuk tujuan pembayaran. '),
(192, 'MP', 'C : Tatacara', '1.5', 'Panduan Pembelian Terus/Lantikan Terus (Bekalan/ Perkhidmatan/ Kerja) '),
(193, 'MP', 'C : Tatacara', '1.5.1', 'Penggiliran \nPemilihan penggiliran dibolehkan bagi perolehan bekalan/perkhidmatan di bawah RM50,000.00 manakala bagi perolehan kerja pula adalah di bawah RM20,000.00 di mana terdapat beberapa pembekal/kontraktor yang boleh memberikan penawaran kerja yang sama maka dibolehkan kaedah penggiliran dibuat.'),
(194, 'MP', 'C : Tatacara', '1.5.2', 'Undian \nPemilihan secara undian adalah dibolehkan bagi perolehan bekalan/perkhidmatan di bawah RM50,000.00 manakala bagi perolehan kerja adalah di bawah RM20,000.00 dan terdapat kuantiti tawaran kerja dalam satu tempoh yang diketahui dengan nilai yang berbeza maka pemilihan pembekal/kontraktor yang berdaftar boleh dibuat secara undian. Pembekal yang diundi akan mendapat kerja dengan nilai yang berbeza setelah ditawarkan Sebut Harga. '),
(195, 'MP', 'C : Tatacara', '2', 'SEBUT HARGA \nPerolehan secara Sebut Harga hendaklah melalui kaedah sama ada Sebut Harga Rundingan Terus, Sebut Harga Tertutup atau Sebut Harga Terbuka. Dalam keadaan tertentu setelah Jawatankuasa berkenaan berpuas hati bolehlah ia menawarkan sesuatu kerja atau pembelian itu secara Rundingan Terus. Ia boleh dilaksanakan atas sebab-sebab berikut:\na) Bekalan/Kerja/Perkhidmatan yang memerlukan tindakan segera; \nb) Pembekal tunggal; dan \nc) Kerja-Kerja khas. \nSebut Harga Tertutup adalah mempelawa syarikat/pembekal bekalan/perkhidmatan/kerja yang telah dikenalpasti dapat memberikan perkhidmatan berkaitan secara pelawaan menyertai sebut harga. Sebut Harga Terbuka adalah dengan mempelawa syarikat pembekal bumiputera/ bukan bumiputera yang beragama Islam yang berdaftar dengan Majlis atau Kementerian Kewangan melalui iklan tawaran Sebut Harga yang dipamerkan di laman web/papan kenyataan.'),
(196, 'MP', 'C : Tatacara', '2.1', 'Had Nilai Sebut Harga '),
(197, 'MP', 'C : Tatacara', '2.1.1', '2.1.1 Sebut Harga Bekalan/Perkhidmatan '),
(198, 'MP', 'C : Tatacara', '2.1.1(a)', 'Pembelian melibatkan perolehan bekalan/ perkhidmatan yang bernilai melebihi RM50,000.00 hingga RM100,000.00 hendaklah \nmempelawa sebut harga di kalangan sekurang-kurangnya lima pembuat/pembekal tempatan yang yang berdaftar dengan Majlis atau Kementerian Kewangan. Keutamaan hendaklah diberikan kepada pembekal bertaraf Bumiputera dan beragama Islam.'),
(199, 'MP', 'C : Tatacara', '2.1.1(b)', 'Perolehan bekalan/perkhidmatan yang bernilai melebihi RM100,000.00 hingga RM500,000.00 hendaklah mempelawa sebut harga di kalangan sekurang-kurangnya lima pembuat/pembekal tempatan yang berdaftar dengan Majlis atau Kementerian Kewangan dengan kod bidang berkaitan. Keutamaan hendaklah diberikan kepada pembekal bertaraf Bumiputera dan beragama Islam. '),
(200, 'MP', 'C : Tatacara', '2.1.1( c )', 'Jika bilangan pelawaan sebut harga yang dikehendaki tidak boleh didapati, Urus setia Jawatankuasa Sebut Harga hendaklah berpuas hati bahawa bilangan pembekal adalah kurang daripada lima. '),
(201, 'MP', 'C : Tatacara', '2.1.2', 'Sebut Harga Kerja '),
(202, 'MP', 'C : Tatacara', '2.1.2(a)', 'Kerja-kerja yang bernilai melebihi RM20,000.00 hingga RM50,000.00 hendaklah dipelawa secara Sebut Harga rundingan terus di kalangan sekurang-kurangnya lima kontraktor tempatan yang berdaftar di bawah Lembaga Pembangunan Industri Pembinaan Malaysia (CIDB) dalam gred G1.'),
(203, 'MP', 'C : Tatacara', '2.1.3(b)', 'Kerja dan perkhidmatan yang lebih daripada RM50,000.00 hingga RM200,000.00 hendaklah dipelawa secara Sebut Harga terbuka. Namun dalam keadaan tertentu Jawatankuasa Sebut Harga MAIS boleh menimbang untuk memanggil di kalangan sekurang-kurangnya lima kontraktor tempatan yang berdaftar di bawah Lembaga Pembangunan Industri Pembinaan Malaysia (CIDB) dalam Gred G1. '),
(204, 'MP', 'C : Tatacara', '2.1.3( c )', 'Perolehan kerja yang bernilai melebihi RM200,000.00 hingga RM500,000.00 hendaklah dipelawa Sebut Harga terbuka. Namun dalam keadaan tertentu Jawatankuasa Sebut Harga MAIS boleh menimbangkan untuk memanggil di kalangan sekurang-kurangnya lima kontraktor tempatan/panel yang berdaftar di bawah Lembaga Pembangunan Industri Pembinaan Malaysia (CIDB) dalam gred G2. '),
(205, 'MP', 'C : Tatacara', '2.1.3(d)', 'Semua perolehan kerja elektrik yang bernilai melebihi RM50,000.00 hingga RM500,000.00 hendaklah dipelawa secara Sebut Harga terbuka. Namun dalam keadaan tertentu Jawatankuasa Sebut Harga MAIS boleh menimbangkan untuk memanggil di kalangan sekurang-kurangnya lima kontraktor elektrik tempatan/panel yang berdaftar di bawah kelas elektrik yang berkaitan dan CIDB dalam gred yang berkenaan.'),
(206, 'MP', 'C : Tatacara', '2.1.3(e)', 'Maksud kontraktor tempatan bagi perolehan kerja untuk kontraktor yang berdaftar dengan CIDB di daerah berkenaan. '),
(207, 'MP', 'C : Tatacara', '2.1.3(f)', 'Sekiranya bilangan kontraktor di daerah berkenaan tidak mencukupi kontraktor di daerah berhampiran/sempadan boleh dipelawa. '),
(208, 'MP', 'C : Tatacara', '2.1.3(g)', 'Maksud kontraktor tempatan bagi perolehan kerja untuk kontraktor gred G2 yang berdaftar dengan CIDB. Sekiranya bilangan kontraktor di negeri berkenaan tidak mencukupi kontraktor di negeri berhampiran/sempadan boleh dipelawa. '),
(209, 'MP', 'C : Tatacara', '2.1.3(h)', 'Majlis dibenarkan untuk melaksanakan perolehan secara pakej merangkumi kerja induk dan kerja yang lazimnya dipelawa secara Wang Kos Prima seperti kerja-kerja mekanikal dan elektrik. '),
(210, 'MP', 'C : Tatacara', '2.1.3(i)', 'Walau bagaimanapun, Majlis hendaklah mensyaratkan kontraktor induk melantik subkontraktor yang berdaftar CIDB dalam gred dan kategori yang berkaitan. '),
(211, 'MP', 'C : Tatacara', '2.1.3(j)', 'Dalam menentukan kaedah perolehan secara sebut harga, Majlis hendaklah mengambil kira nilai perolehan tahunan atau nilai kontrak. '),
(212, 'MP', 'C : Tatacara', '2.1.3(k)', 'Jika bilangan pelawaan Sebut Harga yang dikehendaki tidak boleh didapati, Urus setia Jawatankuasa Sebut Harga hendaklah berpuas hati bahawa bilangan pembekal adalah kurang daripada lima. '),
(213, 'MP', 'C : Tatacara', '2.2', 'Pelawaan Sebut Harga '),
(214, 'MP', 'C : Tatacara', '2.2.1', 'Dokumen Sebut Harga '),
(215, 'MP', 'C : Tatacara', '2.2.1(a)', 'Dokumen sebut harga bagi bekalan dan perkhidmatan hendaklah menggunakan dokumen sebut harga. Dokumen sebut harga merupakan sebahagian daripada kontrak dan hendaklah mengandungi perkara-perkara berikut: \ni. Arahan kepada penyebut harga; \nii. Syarat-syarat am /syarat-syarat khas yang jelas; \niii. Format kontrak jika berkaitan; \niv. Spesifikasi; \nv. Pengalaman/ senarai pesanan bekalan/perkhidmatan yang sedang dan telah dibuat (jika perlu); dan \nvi. Tempat, tarikh dan waktu sebut harga diterima dan ditutup. '),
(216, 'MP', 'C : Tatacara', '2.2.1(b)', 'Dokumen sebut harga bagi kerja hendaklah mengandungi perkara-perkara berikut: \ni. Arahan kepada penyebut harga; \nii. Syarat-syarat sebut harga; \niii. Borang sebut harga termasuk Ringkasan Sebut Harga/Senarai Kuantiti (tetap atau sementara); \niv. Spesifikasi Teknikal (Jadual); \nv. Pelan Tapak Bina dan Lukisan Teknikal (jika ada); \nvi. Jadual Kadar Harga (jika perlu); dan \nvii. Pengalaman dan Senarai Kerja Dalam Tangan (Borang D dan Borang E) '),
(217, 'MP', 'C : Tatacara', '2.2.1( c )', 'Senarai semakan hendaklah disediakan dan disertakan bersama dokumen sebut harga bagi memastikan pembuat/ pembekal/ kontraktor mengemukakan tawaran sebut harga yang lengkap beserta semua dokumen yang diperlukan. Senarai semakan adalah seperti berikut: \ni. Lampiran 1 (Senarai Semak Dokumen Asal); \nii. Lampiran 2 (Senarai Semak Dokumen Kewangan); dan \niii. Lampiran 3 (Senarai Semak Dokumen Teknikal). '),
(218, 'MP', 'C : Tatacara', '2.2.1(d)', 'Pelawaan dibuat dengan mengemukakan dokumen Sebut Harga kepada pembuat/pembekal/kontraktor yang berdaftar dengan Majlis, Kementerian Kewangan dan CIDB yang mana berkenaan. '),
(219, 'MP', 'C : Tatacara', '2.2.2', 'Notis/Pengiklanan Sebut Harga '),
(220, 'MP', 'C : Tatacara', '2.2.2(a)', 'Urus setia Sebut Harga hendaklah mengenalpasti penyebutharga yang berdaftar dengan Majlis, Kementerian Kewangan atau CIDB bagi urusan bekalan/perkhidmatan dan kerja yang berkaitan. Pemberitahuan sebut harga hendaklah dibuat sama ada melalui pos berdaftar, pos laju, pos biasa atau serahan tangan. '),
(221, 'MP', 'C : Tatacara', '2.2.2(b)', 'Bagi Sebut Harga Terbuka, urus setia Sebut Harga juga hendaklah mempamerkan pemberitahuan sebut harga di papan kenyataan awam di Ibu Pejabat Majlis dan Portal untuk memberi peluang kepada penyebutharga lain (tidak berdaftar dengan Majlis) yang berkelayakan dan berminat \nuntuk mengambil bahagian.'),
(222, 'MP', 'C : Tatacara', '2.2.2( c )', 'Satu kaedah yang seragam hendaklah diamalkan kepada penyebutharga yang telah dikenalpasti bagi sesuatu urusan sebut harga untuk memastikan prinsip kesaksamaan dan adil dilaksanakan ke atas penyebutharga. '),
(223, 'MP', 'C : Tatacara', '2.2.2(d)', 'Notis pemberitahuan sebut harga di papan kenyataan awam atau laman web hendaklah menyatakan seperti berikut: \ni. Tajuk Sebut Harga; \nii. Syarat kelayakan penyebutharga Bidang / Kelas / Kepala pendaftaran; \niii. Tempat, tarikh dan waktu bagi lawatan tapak (jika berkaitan); \niv. Tempat, tarikh dan waktu Sebut Harga diterima dan ditutup; dan \nv. Sebut Harga hendaklah diterima dan dibuka pada masa seperti yang telah dijadualkan. '),
(224, 'MP', 'C : Tatacara', '2.2.3', 'Penjualan Dokumen Sebut Harga '),
(225, 'MP', 'C : Tatacara', '2.2.3(a)', 'Dokumen Sebut Harga dijual pada harga RM10.00 setiap satu. '),
(226, 'MP', 'C : Tatacara', '2.2.3(b)', 'Majlis hendaklah mempamerkan Pemberitahuan Sebut Harga (Notis Sebut Harga) di papan kenyataan Majlis di Ibu Pejabat dan laman web selain daripada mempelawa Sebut Harga kepada penyebutharga yang telah dikenal pasti.'),
(227, 'MP', 'C : Tatacara', '2.2.3( c )', 'Tempoh penjualan Dokumen Sebut Harga hendaklah sekurang-kurangnya tujuh hari berturut-turut dan pemberitahuan Sebut Harga hendaklah sentiasa dipamerkan di papan kenyataan sepanjang tempoh tersebut. '),
(228, 'MP', 'C : Tatacara', '2.2.3(d)', 'Satu daftar hendaklah diselenggarakan untuk mencatatkan senarai edaran sebut harga atau pembuat/pembekal/kontraktor yang telah mengambil sendiri dokumen Sebut Harga. '),
(229, 'MP', 'C : Tatacara', '2.2.3(e)', 'Semua Dokumen Sebut Harga yang dijual hendaklah diberi nombor siri untuk pengawalan. '),
(230, 'MP', 'C : Tatacara', '2.2.3(f)', 'Satu penyebutharga hanya dibenarkan mengemukakan satu tawaran Sebut Harga sahaja. Tawaran alternatif adalah tidak dibenarkan. '),
(231, 'MP', 'C : Tatacara', '2.2.3(g)', 'Majlis hendaklah mensyaratkan kepada penyebutharga supaya mengemukakan dokumen asal, dokumen tawaran harga dan dokumen tawaran teknikal dalam tiga sampul surat yang berasingan dan berlakri. '),
(232, 'MP', 'C : Tatacara', '2.2.4', 'Tempoh Sah Laku Sebut Harga '),
(233, 'MP', 'C : Tatacara', '2.2.4(a)', 'Tempoh sah laku tawaran Sebut Harga hendaklah tidak melebihi 90 hari dari tarikh tutup sebut harga bergantung kepada kompleksiti sebut harga yang dilaksanakan; dan '),
(234, 'MP', 'C : Tatacara', '2.2.4(b)', 'Tempoh sah laku tawaran Sebut Harga tidak boleh dilanjutkan. Jika tempoh sah laku tawaran telah tamat atau keputusan tidak dibuat dalam tempoh sah laku tawaran, sebut harga hendaklah dipelawa semula. '),
(235, 'MP', 'C : Tatacara', '2.3', 'Penerimaan Sebut Harga '),
(236, 'MP', 'C : Tatacara', '2.3.1', 'Urus setia Sebut Harga hendaklah memastikan Dokumen Sebut Harga diterima sebagaimana yang telah dijadualkan. '),
(237, 'MP', 'C : Tatacara', '2.3.2', 'Dokumen yang diterima selepas tempoh sebut harga ditutup tidak akan dipertimbangkan. '),
(238, 'MP', 'C : Tatacara', '2.4', 'Pembukaan Sampul Sebut Harga '),
(239, 'MP', 'C : Tatacara', '2.4.1', 'Peti tawaran hendaklah dibuka secepat mungkin selepas sebut harga ditutup. '),
(240, 'MP', 'C : Tatacara', '2.4.2', 'Jawatankuasa Pembuka Sebut Harga mestilah bersidang dalam tempoh 24 jam selepas sesuatu sebut harga ditutup. '),
(241, 'MP', 'C : Tatacara', '2.4.3', 'Jawatankuasa yang bersidang hendaklah memastikan bilangan ahli jawatankuasa memenuhi korum yang dikehendaki. '),
(242, 'MP', 'C : Tatacara', '2.5', 'Penilaian Sebut Harga bagi Sebut Harga Terbuka '),
(243, 'MP', 'C : Tatacara', '2.5.1', 'Semua sebut harga yang diterima hendaklah dikaji dan dinilai sebelum syor dibuat kepada Jawatankuasa Sebut Harga. Sebut harga yang paling menguntungkan Majlis hendaklah disyorkan. '),
(244, 'MP', 'C : Tatacara', '2.5.2', 'Penilaian hendaklah dilakukan melalui dua Jawatankuasa berasingan iaitu: \ni. Jawatankuasa Penilaian Teknikal; dan \nii. Jawatankuasa Penilaian Harga. '),
(245, 'MP', 'C : Tatacara', '2.5.3', 'Dalam penilaian Sebut Harga, perkara-perkara seperti jangka masa siap, kadar harga, kekerapan perkhidmatan, kawasan perlaksanaan, kos penyelenggaraan untuk tempoh tertentu dan sebagainya perlu diambil kira. '),
(246, 'MP', 'C : Tatacara', '2.5.4', 'Bagi semua Sebut Harga, jika perlu nasihat daripada Bahagian Teknikal seperti Unit ICT atau yang seumpamanya atau agensi luar seperti JKR atau JPS yang seumpamanya hendaklah diperolehi. '),
(247, 'MP', 'C : Tatacara', '2.5.5', 'Penilaian Sebut Harga hendaklah disiapkan dalam tempoh 14 hari dari tarikh tawaran Sebut Harga dikemukakan kepada Jawatankuasa Penilaian.'),
(248, 'MP', 'C : Tatacara', '2.5.6', 'Laporan penilaian hendaklah ditandatangani oleh semua ahli Jawatankuasa. '),
(249, 'MP', 'C : Tatacara', '2.6', 'Asas Penilaian Sebut Harga '),
(250, 'MP', 'C : Tatacara', '2.6.1', 'Penyebutharga memenuhi semua syarat yang dinyatakan dalam dokumen Sebut Harga. '),
(251, 'MP', 'C : Tatacara', '2.6.2', 'Keupayaan/kemampuan penyebutharga seperti pengalaman kerja yang lepas, prestasi kerja/kontrak semasa, projek yang sedang dilaksanakan/dalam tangan. '),
(252, 'MP', 'C : Tatacara', '2.6.3', 'Analisa harga dengan mengambil kira tempoh siap/penghantaran. '),
(253, 'MP', 'C : Tatacara', '2.6.4', 'Kemampuan kewangan hendaklah dinilai bagi Sebut Harga bernilai melebihi RM200,000.00 hingga RM500,000.00 dari segi modal minimum sekurang-kurangnya 1.5% daripada anggaran harga yang ditetapkan oleh Majlis. Modal minimum hendaklah dalam bentuk aset mudah cair iaitu campuran nilai positif purata baki akhir bulan dalam penyata bulanan bank bagi tiga bulan terakhir termasuk deposit tetap, baki nilai kemudahan kredit dan nilai kemudahan kredit tambahan yang layak/akan diperolehi oleh penyebut harga daripada bank atau institusi kewangan. '),
(254, 'MP', 'C : Tatacara', '2.7', 'Penyediaan Kertas Taklimat Sebut Harga \nSetelah laporan penilaian sebut harga diterima oleh Urusetia Sebut Harga, kertas taklimat Sebut Harga hendaklah disediakan dengan maklumat-maklumat seperti berikut :- \na) Salinan spesifikasi sebut harga; \nb) Jadual sebut harga; \nc) Senarai syarikat yang telah dijemput dan syarikat yang menjawab/tidak menjawab; \nd) Salinan sijil pendaftaran dengan Majlis/Kementerian Kewangan/PKK/LPIPM/CIDB; \ne) Laporan Penilaian Sebut Harga; \nf) Kertas Pertimbangan Sebut Harga; dan \ng) Maklumat berkaitan yang mungkin dikehendaki atau yang disyaratkan oleh Jawatankuasa Sebut Harga. '),
(255, 'MP', 'C : Tatacara', '2.8', 'Jawatankuasa Sebut Harga '),
(256, 'MP', 'C : Tatacara', '2.8.1', 'Keanggotaan Jawatankuasa adalah seperti di Bab B Perkara 13.1. '),
(257, 'MP', 'C : Tatacara', '2.8.2', 'Bagi perolehan kerja, seorang daripada Ahli Jawatankuasa Sebut Harga hendaklah terdiri daripada Pegawai Teknikal. '),
(258, 'MP', 'C : Tatacara', '2.8.3', 'Jawatankuasa Sebut Harga hendaklah bersidang secepat mungkin supaya keputusan dapat dibuat dengan segera. Jawatankuasa Sebut Harga hendaklah seboleh-bolehnya bersidang dalam tempoh 14 hari bekerja selepas penilaian dibuat.'),
(259, 'MP', 'C : Tatacara', '2.8.4', 'Ahli Jawatankuasa Sebut Harga tidak boleh menganggotai Jawatankuasa Pembuka Sebut Harga atau mana-mana Jawatankuasa Penilaian. '),
(260, 'MP', 'C : Tatacara', '2.9', 'Pertimbangan Sebut Harga '),
(261, 'MP', 'C : Tatacara', '2.9.1', 'Jawatankuasa Sebut Harga hendaklah bersidang/bermesyuarat untuk memutuskan Sebut Harga. Pertimbangan Sebut Harga secara edaran dibenarkan selepas mendapat kelulusan khas Pegawai Pengawal. '),
(262, 'MP', 'C : Tatacara', '2.9.2', 'Segala perbincangan Jawatankuasa Sebut Harga hendaklah dicatatkan dalam minit dan keputusan sesuatu Sebut Harga hendaklah mendapatkan tandatangan Pengerusi dan dua Ahli Jawatankuasa Sebut Harga. Asas-asas pemilihan sebut harga hendaklah dicatatkan dengan jelas pada kertas pertimbangan. '),
(263, 'MP', 'C : Tatacara', '2.9.3', 'Keputusan Jawatankuasa Sebut Harga hendaklah sebulat suara. Keputusan yang tidak sebulat suara dan/atau melebihi angggaran jabatan hendaklah diputuskan oleh Pengerusi Jawatankuasa. '),
(264, 'MP', 'C : Tatacara', '2.9.4', 'Jika tawaran yang paling rendah tidak disetuju terima, sebab-sebab bagi menyetuju terima suatu tawaran alternatif hendaklah dicatatkan dalam jadual sebut harga.'),
(265, 'MP', 'C : Tatacara', '2.9.5', 'Satu rekod yang lengkap mengenai pertimbangan dan keputusan Sebut Harga hendaklah disimpan oleh urusetia Sebut Harga bagi tujuan auditan. '),
(266, 'MP', 'C : Tatacara', '2.1', 'Keputusan Jawatankuasa Sebut Harga '),
(267, 'MP', 'C : Tatacara', '2.10.1', 'Setelah menerima keputusan Jawatankuasa Sebut Harga, surat tawaran/lantikan dikeluarkan dengan segera kepada pembuat/pembekal/kontraktor yang berjaya. '),
(268, 'MP', 'C : Tatacara', '2.10.2', 'Bagi bekalan/perkhidmatan/kerja di mana kontrak formal tidak sesuai, memadai pesanan tempatan (LO) atau inden kerja dikeluarkan. Walau bagaimanapun, dokumen sebut harga hendaklah disertakan bersama pesanan tempatan atau inden supaya kontraktor jelas mengenai obligasinya. '),
(269, 'MP', 'C : Tatacara', '2.10.3', 'Bagi bekalan/perkhidmatan/kerja bermasa yang mana pesanan tempatan /inden kerja tidak sesuai dikeluarkan memadai hanya surat tawaran atau lantikan. '),
(270, 'MP', 'C : Tatacara', '2.10.4', 'Majlis hendaklah mengadakan satu kontrak formal bagi perolehan bekalan/perkhidmatan bermasa iaitu menjangkau tempoh setahun atau perolehan kerja, yang mana berkenaan, untuk ditandatangani walaupun nilai perolehan adalah kurang daripada RM50,000.00 selaras dengan Tatacara 180B (c) Amalan Terbaik Pengurusan Kewangan MAIS 2011.');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(271, 'MP', 'C : Tatacara', '3', 'TENDER \nPerolehan secara Tender hendaklah melalui kaedah sama ada Tender secara Rundingan Terus, Tender Tertutup atau Tender Terbuka. Dalam keadaan tertentu setelah Jawatankuasa berkenaan berpuas hati bolehlah ia menawarkan sesuatu kerja atau pembelian itu secara Rundingan Terus. Ia boleh dilaksanakan atas sebab-sebab berikut:\na) Bekalan/Kerja/Perkhidmatan yang memerlukan tindakan segera; \nb) Pembekal tunggal; dan \nc) Kerja-Kerja khas. \nTender Tertutup adalah mempelawa syarikat/pembekal bekalan/perkhidmatan/kerja yang telah dikenalpasti dapat memberikan perkhidmatan berkaitan secara pelawaan menyertai tender. Tender Terbuka adalah dengan mempelawa syarikat pembekal bumiputera/bukan bumiputera yang beragama Islam yang berdaftar dengan Majlis atau Kementerian Kewangan melalui iklan tawaran Tender yang dipamerkan di laman web/papan kenyataan.\nTender hendaklah dipelawa daripada Pembekal/ Kontraktor/Perunding Berdaftar yang dikawal selia oleh pihak berkuasa dan digunapakai bersama oleh Majlis.'),
(272, 'MP', 'C : Tatacara', '3.1', 'Had Nilai Tender '),
(273, 'MP', 'C : Tatacara', '3.1.1', 'Tender Bekalan/Perkhidmatan '),
(274, 'MP', 'C : Tatacara', '3.1.1(a)', 'Pembelian yang melebihi RM500,000.00 ke atas hendaklah dipelawa secara tender. '),
(275, 'MP', 'C : Tatacara', '3.1.1(b)', '(b) Tender tempatan bagi barangan buatan tempatan hendaklah dipelawa dengan menggunakan harga bersih sahaja. Tender tempatan bagi barangan yang perlu diimport hendaklah dipelawa dengan menggunakan harga tawaran Free On Board (FOB) sahaja. '),
(276, 'MP', 'C : Tatacara', '3.1.1( c )', 'Tender antarabangsa hanya boleh dipelawa bagi perolehan bekalan dan perkhidmatan yang tidak boleh disediakan atau dikeluarkan oleh sumber dalam Negara. '),
(277, 'MP', 'C : Tatacara', '3.1.1(d)', 'Tender terbuka hendaklah dipelawa untuk bekalan yang nilainya melebihi nilai yang ditentukan setahun bagi setiap item. '),
(278, 'MP', 'C : Tatacara', '3.1.2', 'Tender Kerja '),
(279, 'MP', 'C : Tatacara', '3.1.2(a)', 'Kerja yang bernilai melebihi daripada RM500,000.00 hendaklah ditawarkan secara tender. '),
(280, 'MP', 'C : Tatacara', '3.1.2(b)', 'Semua tender kerja hendaklah dipanggil secara tempatan kecuali jika kontraktor tempatan tidak mempunyai kepakaran atau keupayaan berkenaan dengannya dan sukar dilaksanakan secara usaha sama. '),
(281, 'MP', 'C : Tatacara', '3.1.2( c )', 'Bagi tender kerja yang disertai oleh beberapa syarikat tempatan secara usaha sama, kesemuanya hendaklah berdaftar dengan CIDB/LPIPM serta salah satu daripadanya hendaklah berdaftar dalam kelas dan gred yang ditetapkan mengikut nilai tender. '),
(282, 'MP', 'C : Tatacara', '3.1.2(d)', 'Pembangunan Projek secara usahasama dikecualikan daripada tatacara ini. Namun ianya tertakluk kepada kelulusan jawatankuasa lain yang terlibat. '),
(283, 'MP', 'C : Tatacara', '3.2', 'Pelawaan Tender '),
(284, 'MP', 'C : Tatacara', '3.2.1', 'Dokumen Tender '),
(285, 'MP', 'C : Tatacara', '3.2.1(a)', 'Bagi Tender Kerja, dokumen tender hendaklah disediakan oleh perunding atau pengamal persendirian dengan menggunakan Borang Kontrak yang berkenaan dan Borang Tender yang berkaitan. '),
(286, 'MP', 'C : Tatacara', '3.2.1(b)', 'Setiap set Dokumen Tender hendaklah sempurna serta mengandungi antara lain, perkara berikut: \n\ni. Syarat-Syarat Dan Arahan Kepada Petender ? maklumat-maklumat penting yang wajib dipatuhi oleh petender;\n \nii. Spesifikasi - spesifikasi umum/khusus/ teknikal bagi bekalan/perkhidmatan/ kerja yang dikehendaki oleh Majlis;\n \niii. Borang Tender - borang hendaklah diisi dan ditandatangani oleh petender bagi menyertai tender;\n \niv. Contoh Surat Setuju Terima Tender - contoh surat yang mengesahkan penerimaan sesuatu tawaran berasaskan kepada syarat yang telah ditetapkan dalam dokumen tender, syarat yang telah dipersetujui dalam surat niat atau apa-apa syarat lain yang telah dipersetujui melalui rundingan sebelumnya; \n\nv. Syarat-Syarat Kontrak - syarat-syarat kontrak yang akan diguna pakai bagi petender yang berjaya; \n\nvi. Senarai Kuantiti / Ringkasan Tender - maklumat yang menjelaskan huraian kerja/kuantiti yang diperlukan dan tempoh masa yang akan diikat; \n\nvii. Jadual Kadar Harga - jadual kadar harga yang perlu diisi oleh petender (jika berkenaan);\n\nviii. Lukisan Teknikal/Pelan - sesuatu pelan, model atau lukisanyang diperlukan (jika berkenaan); \n\nix. Tempat Penghantaran - tempat serahan terakhir di mana barangan yang diperolehi akan diserah kepada Majlis; \n\nx. Tempoh siap/penghantaran; \n\nxi. prestasi/Rekod Pengalaman - maklumat tentang pengalaman petender dalam bidang berkaitan yang dibekalkan/ dilaksanakan kepada Majlis; \n\nxii. Profil Dan Kedudukan Kewangan Petender ? maklumat terperinci tentang latar belakang dan keupayaan kewangan petender; \n\nxiii. Senarai Semak - senarai dokumen / perkara yang perlu dikemukakan oleh petender; \n\nxiv. perkara-perkara lain yang berkenaan; dan \n\nxv. Setiap dokumen tender hendaklah diberi nombor siri. '),
(287, 'MP', 'C : Tatacara', '3.2.2', 'Dokumen Tender Bekalan/Perkhidmatan '),
(288, 'MP', 'C : Tatacara', '3.2.2(a)', 'Perkara-perkara utama yang perlu dinyatakan dengan jelas di dalam dokumen tender (yang mana berkaitan) adalah seperti:\n\ni. Tempoh sah laku tender; \n\nii. Larangan publisiti; \n\niii. Cara pembelian (FOB/Harga Bersih); \n\niv. Penghantaran barangan import dan eksport Kerajaan[Multimodal Transport Operator (MTO)]; \n\nv. Mata wang; \n\nvi. Jadual pelaksanaan perkhidmatan/ jadual penghantaran; \n\nvii. Tempoh siap perkhidmatan/ penghantaran; \n\nviii. Tempat penghantaran; \n\nix. Terma pembayaran; \n\nx. Insurans Kumpulan Wang Insurans Barang-barang Kerajaan(KWIBK); \n\nxi. Addendum; \n\nxii. Tempoh tanggungan kecacatan/ waranti; \n\nxiii. Bantahan spesifikasi seperti menjurus kepada sesuatu jenama; \n\nxiv. Offset; \n\nxv. Denda (pelanggaran syarat kontrak untuk bekalan/perkhidmatan); \n\nxvi. Penamatan kontrak; \n\nxvii. Timbang tara; \n\nxviii. Draf perjanjian kontrak; dan \n\nxix. Syarat-syarat lain yang perlu bagi menjaga kepentingan Majlis. \n'),
(289, 'MP', 'C : Tatacara', '3.2.2(b)', 'Apabila mempelawa tender bekalan/perkhidmatan, Majlis hendaklah menyatakan dengan jelas perkara-perkara berikut: \ni. Perincian teknikal dan perincian harga hendaklah disediakan dalam sampul     yang berasingan dan berlakri; \nii. Kedua-dua sampul perincian tersebut hendaklah dimasukkan ke dalam satu bungkusan; dan \niii. Petender tidak dibenarkan mencatat nama petender dan harga dalam dokumen spesifikasi yang dikemukakan. Bagi Petender yang tidak mengikut arahan ini akan menyebabkan tawaran tender mereka tidak layak dipertimbangkan. '),
(290, 'MP', 'C : Tatacara', '3.2.3', 'Kerja Dokumen Tender \nPerkara-perkara utama yang perlu dimasukkan dalam Dokumen Tender Kerja adalah seperti: \n\ni. Majlis hendaklah menggunakan Borang JKR Siri 203 untuk tender kerja kecuali projek-projek yang dilaksanakan secara bukan konvensional. Projek-projek yang dilaksanakan secara Turnkey atau Design  &  Build  boleh  menggunakan Borang PWD DB/T yang berkuat kuasa;\n\nii. Bagi projek yang dibiayai oleh peruntukan asing, penggunaan borang JKR Siri 203 hendaklah dipakai jika dipersetujui oleh pembiaya; dan \n\niii. Sepertimana yang ditetapkan di bawah seksyen 34(2) Akta Lembaga Pembangunan Industri Pembinaan Malaysia (LPIPM/CIDB) 1994 (Akta 520), Majlis hendaklah memasukkan peruntukan dalam ?Arahan Kepada Petender? bahawa semua tender yang bernilai melebihi RM500,000.00 ke atas, levi sebanyak 0.25% daripada nilai harga kontrak akan dikenakan ke atas petender yang berjaya. \n'),
(291, 'MP', 'C : Tatacara', '3.2.4', 'Pengiklanan '),
(292, 'MP', 'C : Tatacara', '3.2.4(a)', 'Apabila mempelawa tender, publisiti hendaklah dibuat supaya semua pembekal yang bona fide berpeluang untuk menyertai tender dan dengan demikian membolehkan Majlis mendapat tawaran yang terbaik. '),
(293, 'MP', 'C : Tatacara', '3.2.4(b)', 'Kandungan iklan tender hendaklah jelas, kemas, ringkas dan tidak mendatangkan apa-apa keraguan atau pembaziran. '),
(294, 'MP', 'C : Tatacara', '3.2.4( c )', 'Majlis juga hendaklah mengiklankan tawaran tender dalam laman web Majlis. '),
(295, 'MP', 'C : Tatacara', '3.2.4(d)', 'Kandungan iklan tender hendaklah menyatakan perkara-perkara berikut: \ni. Majlis selaku yang memanggil tender; \nii. tajuk tender yang diiklankan; \niii. jenis tender, sama ada terbuka dan/atau khas Bumiputera; '),
(296, 'MP', 'C : Tatacara', '3.2.4(e)', 'Syarat-syarat kelayakan petender: \ni. bagi tender bekalan dan/atau perkhidmatan - berdaftar dengan Majlis dan Kementerian Kewangan di bawah kod bidang dan sub-bidang yang berkenaan; \nii. bagi tender kerja - berdaftar Lembaga Pembangunan Industri Pembinaan Malaysia (LPIPM/CIDB) dalam gred dan kategori yang berkenaan. '),
(297, 'MP', 'C : Tatacara', '3.2.4(f)', 'Tempat, tarikh dan masa dokumen tender dijual. '),
(298, 'MP', 'C : Tatacara', '3.2.4(g)', 'Tempat, tarikh dan masa bagi lawatan tapak/premis (jika berkenaan). '),
(299, 'MP', 'C : Tatacara', '3.2.4(h)', 'Harga, cara bayaran dan penerima bayaran dokumen tender. '),
(300, 'MP', 'C : Tatacara', '3.2.4(i)', 'Tempat, tarikh dan waktu tender diterima dan ditutup. '),
(301, 'MP', 'C : Tatacara', '3.2.4(j)', 'Masa yang cukup hendaklah diberi bagi membolehkan petender mengkaji tawaran dan membuat persediaan bagi sesuatu tender. Untuk tujuan ini, tarikh tender ditutup hendaklah sekurang-kurangnya 21 hari dari tarikh dokumen tender mula dijual. '),
(302, 'MP', 'C : Tatacara', '3.2.4(k)', 'Bagi tender yang melibatkan lawatan tapak/premis, Majlis hendaklah memastikan bahawa tarikh tutup tender hendaklah sekurang-kurangnya 21 hari dari tarikh lawatan yang ditetapkan. '),
(303, 'MP', 'C : Tatacara', '3.2.4(l)', 'Majlis hendaklah menetapkan jam 12.00 tengah hari pada hari pelawaan tender ditutup (hari bekerja) sebagai waktu terakhir untuk menerima tawaran tender. Tawaran tender yang lewat diterima dari waktu dan tarikh yang ditetapkan tidak boleh dipertimbangkan. '),
(304, 'MP', 'C : Tatacara', '3.2.5', 'Penjualan Dokumen Tender '),
(305, 'MP', 'C : Tatacara', '3.2.5(a)', 'Penjualan dokumen hendaklah berasaskan syarat-syarat yang dinyatakan dalam iklan tender. '),
(306, 'MP', 'C : Tatacara', '3.2.5(b)', 'Satu petender hanya dibenarkan membeli satu dokumen tender dan mengemukakan satu tawaran tender sahaja. Tawaran alternatif adalah tidak dibenarkan.'),
(307, 'MP', 'C : Tatacara', '3.2.5( c )', 'Kadar Bayaran Dokumen Tender (Hard Copy/Soft Copy) Bagi Tender Bekalan/Perkhidmatan/Kerja adalah: \n\ni. dokumen tender hendaklah dijual dalam bentuk hard copy. Sekiranya perlu, Majlis boleh menyediakan dokumen tender dalam bentuk soft copy, dan bayaran tambahan sebanyak RM10.00 bagi sekeping cakera padat hendaklah dikenakan kepada petender yang memerlukan. Sekiranya terdapat percanggahan maklumat antara soft copy dan hard copy, dokumen dalam hard copy hendaklah diguna pakai; \n\nii. walau bagaimanapun, tawaran tender hendaklah dikemukakan dalam bentuk hard copy sahaja; \n\niii. dokumen tender dijual pada harga RM50.00 setiap satu; \n\niv. bayaran bagi dokumen tender hendaklah dibuat dalam bentuk tunai/cek/draf bank daripada bank yang berlesen dan beroperasi di Malaysia atau kiriman wang pos (money order) atau wang kiriman pos berpalang (postal order), dan dibayar atas nama Majlis Agama Islam Selangor; dan\n\nv. jika dokumen tender disediakan oleh perunding yang dilantik, Majlis hendaklah memastikan bahawa bayaran yang dikenakan adalah seperti di perenggan 3.2.5 (c)(i) dan 3.2.5 (c)(ii) dan perunding tersebut tidak dibenarkan mengguna kadar-kadar di atas untuk apa-apa jenis tuntutan termasuk tuntutan imbuhan balik daripada Majlis. '),
(308, 'MP', 'C : Tatacara', '3.2.6', 'Tempoh Sah Laku Tender '),
(309, 'MP', 'C : Tatacara', '3.2.6(a)', 'Sebelum sesuatu tender dipelawa, satu jadual pelaksanaan hendaklah disediakan bagi menunjukkan anggaran masa, untuk setiap peringkat, dari tarikh penyediaan spesifikasi sehingga keputusan dibuat. '),
(310, 'MP', 'C : Tatacara', '3.2.6(b)', 'Tempoh sah laku tender yang munasabah hendaklah ditetapkan dengan mengambil kira faktor-faktor berikut: \ni. jangka masa untuk menilai tawaran dari segi teknikal; \nii. jangka masa untuk menilai tawaran dari segi harga; \niii. jangka masa untuk menilai keupayaan petender; \niv. jangka masa untuk menyediakan laporan dan masa yang diperlukan untuk membuat keputusan bagi tender tersebut; dan \nv. jangka masa untuk membuat keputusan dan mengeluarkan Surat Setuju Terima kepada petender yang berjaya. '),
(311, 'MP', 'C : Tatacara', '3.2.6( c )', 'Penilaian tender hendaklah dilakukan dengan segera dan tidak melewati tempoh ditetapkan dalam jadual pelaksanaan tender. '),
(312, 'MP', 'C : Tatacara', '3.2.6(d)', 'Tempoh sah laku tender yang minimum adalah 90 hari, yang dikira dari tarikh tender ditutup. Majlis hendaklah memastikan supaya tempoh sah laku tender tidak terlalu panjang kerana ini menyebabkan petender menaikkan harga tawaran mereka.'),
(313, 'MP', 'C : Tatacara', '3.2.6(e)', 'Majlis tidak digalakkan untuk melanjutkan tempoh sah laku tawaran tender daripada tempoh sah yang telah ditetapkan kerana ini boleh menyebabkan petender menarik diri. Sekiranya perlanjutan tempoh sah laku tawaran tender diperlukan, pelanjutan hendaklah dibuat sebelum tarikh tempoh sah laku asal tamat dengan kelulusan Pengerusi Jawatankuasa Tender Majlis. '),
(314, 'MP', 'C : Tatacara', '3.2.6(f)', 'Sebarang pembatalan tender hendaklah mendapat kelulusan Jawatankuasa Tender Majlis. '),
(315, 'MP', 'C : Tatacara', '3.2.7', 'Deposit Tender '),
(316, 'MP', 'C : Tatacara', '3.2.7(a)', 'Deposit tender hendaklah dikenakan sebanyak 2.5% daripada tawarannya. Deposit tender hendaklah dalam bentuk draf bank/jaminan bank dan hendaklah dikemukakan bersekali dengan tawaran tender. '),
(317, 'MP', 'C : Tatacara', '3.2.7(b)', 'Deposit tender akan dikembalikan seperti berikut: \ni. kepada petender yang berjaya, sebaik sahaja petender tersebut mengemukakan Bon Pelaksanaan; dan\nii. kepada petender yang menyertai tawaran tetapi tidak berjaya, sebaik sahaja tawaran tender tersebut di setuju terima oleh petender yang berjaya atau selepas tamatnya tempoh sah laku tender, mengikut yang mana terdahulu. '),
(318, 'MP', 'C : Tatacara', '3.2.7( c)', 'Dalam pelawaan tender hendaklah disyaratkan bahawa bagi mereka yang dikenakan bayaran deposit tender jika petender enggan menandatangani sesuatu kontrak selepas tendernya disetuju terima atau menarik balik tendernya sebelum keputusan dibuat, maka deposit tendernya akan dirampas. Deposit tender bagi petender yang tidak berjaya hendaklah dikembalikan setelah tender itu disetuju terima oleh petender yang berjaya atau selepas tamatnya tarikh sah laku tender. Deposit petender yang berjaya hendaklah dikembalikan selepas Bon Pelaksanaan diterima. '),
(319, 'MP', 'C : Tatacara', '3.2.8', 'Dokumen Tender Yang Mempunyai Security Document/Security Drawing '),
(320, 'MP', 'C : Tatacara', '3.2.8.(a) ', 'Definisi Security Document berdasarkan kepada Seksyen 41, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003. '),
(321, 'MP', 'C : Tatacara', '3.2.8 (b)', 'Berdasarkan  tafsiran  Arahan  Keselamatan,  Security Document adalah dokumen terperingkat, iaitu dokumen yang mengandungi maklumat rasmi yang mesti diberi perlindungan untuk kepentingan keselamatan dan yang bertanda dengan sesuatu peringkat keselamatan. \n'),
(322, 'MP', 'C : Tatacara', '3.2.8 ( c )', 'Mengikut Akta Rahsia Rasmi 1972 (Akta 88), \nSecurityDocument adalah termasuk dokumen-dokumen seperti berikut: \ni. sesuatu peta, pelan, model, graf atau lukisan; \nii. sesuatu gambar foto; \niii. sesuatu piring hitam, pita rakam, runut bunyi atau data-data lain (bukan imej yang boleh dilihat) supaya boleh (dengan atau tanpa bantuan sesuatu alat kelengkapan lain) dikeluarkan ulang darinya; dan  \niv. sesuatu filem, negatif, pita rakam atau alat-alat lain yang mengandungi satu imej atau lebih yang boleh dilihat supaya boleh (seperti tersebut di atas) dikeluarkan ulang darinya; dan apa-apa juga yang ditafsirkan oleh Majlis sebagai Security Document. '),
(323, 'MP', 'C : Tatacara', '3.2.8 (d)', 'Berdasarkan tafsiran Arahan Keselamatan, Security Drawing termasuk di bawah dokumen rasmi, iaitu apa-apa jenis maklumat yang tercatat berkenaan dengan perkara-perkara rasmi. '),
(324, 'MP', 'C : Tatacara', '3.2.8 (e)', 'Security Drawing adalah termasuk dokumen-dokumen seperti berikut: \ni. perkara yang bertulis, bertaip, bertulis trengkas, disalin, berstensil dan bercetak dan juga draf serta buangan daripada perkara itu; \nii. fotograf, foto salinan, pelan cetak, negatif foto dan filem, jalur suara dan rakaman; \niii. pelan, pelan lakar, lukisan, gambar rajah, peta dan aneka jenis carta; huruf cetak atur atau huruf cetak miring, blok litograf, acuan, stensil, proses plat atau alat-alat lain yang digunakan untuk membuat dokumen- dokumen rasmi; dan\niv. apa-apa juga yang ditafsirkan oleh Setiausaha Majlis sebagai Security Drawing. \n'),
(325, 'MP', 'C : Tatacara', '3.2.8 (f)', 'Bagi  dokumen  tender  yang  mempunyai Security Drawing atau Security Document, deposit sebanyak RM5,000.00 hendaklah dikenakan kepada petender. Deposit ini akan dikembalikan apabila Security Drawing atau Security Document tersebut dikembalikan kepada Majlis dalam keadaan asal semasa ianya dikeluarkan. '),
(326, 'MP', 'C : Tatacara', '3.2.8 (g)', 'Setiausaha Majlis hendaklah mengadakan peruntukan dalam dokumen tender di mana petender dikehendaki memberi Aku Janji untuk tidak menzahirkan (reproduce) dan tidak membocorkan apa-apa maklumat dalam Security Drawing/Security Document dan untuk mengembalikan Security Drawing/Security Document kepada Majlis seperti yang dinyatakan dalam dokumen tender. '),
(327, 'MP', 'C : Tatacara', '3.3', 'Penerimaan Tender '),
(328, 'MP', 'C : Tatacara', '3.3.1', 'Urus setia Tender hendaklah memastikan Dokumen Tender diterima sebagaimana yang telah dijadualkan'),
(329, 'MP', 'C : Tatacara', '3.3.2', 'Dokumen yang diterima selepas tempoh tender ditutup tidak akan dipertimbangkan. '),
(330, 'MP', 'C : Tatacara', '3.4', 'Pembukaan Sampul Tender '),
(331, 'MP', 'C : Tatacara', '3.4.1', 'Peti tawaran hendaklah dibuka secepat mungkin selepas tender ditutup.'),
(332, 'MP', 'C : Tatacara', '3.4.2', 'Jawatankuasa Pembuka Tender mestilah bersidang dalam tempoh 24 jam selepas sesuatu tender ditutup. '),
(333, 'MP', 'C : Tatacara', '3.4.3', 'Jawatankuasa yang bersidang hendaklah memastikan bilangan ahli jawatankuasa memenuhi korum yang dikehendaki. '),
(334, 'MP', 'C : Tatacara', '3.4.4', 'Semua tawaran tender bekalan/perkhidmatan yang dianggarkan bernilai melebihi RM30 juta dan tender kerja yang bernilai melebihi RM50 juta hendaklah dibuka di hadapan awam. '),
(335, 'MP', 'C : Tatacara', '3.4.5', 'Bagi Tender Kerja yang disediakan oleh perunding, dokumen hendaklah diterima di Pejabat Majlis dan hendaklah dibuka dan dijadualkan mengikut amalan biasa oleh Jawatankuasa Pembuka Tender, yang mana jadual tender dan dokumen berkaitan diserahkan kepada Urusetia Jawatankuasa Tender yang akan bertanggungjawab menyimpan secara sulit sehingga ianya dikemukakan kepada Jawatankuasa Tender MAIS dan Dokumen tender itu hendaklah kemudiannya dihantar kepada perunding atau pengamal persendirian untuk pemeriksaan dan ulasan.'),
(336, 'MP', 'C : Tatacara', '3.5', 'Pembukaan Tender di Hadapan Awam '),
(337, 'MP', 'C : Tatacara', '3.5.1', 'Semua tawaran tender bekalan/perkhidmatan yang dianggarkan bernilai melebihi RM30 juta dan tender kerja yang bernilai melebihi RM50 juta hendaklah dibuka di hadapan awam. '),
(338, 'MP', 'C : Tatacara', '3.5.2', 'Pembukaan tender di hadapan awam bermaksud pembukaan tender di kalangan petender yang membeli dan mengembalikan dokumen tender sahaja. '),
(339, 'MP', 'C : Tatacara', '3.6', 'Merahsiakan Maklumat dan Kedudukan sesuatu Tender '),
(340, 'MP', 'C : Tatacara', '3.6.1', 'Dokumen Tender Diklasifikasikan Sebagai Sulit \nSemua maklumat berkenaan sesuatu tender termasuk dokumentender, tawaran tender dan penilaian tender hendaklah diklasifikasikan sebagai SULIT dan tidak boleh disebarkan kepada mana-mana pihak yang tidak terlibat dengan pengurusan tender sebelum keputusan muktamad dibuat. '),
(341, 'MP', 'C : Tatacara', '3.6.2', 'Merahsiakan Maklumat Tender '),
(342, 'MP', 'C : Tatacara', '3.6.2 (a)', 'Setiausaha Majlis adalah bertanggungjawab untuk menjelaskan tentang keperluan merahsiakan maklumat dan kedudukan sesuatu tender kepada pegawai yang berkenaan di bawah arahan pentadbirannya. Rujukan hendaklah dibuat kepada peruntukan Akta Rahsia Rasmi1972 (Akta 88) dan Akta Pencegahan Rasuah 1997 (Akta 575) dan Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003 Pindaan 2008. '),
(343, 'MP', 'C : Tatacara', '3.6.2 (b)', 'Mana-mana pegawai Majlis yang didapati membocorkan rahsia berkenaan sesuatu tender adalah dianggap telah melanggarperuntukan dalam Akta-Akta/ Enakmen tersebut dan tindakan yang sewajarnya hendaklah diambil terhadap pegawai Majlis yang terlibat. '),
(344, 'MP', 'C : Tatacara', '3.6.3', 'Peruntukan Melarang Pengiklanan dan Publisiti \nMajlis hendaklah memasukkan peruntukan berkenaan laranganpengiklanan dan publisiti dalam syarat pelawaan tender dan dokumen kontrak. Contoh peruntukan tersebut adalah sepertiberikut: \n?IKLAN & PUBLISITI \nTiada  maklumat  berkenaan  kontrak  boleh disiarkan dalamsebarang akhbar, majalah atau alat pengiklanan/media massamelainkan jika pengiklanan itu telah pun dihantar kepada dandiluluskan oleh Majlis terlebih dahulu.'),
(345, 'MP', 'C : Tatacara', '3.6.4', 'Penjelasan Lanjut Berkenaan Dengan Tender \nKebenaran bertulis hendaklah diperolehi daripada Pengerusi Jawatankuasa Tender sekiranya Jawatankuasa Penilaian memerlukan penjelasan lanjut daripada petender semasa proses penilaian tender. Walau bagaimanapun, sebarang perundingan tidak dibenarkan. Penjelasan tersebut hendaklah diuruskan oleh Urus Setia Tender secara bertulis. '),
(346, 'MP', 'C : Tatacara', '3.6.5', 'Larangan Mengubah Maklumat Dokumen Petender '),
(347, 'MP', 'C : Tatacara', '3.6.5 (a)', 'Semua pihak yang terlibat dalam sesuatu urusan tender termasuk mempertimbangkan sesuatu tender dilarang mengubah apa-apa maklumat yang dikemukakan petender di dalam dokumen tender termasuk harga, pengalaman petender dan sebagainya. '),
(348, 'MP', 'C : Tatacara', '3.6.5 (b)', 'Sekiranya terdapat kesilapan pengiraan oleh petender dan berlaku perbezaan harga di antara harga di dalam Bill Of Quantities (BQ) dan Borang Tender yang telah ditandatangani, harga di dalam Borang Tender hendaklah diguna pakai dalam mempertimbangkan sesuatu perolehan.'),
(349, 'MP', 'C : Tatacara', '3.7', 'Penilaian Tender'),
(350, 'MP', 'C : Tatacara', '3.7.1', 'Penilaian tender bekalan, perkhidmatan dan kerja berasaskan Turnkey atau Design and Build hendaklah dibuat oleh dua jawatankuasa yang berasingan seperti berikut: \n(a) Jawatankuasa Penilaian Teknikal; dan \n(b) Jawatankuasa Penilaian Harga. '),
(351, 'MP', 'C : Tatacara', '3.7.2', 'Jawatankuasa Penilaian Teknikal hendaklah membuat penilaian secara terperinci bagi memastikan penilaian yang dibuat adalah berasaskan kepada spesifikasi perolehan yang ditetapkan dalam dokumen tender. Penilaian hendaklah dibuat berdasarkan dokumen-dokumen yang dikemukakan, pernyataan pematuhan (statement of compliance) dan/atau penilaian fizikal. Walau bagaimanapun, maklumat dan teknologi semasa mengenai barang/perkhidmatan tersebut boleh diambil kira. '),
(352, 'MP', 'C : Tatacara', '3.7.3', 'Jawatankuasa Penilaian Harga '),
(353, 'MP', 'C : Tatacara', '3.7.3 (a)', 'Penilaian harga sesuatu tender hendaklah mengambil kira faktor-faktor seperti syarat pembayaran, kadar pembayaran, tempoh, diskaun, harga pasaran terbuka, keupayaan petender dan sebagainya. Perbandingan antara satu tawaran petender dengan tawaran petender yang lain hendaklah dibuat berasaskan kepada faktor atau unsur yang sama (equal footing).'),
(354, 'MP', 'C : Tatacara', '3.7.3 (b)', 'Penilaian bagi pembelian harta modal seperti kenderaan, jentera berat, sistem maklumat dan lain-lain hendaklah berdasarkan kepada prinsip tawaran harga menyeluruh (total cost bid), yang bermaksud mengambil kira harga barangan, alat ganti, latihan, pentauliahan /pemasangan, jaminan, penyenggaraan dan lain-lain. '),
(355, 'MP', 'C : Tatacara', '3.7.4', 'Proses penilaian tender hendaklah disiapkan dan dilaksanakan seboleh-bolehnya tidak melebihi tempoh 30 hari dari tarikh tawaran tender dikemukakan kepada Jawatankuasa Penilaian. '),
(356, 'MP', 'C : Tatacara', '3.7.5', 'Laporan penilaian hendaklah ditandatangani oleh semua ahli Jawatankuasa. '),
(357, 'MP', 'C : Tatacara', '3.8', 'Asas Penilaian Tender'),
(358, 'MP', 'C : Tatacara', '3.8.1', 'Memastikan petender memenuhi kesemua syarat yang dinyatakan dalam dokumen tender. '),
(359, 'MP', 'C : Tatacara', '3.8.2', 'Menilai kemampuan teknikal petender seperti kakitangan profesional dan teknikal, pengalaman kerja, rekod prestasi, projek yang sedang dilaksanakan/ dalam tangan dan loji (disewa atau milik sendiri). '),
(360, 'MP', 'C : Tatacara', '3.8.3', 'Menilai kemampuan kewangan petender dari segi modal pusingan, net worth, kemudahan kredit, nilai baki kerja dalam tangan yang belum siap dan sebagainya. '),
(361, 'MP', 'C : Tatacara', '3.8.4', 'Menganalisis harga tender dengan mengambil kira tempoh siap /penghantaran yang ditawarkan berdasarkan tawaran yang paling menguntungkan Majlis. '),
(362, 'MP', 'C : Tatacara', '3.9', 'Penyediaan Kertas Taklimat Tender '),
(363, 'MP', 'C : Tatacara', '3.9.1', 'Urus setia yang menguruskan tender hendaklah menyediakan Kertas Taklimat Tender untuk pertimbangan Jawatankuasa Tender MAIS. '),
(364, 'MP', 'C : Tatacara', '3.9.2', 'Kertas taklimat menyatakan syor secara khusus petender yang dicadangkan untuk dipertimbangkan. '),
(365, 'MP', 'C : Tatacara', '3.9.3', 'Dalam membuat pengesyoran, pertimbangan hendaklah diberi kepada harga beli, tambang, pengendalian membungkus barang ganti, servis, penyelenggaraan, kuasa, penggunaan bahan api, bahan mentah, nilai jual semula dan kos-kos lain mengenai barang itu.'),
(366, 'MP', 'C : Tatacara', '3.9.4', 'Kertas Taklimat Tender hendaklah mengandungi (yang mana berkaitan): \n(a) satu salinan tawaran pelawaan tender; \n(b) satu salinan iklan surat khabar jika ada; \n(c) salinan spesifikasi tender; \n(d) satu jadual pembuka tender; \n(e) Laporan Penilaian Tender; \n(f) syor setuju terima bagi satu tawaran tertentu dengan penerangan mengenai pilihan itu; \n(g) ulasan kepada analisis perunding atau pengamal persendirian jika ada; dan \n(h) maklumat-maklumat lain yang mungkin dikehendaki atau disyaratkan oleh Jawatankuasa Tender. '),
(367, 'MP', 'C : Tatacara', '3.9.5', 'Ketua Bahagian Pentadbiran/setara hendaklah membuat perakuan kepada Jawatankuasa Tender MAIS berdasarkan kepada Laporan Penilaian Tender. Perakuan tersebut hendaklah berdasarkan tawaran yang terbaik dari segi harga dan kualiti mengikut keperluan Majlis dan hendaklah mengambil kira prinsip-prinsip perolehan dan dasar-dasar Majlis. Perakuan tersebut juga hendaklah menyatakan dengan khusus tawaran petender yang dicadangkan untuk dipertimbangkan. '),
(368, 'MP', 'C : Tatacara', '3.1', 'Jawatankuasa Tender MAIS \nKeanggotaan Jawatankuasa adalah seperti di Bab B Perkara 14.1. '),
(369, 'MP', 'C : Tatacara', '3.11', 'Pertimbangan Tender '),
(370, 'MP', 'C : Tatacara', '3.11.1', 'Jawatankuasa Tender hendaklah bersidang/bermesyuarat untuk memutuskan Tender. Pertimbangan Tender secara edaran dibenarkan selepas mendapat kelulusan khas Pengerusi Jawatankuasa Tender. '),
(371, 'MP', 'C : Tatacara', '3.11.2', 'Segala perbincangan Jawatankuasa Tender hendaklah dicatatkan dalam minit dan keputusan sesuatu Tender hendaklah mendapatkan tandatangan Pengerusi dan dua Ahli Jawatankuasa Tender. Asas-asas pemilihan tender hendaklah dicatatkan dengan jelas pada kertas pertimbangan. '),
(372, 'MP', 'C : Tatacara', '3.11.3', 'Keputusan Jawatankuasa Tender hendaklah sebulat suara. Keputusan yang tidak sebulat suara dan/atau melebihi angggaran jabatan hendaklah diputuskan oleh Pengerusi Jawatankuasa. '),
(373, 'MP', 'C : Tatacara', '3.11.4', 'Jika tawaran yang paling rendah tidak disetuju terima, sebab-sebab bagi menyetuju terima suatu tawaran alternatif hendaklah dicatatkan dalam minit. '),
(374, 'MP', 'C : Tatacara', '3.11.5', 'Urusetia Jawatankuasa Tender MAIS hendaklah merekodkan segala keputusan penting seperti nombor tender, tarikh keputusan tender, nama/kod petender yang berjaya, harga tawaran, tempoh siap, sebab-sebab penerimaan /penolakan dan sebagainya '),
(375, 'MP', 'C : Tatacara', '3.12', 'Keputusan Tender'),
(376, 'MP', 'C : Tatacara', '3.12.1', 'Setelah Jawatankuasa Tender membuat keputusan, surat tawaran/lantikan dikeluarkan dengan segera kepada pembuat/pembekal/kontraktor yang berjaya '),
(377, 'MP', 'C : Tatacara', '3.12.2', 'Keputusan muktamad Jawatankuasa Tender MAIS hendaklah dipamerkan di papan kenyataan Majlis dan disiarkan di dalam laman web MAIS (Portal MAIS)  www.mais.gov.my dengan menyatakan nama syarikat yang berjaya, harga tawaran dan tempoh siap secepat mungkin selepas Surat Setuju Terima dikeluarkan, ditandatangani dan dikembalikan. '),
(378, 'MP', 'C : Tatacara', '3.12.3', 'Sekiranya sesuatu tender itu perlu dirujuk kepada Jawatankuasa Baitulmal MAIS untuk keputusan, rujukan tersebut hendaklah sampai kepada urusetia Jawatankuasa Baitulmal dalam tempoh tujuh (7) hari selepas keputusan Jawatankuasa Tender. '),
(379, 'MP', 'C : Tatacara', '3.12.4', 'Urusetia    Jawatankuasa    Baitulmal    hendaklah memanggil untuk bersidang dengan segera bagi memutuskan perkara berbangkit.'),
(380, 'MP', 'C : Tatacara', '4', 'PENTADBIRAN KONTRAK '),
(381, 'MP', 'C : Tatacara', '4.1', 'Pelantikan Pembekal/Kontraktor '),
(382, 'MP', 'C : Tatacara', '4.1.1', 'Surat Niat'),
(383, 'MP', 'C : Tatacara', '4.1.1 (a)', 'Surat Niat ialah surat yang dihantar kepada penyebutharga/petender yang mana kandungannya meliputi kenyataan hasrat/niat untuk menerima tawaran penyebutharga/petender tersebut, tertakluk kepada persetujuan syarat-syarat Majlis yang dikehendaki dipatuhi oleh penyebutharga/ petender sama ada melalui rundingan atau cara-cara lain. Surat Niat ini tidak mengikat kedua-dua pihak kepada satu kontrak. '),
(384, 'MP', 'C : Tatacara', '4.1.1 (b)', 'Sekiranya Majlis bercadang untuk menerima sesuatu tawaran tetapi dengan beberapa syarat yang mesti dipatuhi oleh penyebutharga/petender terlebih dahulu, maka Surat Niat hendaklah dikeluarkan. '),
(385, 'MP', 'C : Tatacara', '4.1.2', 'Surat Tawaran'),
(386, 'MP', 'C : Tatacara', '4.1.2 (a)', 'Surat tawaran ialah surat yang dihantar kepada penyebutharga/petender yang berjaya. '),
(387, 'MP', 'C : Tatacara', '4.1.2 (b)', 'Kandungan surat tawaran adalah seperti berikut: \ni. Tajuk tawaran/keperluan kontrak; \nii. Nilai perolehan yang mana nilainya telah ditetapkan dan tiada perbincangan penetapan harga akan dijalankan. Nilai perolehan yang belum diputuskan tidak perlu dinyatakan; \niii. Nilai Bon Pelaksanaan jika dikenakan; \niv. Tarikh mula dan tamat kerja/perkhidmatan; \nv. Tempoh kerja/kontrak; \nvi. Pengesahan kerja; \nvii. Cara tuntutan bayaran; \nviii. Lokasi penghantaran; \nix. Tempoh percubaan jika ada; \nx. Spesifikasi/skop kerja; \nxi. Obligasi/tanggungjawab \npembekal/kontraktor; \nxii. Tempoh tanggungan kecatatan kerja; dan \nxiii. Lain-lain mengikut kesesuaian. '),
(388, 'MP', 'C : Tatacara', '4.1.3', 'Surat/Perakuan Setuju Terima Tawaran '),
(389, 'MP', 'C : Tatacara', '4.1.3 (b)', 'Surat/Perakuan Setuju Terima Tawaran ialah surat yang mengesahkan penerimaan sesuatu tawaran berasaskan kepada syarat yang telah ditetapkan dalam dokumen tender/sebut harga, syarat yang telah dipersetujui dalam Surat Niat dan apa-apa syarat lain yang telah dipersetujui melalui rundingan sebelumnya. Sehubungan dengan itu, apa-apa syarat yang telah dipersetujui sebelumnya hendaklah dinyatakan dengan jelas dan persetujuan tersebut merupakan sebahagian daripada perjanjian kontrak. Surat Setuju Terima ialah satu dokumen perundangan yang sah dan merupakan sebahagian daripada perjanjian kontrak.'),
(390, 'MP', 'C : Tatacara', '3.4.1 ( c )', 'Surat/Perakuan Setuju Terima Tawaran mestilah dikembalikan kepada Majlis dalam tempoh yang ditetapkan. '),
(391, 'MP', 'C : Tatacara', '4.2', 'Bon Pelaksanaan \nBon Pelaksanaan adalah merupakan suatu aku janji tidak bersyarat (unconditional undertaking) oleh sesuatu institusi kewangan yang dibenarkan oleh Baitulmal untuk dibayar kepada Majlis suatu amaun tertentu atas tuntutan (on demand) sekiranya kontraktor tidak atau gagal mematuhi dan melaksanakan obligasinya di bawah kontrak. '),
(392, 'MP', 'C : Tatacara', '4.2.1', 'Nilai Bon Pelaksanaan (Bekalan dan Perkhidmatan) '),
(393, 'MP', 'C : Tatacara', '4.2.1 (a)', 'Bon Pelaksanaan hendaklah dikenakan bagi perolehan bekalan, perkhidmatan yang bernilai melebihi RM50,000.00 dan tidak melebihi RM500,000.00 sebanyak 2.5%.'),
(394, 'MP', 'C : Tatacara', '4.2.1 (b)', 'Bon Pelaksanaan hendaklah dikenakan bagi perolehan bekalan, perkhidmatan yang bernilai melebihi RM500,000.00 sebanyak 5%. '),
(395, 'MP', 'C : Tatacara', '4.2.1 ( c )', 'Bagi kontrak bermasa (periodic contract) yang berkuatkuasa dalam tempoh dua (2) tahun atau lebih, peratus dan pengiraan nilai Bon Pelaksanaan hendaklah dikira mengikut harga kontrak iaitu bagi nilai kontrak tidak melebihi RM500,000.00. '),
(396, 'MP', 'C : Tatacara', '4.2.1 (d)', '(Bagi kontrak bermasa (periodic contract) yang berkuatkuasa dalam tempoh dua (2) tahun atau lebih, peratus dan pengiraan nilai Bon Pelaksanaan hendaklah dikira mengikut harga kontrak setahun sahaja iaitu bagi nilai kontrak melebihi RM500,000.00. Bon Pelaksanaan yang dikemukakan oleh kontraktor hendaklah meliputi keseluruhan tempoh kontrak bermasa. '),
(397, 'MP', 'C : Tatacara', '4.2.1 (e)', 'Bon Pelaksanaan hendaklah dikemukakan dalam Ringgit Malaysia (RM) sama ada dari bank-bank atau institusi-institusi kewangan yang dibenarkan oleh Majlis sama ada dalam bentuk: \ni. Tunai; \nii. Bank Deraf; dan \niii. Jaminan Bank/Syarikat Kewangan yang dikeluarkan oleh bank-bank/syarikat kewangan berlesen di bawah Akta Bank dan Institusi Kewangan 1989 /Akta Bank Islam 1983/Jaminan Insurans Insurans yang dikeluarkan oleh syarikat-syarikat insurans yang berlesen di bawah Akta Insurans 1996/Jaminan Takaful yang dikeluarkan oleh SyarikatTakaful yang berlesen di bawah Akta Takaful/Jaminan yang dikeluarkan oleh Bank PembangunanMalaysia Berhad (BPMB) dan Bank Perusahaan Kecil& Sederhana Malaysia (SME Bank). '),
(398, 'MP', 'C : Tatacara', '4.2.1 (f)', 'Bon Pelaksanaan tidak akan dikenakan bagi perolehan bekalan/perkhidmatan one off seperti contoh pembelian Al-Quran, alat tulis dan komputer '),
(399, 'MP', 'C : Tatacara', '4.2.2', 'Nilai Bon Pelaksanaan (Kerja) '),
(400, 'MP', 'C : Tatacara', '4.2.2 (a)', 'Bon Pelaksanaan bagi kontrak kerja yang bernilai melebihi RM200,000 ialah sebanyak 5% daripada jumlah harga kontrak. '),
(401, 'MP', 'C : Tatacara', '4.2.2 (b)', 'Bon Pelaksanaan hendaklah dikemukakan dalam Ringgit Malaysia (RM) sama ada dari bank-bank atau institusi-institusi kewangan yang dibenarkan oleh Majlis sama ada dalam bentuk:\ni. Tunai; \nii. Bank Deraf; dan \niii. Jaminan Bank/Syarikat Kewangan yang dikeluarkan oleh bank-bank/ syarikat kewangan berlesen di bawah Akta Bank dan Institusi Kewangan 1989 /Akta Bank Islam 1983/Jaminan Insurans Insurans yang dikeluarkan oleh syarikat-syarikat insurans yang berlesen di bawah Akta Insurans 1996/Jaminan Takaful yang dikeluarkan oleh SyarikatTakaful yang berlesen di bawah Akta Takaful/Jaminan yang dikeluarkan oleh Bank PembangunanMalaysia Berhad (BPMB) dan Bank Perusahaan Kecil & Sederhana Malaysia (SME Bank). '),
(402, 'MP', 'C : Tatacara', '4.2.3', 'Peraturan Berkenaan Bon Pelaksanaan '),
(403, 'MP', 'C : Tatacara', '4.2.3 (a)', 'Kontrak Bekalan/Perkhidmatan \ni. Bon Pelaksanaan hendaklah dikemukakan dalam bentuk seperti di perkara 4.2.1(e). \nii. Tempoh   sah   laku   jaminan   tersebut hendaklah dari tarikh ianya dikeluarkan sehingga 12 bulan selepas tarikh tamat kontrak atau selepas obligasi terakhir, mengikut mana yang terkemudian.'),
(404, 'MP', 'C : Tatacara', '4.2.3 (b)', 'Kontrak Kerja \ni. Bon Pelaksanaan hendaklah dikemukakan dalam bentuk seperti di perkara 4.2.1(e) atau dalam bentuk Wang Jaminan Pelaksanaan. \nii. Wang Jaminan Pelaksanaan (WJP) ialah satu kemudahan bagi memenuhi keperluan Bon Pelaksanaan. WJP bermaksud sejumlah wang yang dipegang oleh Majlis bagi memastikan kontraktor mematuhi dan melaksanakan obligasinya di bawah kontrak yang ditandatangani. \niii. Jumlah WJP adalah lima peratus (5%) daripada harga keseluruhan kontrak. Kemudahan ini hanya dibenarkan kepada kontraktor kerja tempatan/Bumiputera sahaja. Walau bagaimanapun, Wang Jaminan Pelaksanaan tidak dibenarkan untuk Subkontraktor Dinamakan bagi Kerja. \niv. Sekiranya kontraktor gagal mengemukakan bon pelaksanaan seperti di perkara 4.2.1(e) pada tarikh milik tapak, secara automatik kaedah Wang Jaminan Pelaksanaan akan digunapakai. Kontraktor yang memilih/ dikenakan kaedah WangJaminan Pelaksanaan akan dikenakan potongan sebanyaksepuluh peratus (10%) daripada bayaran kemajuanpertama dan seterusnya sehingga ia mencapai jumlah lima peratus (5%) daripada nilai keseluruhan kontrak. \nv. WJP yang dipotong daripada bayaran kemajuan hendaklah diakaunkan mengikut Amalan Terbaik Pengurusan Kewangan MAIS. \nvi. Tempoh sah laku Bon Pelaksanaan tersebut hendaklah dari tarikh kuat kuasa kontrak sehingga 12 bulan selepas tamat Tempoh Tanggungan Kecacatan. \nvii. Jika berlaku kelewatan dalam penyiapan projek, tindakan serta merta hendaklah diambil oleh Majlis untuk memastikan tempoh sah laku Bon Pelaksanaan '),
(405, 'MP', 'C : Tatacara', '4.2.4', 'Mengesahkan Surat Jaminan '),
(406, 'MP', 'C : Tatacara', '4.2.4 (a)', 'Majlis hendaklah menyemak dengan Bank/Syarikat Kewangan/ Bank Islam/Syarikat Insurans/Syarikat Takaful yang berkenaan tentang kesahihan Bon Pelaksanaan yang dikemukakan kontraktor. '),
(407, 'MP', 'C : Tatacara', '4.2.4 (b)', 'Sekiranya terdapat kes pemalsuan Bon Pelaksanaan, Pegawai Pengawal hendaklah membuat laporan polis supaya tindakan dapat diambil kerana kes pemalsuan sedemikian adalah merupakan kes jenayah. '),
(408, 'MP', 'C : Tatacara', '4.2.4 (c )', 'Bagi kontrak bekalan dan perkhidmatan, Ketua Bahagian dikehendaki melaporkan segera kepada Majlis dan bagi kontrak kerja, hendaklah melaporkan segera kepada Lembaga Pembangunan Industri Pembinaan Malaysia (LPIPM) supaya tindakan sewajarnya boleh diambil. '),
(409, 'MP', 'C : Tatacara', '4.2.5', 'Pelepasan Bon Pelaksanaan Bekalan/Perkhidmatan '),
(410, 'MP', 'C : Tatacara', '4.2.5 (a)', 'Bon Pelaksanaan boleh dilepaskan setelah segala obligasi kontrak selesai. Surat Pelepasan (Letter   of   Discharge)   bagi Jaminan Bank/Jaminan Syarikat Kewangan/Jaminan BankIslam/Jaminan Insurans/ Jaminan Takaful hendaklah dikemukakan kepada Bank /Syarikat Kewangan/Bank Islam/Syarikat Insurans /Syarikat Takaful. Salinan asal atau satu salinan yang telah disahkan oleh Pegawai Pengawal hendaklah disimpan sebagai rekod.'),
(411, 'MP', 'C : Tatacara', '4.2.5 (b)', 'Bagi perkhidmatan dan barang guna habis (consumableitem) Bon Pelaksanaan boleh dilepaskan lebih awal sekiranya Majlis berpuas hati segala obligasi kontrak selesaidan tiada sebarang tuntutan yang akan dibuat. '),
(412, 'MP', 'C : Tatacara', '4.2.6', 'Pelepasan Bon Pelaksanaan Kerja '),
(413, 'MP', 'C : Tatacara', '4.2.6 (a)', 'Jaminan Bank/Jaminan Syarikat Kewangan/Jaminan Bank Islam/Jaminan Insurans/Jaminan Takaful \ni. Sebaik sahaja Perakuan Siap Kerja dikeluarkan ,Majlis boleh mengurangkan nilai Bon Pelaksanaan yang asal sebanyak lima puluh peratus (50%). \nii. Majlis dikehendaki memaklumkan secara bertulis tentang perkara ini dan kontraktor berkenaan hendaklah menggantikan Jaminan yang asal dengan Jaminan baru yang nilainya bersamaan dengan lima puluh peratus (50%) dari Jaminan asal.\niii. Setelah Jaminan baru ini diterima, Majlis hendaklah melepaskan Bon Pelaksanaan yang asal kepada kontraktor berkenaan. '),
(414, 'MP', 'C : Tatacara', '4.2.6 (b)', 'Wang Jaminan Pelaksanaan \ni. Majlis boleh memulangkan sebanyak lima puluh peratus (50%) daripada nilai Wang JaminanPelaksanaan yang ditahan setelah Sijil Perakuan Siap Kerja dikeluarkan. \nii. Baki Wang Jaminan Pelaksanaan sebanyak lima puluh peratus (50%) lagi boleh dipulangkan sekiranya kontraktor dapat mengemukakan Jaminan seperti di perkara 4.2.1(e) yang sama nilai dengan Wang Jaminan Pelaksanaan yang ditahan. \niii. Baki Bon Pelaksanaan sebanyak lima puluh peratus (50%) lagi hendaklah dilepaskan/dipulangkan 12 bulan selepas tamat tempoh tanggungan kecacatan atau setelah Perakuan Siap Memperbaiki Kecacatan dikeluarkan, mengikut mana yang terkemudian.\niv. Setelah segala obligasi kontrak selesai, Surat Pelepasan (Letter of Discharge) bagi Bon Pelaksanaan hendaklah dikemukakan kepada Bank/ Syarikat Kewangan /Bank Islam/Syarikat Insurans /Syarikat Takaful. Salinan asal atau satu salinan yang telah disahkan. '),
(415, 'MP', 'C : Tatacara', '4.3', 'PERJANJIAN/KONTRAK '),
(416, 'MP', 'C : Tatacara', '4.3.1', 'Peruntukan Akta Kontrak Kerajaan 1949 (Semakan 1973) '),
(417, 'MP', 'C : Tatacara', '4.3.1 (a)', 'Jika didapati perlu atau sesuai mengadakan satu kontrak rasmi, kontrak itu hendaklah dibuat di atas nama Majlis dan ditandatangani mengikut kaedah-kaedah yang dibuat di bawah Akta Kontrak Kerajaan 1949 (Disemak 1973). '),
(418, 'MP', 'C : Tatacara', '4.3.1 (b)', 'Sehubungan dengan peruntukan di atas, Majlis hendaklah memastikan pemberian kuasa telah dibuat secara bertulis kepada Pegawai Pengawal dan pegawai lain yang telah dinamakan (atas jawatan) sebelum kontrak ditandatangani. '),
(419, 'MP', 'C : Tatacara', '4.3.2', 'Tempoh bagi Menandatangani Kontrak \nKontrak hendaklah ditandatangani secepat mungkin selepas semua terma dan syarat dipersetujui oleh kedua-dua pihak dan selewat-lewatnya empat bulan dari tarikh keluarnya Surat Setuju Terima. Tarikh kuat kuasa kontrak (effective date) hendaklah dinyatakan sebagai tarikh Surat Setuju Terima ditandatangani oleh petender yang berjaya manakala tarikh kontrak ialah tarikh kontrak ditandatangani oleh kedua-dua pihak. Bagi kontrak kerja reka dan bina sebarang perubahan kepada terma-terma kontrak hendaklah dipersetujui oleh kedua-dua pihak. '),
(420, 'MP', 'C : Tatacara', '4.3.3', 'Pengedaran dan Penyimpanan Dokumen Kontrak \nPenjelasan mengenai pengedaran dan penyimpanan dokumen kontrak adalah seperti dalam Amalan Terbaik Pengurusan Kewangan Majlis Agama Islam yang berkaitan. Salinan-salinan dokumen kontrak dan perubahan kontrak atau Arahan Perubahan dan Sijil Lanjutan Masa hanya perlu dikemukakan kepada Ketua Akauntan Majlis sahaja. '),
(421, 'MP', 'C : Tatacara', '4.4', 'Perubahan Dan Perlanjutan Tempoh Kontrak Untuk Kontrak '),
(422, 'MP', 'C : Tatacara', '4.4.1', 'Bekalan Dan Perkhidmatan \nPerubahan kontrak dan perlanjutan tempoh kontrak bekalan dan perkhidmatan boleh diluluskan oleh Jawatankuasa Sebut Harga dengan syarat-syarat berikut: '),
(423, 'MP', 'C : Tatacara', '4.4.1 (a)', 'Dibenarkan bagi kontrak yang bernilai melebihi RM200,000.00 hingga RM500,000.00; '),
(424, 'MP', 'C : Tatacara', '4.4.1 (b)', 'Kontrak masih berkuatkuasa semasa perlanjutan tempoh kontrak dipertimbangkan; '),
(425, 'MP', 'C : Tatacara', '4.4.1 (c )', 'Perubahan kontrak dan perlanjutan tempoh kontrak tidak melibatkan perubahan kadar harga dan syarat-syarat lain dalam kontrak; '),
(426, 'MP', 'C : Tatacara', '4.4.1 (d)', 'Perlanjutan tempoh kontrak hanya diberi sekali sahaja dan tempoh maksimum adalah sehingga dua (2) tahun; dan '),
(427, 'MP', 'C : Tatacara', '4.4.1 (e)', 'Bagi kontrak bekalan, tambahan kuantiti tidak melebihi 20% daripada kontrak asal tertakluk kepada nilai maksimum RM100,000.00 dengan syarat jumlah keseluruhan tambahan ini dan nilai kontrak asal tidak melebihi RM600,000.00; atau'),
(428, 'MP', 'C : Tatacara', '4.4.1 (f)', 'Bagi kontrak perkhidmatan, nilai tambahan tidak melebihi 20% daripada kontrak asal tertakluk kepada nilai maksimum RM100,000.00 dengan syarat jumlah tambahan ini dan nilai kontrak asal tidak melebihi RM600,000.00. '),
(429, 'MP', 'C : Tatacara', '4.4.2', 'Perubahan Kerja dan Pelanjutan Masa Untuk Kontrak Kerja '),
(430, 'MP', 'C : Tatacara', '4.4.2 (a)', 'Perubahan kerja dibenarkan sekiranya perlu tertakluk kepada syarat-syarat berikut: \ni. Peruntukan mencukupi; \nii. Kelulusan Jawatankuasa Sebut Harga diperolehi terlebih dahulu sebelum perubahan kerja dilaksanakan. Seorang daripada ahli Jawatankuasa Sebut Harga hendaklah terdiri daripada Pegawai Teknikal; dan \niii. Jumlah perubahan kerja terkumpul yang dicadangkan tidak melebihi 20% daripada nilai kontrak atau tidak melebihi RM100,000.00 mengikut mana yang terendah. Cadangan perubahan \nkerja terkumpul melebihi 20% daripada nilai kontrak atau RM100,000.00 hendaklah dirujuk untuk kelulusan Jawatankuasa Baitulmal terlebih dahulu.'),
(431, 'MP', 'C : Tatacara', '4.4.2 (b)', 'Pelanjutan masa bagi kontrak kerja bernilai melebihi RM200,000.00 hingga RM500,000.00 dibenarkan tertakluk kepada kelulusan Jawatankuasa Sebut Harga terlebih dahulu. '),
(432, 'MP', 'C : Tatacara', '4.4.3', 'Definisi dan kriteria Perubahan Kerja adalah sama seperti yang ditetapkan dalam Tatacara 225 Amalan Terbaik Pengurusan Kewangan MAIS iaitu: \n?Perlanjutan masa perlulah dipertimbangkan dan ditadbir selaras dengan peruntukan syarat-syarat kontrak JKR? .'),
(433, 'MP', 'C : Tatacara', '4.4.4', 'Bagi perubahan kerja dan pelanjutan tempoh kontrak bagi tender yang tidak disebutkan di atas mestilah dirujuk kepada Tatacara 225, 226, 227, 227A dan 227B. '),
(434, 'MP', 'C : Tatacara', '5', 'PEMATUHAN DAN TANGGUNGJAWAB '),
(435, 'MP', 'C : Tatacara', '5.1', 'Tanggungjawab Majlis '),
(436, 'MP', 'C : Tatacara', '5.1.1', 'Majlis dikehendaki mematuhi semua peraturan kewangan yang telah ditetapkan. Bagi maksud ini, Majlis hendaklah merujuk kepada semua peraturan kewangan dan sebagainya yang telah diluluskan sebelum melaksanakan apa-apa jenis perolehan '),
(437, 'MP', 'C : Tatacara', '5.1.2', 'Bagi kaedah perolehan yang tidak dinyatakan di dalam Manual Perolehan ini, kuasa pemutus adalah ditentukan di Jawatankuasa yang yang tertentu seperti Jawatankuasa Baitulmal. '),
(438, 'MP', 'C : Tatacara', '5.2', 'Tanggungjawab Pegawai Pengawal '),
(439, 'MP', 'C : Tatacara', '5.2.1', 'Pegawai Pengawal adalah bertanggungjawab dalam memastikan semua urusan perolehan hendaklah dilaksanakan mengikut peraturan dan prinsip-prinsip serta dasar-dasar perolehan Majlis yang telah ditetapkan. Sebarang pelanggaran terhadap peraturan yang ditetapkan hendaklah diambil tindakan sewajarnya seperti yang ditetapkan dalam Enakmen Pentadbiran Agama Islam Negeri Selangor 2003 Pindaan 2009 atau peraturan kewangan yang sedang berkuatkuasa. '),
(440, 'MP', 'C : Tatacara', '5.2.2', 'Majlis hendaklah menyediakan rekod lengkap yang mengandungi maklumat antaranya tajuk perolehan, nama pembuat/pembekal/ kontraktor, nilai perolehan dan sebagainya. Rekod tersebut hendaklah disimpan dan dikemas kini oleh Majlis dan dikemukakan kepada Mesyuarat Jawatankuasa Kewangan setiap 3 bulan. '),
(441, 'MP', 'C : Tatacara', '5.2.3', 'Dalam memproses Tender/Sebut Harga, Pegawai Pengawal hendaklah memastikan bahawa ia atau pegawai-pegawai yang menguruskan Tender/Sebut Harga itu tidak mempunyai apa-apa kepentingan persendirian atau kepentingan terletak hak mengenai Tender/Sebut Harga itu.'),
(442, 'MP', 'C : Tatacara', '5.2.4', 'Jika sesiapa yang terlibat mengurus Tender/Sebut Harga itu mempunyai kepentingan persendirian atau kepentingan terletak hak dalam apa-apa cara pun dengan petender itu, pegawai tersebut hendaklah menarik diri daripada mengurus Tender/Sebut Harga itu. '),
(443, 'MP', 'C : Tatacara', '5.3', 'Pengecualian \nPengecualian daripada mana-mana perkara dalam manual perolehan ini yang ditetapkan hendaklah mendapat kelulusan bertulis daripada Pegawai Pengawal. '),
(444, 'MP', 'C : Tatacara', '5.4', 'Tender Terhad '),
(445, 'MP', 'C : Tatacara', '5.4.1', 'Walaupun publisiti meluas digalakkan bagi urusan tender, pertimbangan sewajarnya mengenai kos iklan hendaklah juga diambil kira. '),
(446, 'MP', 'C : Tatacara', '5.4.2', 'Sekiranya pembekal yang berkelayakan di bawah satu-satu bidang adalah diketahui tidak lebih lima (5) pembekal, iklan tidak diperlukan. '),
(447, 'MP', 'C : Tatacara', '5.4.3', 'Dalam hal demikian kelulusan Pegawai Pengawal hendaklah diperolehi terlebih dahulu. Setelah mendapat kelulusan pemberitahuan tender kepada pembekal yang diluluskan hendaklah dihantar dengan surat berdaftar atau pos laju.'),
(448, 'MP', 'C : Tatacara', '5.4.4', 'Walau bagaimanapun, Majlis hendaklah mematuhi syarat-syarat yang diwajibkan di dalam peraturan pengurusan tender bekalan, perkhidmatan dan kerja. '),
(449, 'MP', 'C : Tatacara', '5.5', 'Rundingan Terus '),
(450, 'MP', 'C : Tatacara', '5.5.1', 'Dalam keadaan tertentu, setelah Majlis berpuas hati bolehlah ia menawarkan sesuatu kerja atau pembelian itu secara rundingan terus. '),
(451, 'MP', 'C : Tatacara', '5.5.2', 'Seseorang Pegawai Pegawal boleh mengizinkan membuat perolehan yang mana peruntukan telah disediakan dalam anggaran yang diluluskan oleh Majlis. Sebarang pengecualian daripada mana-mana peraturan yang ditetapkan dalam Amalan Terbaik Pengurusan Kewangan MAIS Surat Pekeliling Kewangan hendaklah mendapat kelulusan Majlis terlebih dahulu mengikut mana yang berkenaan. '),
(452, 'MP', 'C : Tatacara', '5.5.3', 'Pegawai Pengawal atau pegawai-pegawai Majlis yang diberi kuasa boleh meluluskan apa-apa barang yang baginya peruntukan telah dibuat dalam Anggaran. Lantikan ini adalah berdasarkan Amalan Terbaik Pengurusan Kewangan MAIS 2003. '),
(453, 'MP', 'D : Kuasa Melulus', '', 'KUASA MELULUS'),
(454, 'MP', 'D : Kuasa Melulus', '1', 'KELULUSAN PEROLEHAN\nPegawai Pengawal atau pegawai-pegawai Majlis yang diberi kuasa boleh meluluskan apa-apa barang yang baginya peruntukan telah dibuat dalam Anggaran. Lantikan ini adalah berdasarkan Amalan Terbaik Pengurusan Kewangan MAIS.'),
(455, 'MP', 'D : Kuasa Melulus', '1.1', 'Bekalan Perkhidmatan'),
(456, 'MP', 'D : Kuasa Melulus', '1.2', 'Kerja');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(457, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '', 'PENDAFTARAN PEMBEKAL / KONTRAKTOR / PERUNDING'),
(458, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '1', 'PENDAFTARAN PEMBEKAL (BEKALAN DAN PERKHIDMATAN)'),
(459, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '1.1', 'Pendaftaran pembekal / kontraktor / perunding di Majlis dengan mengisi borang / online (jika ada). Pendaftaran pembekal atau kontraktor boleh diperolehi di Ibu Pejabat atau mana-mana Cawangan Majlis Agama Islam Selangor.'),
(460, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '1.2', 'Pembekal yang berdaftar sahaja yang akan dipertimbangkan dalam pemberian sebarang perolehan Majlis.'),
(461, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '1.3', 'Pegawai yang menguruskan perolehan tidak tertakluk dengan apa-apa syarat untuk memproses permohonan pendaftaran pembekal melainkan hanya tertakluk dengan peraturan yang ditetapkan oleh Majlis.'),
(462, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2', 'PENDAFTARAN KONTRAKTOR'),
(463, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.1', 'Pendaftaran Kontraktor Kerja'),
(464, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.1.1', 'Daftar kontraktor yang diselenggara oleh Kerajaan bolehlah diterima pakai oleh Majlis.'),
(465, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.1.2', 'Suatu daftar kontraktor kerja akan diselenggara oleh Pusat Khidmat Kontraktor (PKK) lengkap dengan profail kontraktor-kontraktor yang mengandungi rekod prestasi kerja-kerja yang telah dan sedang dijalankan, kedudukan kewangan, nilai maksimum kerja yang mampu dijalankan dan maklumat-maklumat berkaitan. Maklumat- maklumat ini akan digunakan oleh Majlis dalam membuat sesuatu keputusan.'),
(466, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.1.3', 'Majlis dikehendaki menjemput hanya kontraktor-kontraktor kerja dan elektrik yang berdaftar dengan CIDB untuk mengambil bahagian di dalam urusan perolehan yang berkenaan.'),
(467, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.2', 'Pendaftaran Kontraktor Bekalan atau Perkhidmatan'),
(468, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.2.1', 'Pendaftaran kontraktor bekalan dan perkhidmatan adalah diselenggarakan oleh Kementerian Kewangan sahaja sebagai pendaftaran pusat. Majlis dikehendaki menjemput hanya kontraktor-kontraktor yang berdaftar dengan Kementerian Kewangan untuk mengambil bahagian di dalam urusan perolehan kecuali : \n\n(a) Perolehan perkhidmatan yang bernilai tidak melebihi  \n       RM10,000.00 setahun; \n\n(b) Perolehan bekalan yang bernilai tidak melebihi RM10,000.00  \n       setahun bagi setiap jenis item; dan\n\n(c) Majlis boleh mendapatkan perkhidmatan pembekal/ kontraktor \n      yang tidak mempunyai Sijil Kementerian Kewangan dengan pihak \n      Majlis dan Majlis berhak mempertimbangkan untuk berdaftar \n     dengan Majlis.'),
(469, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '2.3', 'Pendaftaran Perunding\nPendaftaran Perunding bagi seluruh Malaysia adalah diselenggarakan oleh Kementerian Kewangan sahaja sebagai pendaftaran pusat. Semua Jabatan/Bahagian dikehendaki menggunakan hanya Perunding yang berdaftar dengan Kementerian Kewangan sahaja.'),
(470, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3', 'SYARAT PENDAFTARAN PEMBEKAL / KONTRAKTOR / PERUNDING'),
(471, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3.1', 'Sila rujuk Bab B Perkara 8 bagi syarat-syarat pendaftaran.'),
(472, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3.2', 'Perlu memberi maklumat yang lengkap berkaitan syarikat yang didaftarkan.'),
(473, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3.3', 'Kontraktor mestilah mempunyai sijil pendaftaran dengan Kementerian Kewangan / Pusat Khidmat Kontraktor / CIDB/ SSM.'),
(474, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3.4', 'Maklumat yang diberi perlu yang terkini dan benar. Majlis berhak membatalkan pendaftaran sekiranya terdapat unsur penipuan dalam maklumat yang diberi.'),
(475, 'MP', 'E : Pendaftaran Pembekal/Kontraktor/Perunding', '3.5', 'Lain-lain syarat yang ditetapkan oleh Majlis.'),
(477, 'TPAAM', 'DEFINISI', '0.a', '<b>"Majlis" </b>bermaksud Majlis Agama Islam Selangor (MAIS) yang ditubuhkan dibawah subseksyen 4(1) mengikut Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(478, 'TPAAM', 'DEFINISI', '0.b', '<b>"Pegawai Pengawal"</b> bermaksud Setiausaha MAIS.'),
(479, 'TPAAM', 'DEFINISI', '0.c', '<b>"Ketua Jabatan"</b> bermaksud mana-mana Pegawai, walau dengan nama apa pun disebut, yang dilantik mengurus aset alih oleh Setiausaha MAIS.'),
(480, 'TPAAM', 'DEFINISI', '0.d', '<b>"Pengarah"</b>&nbsp;bermaksud Pegawai yang mengetuai Sektor-sektor di dalam struktur organisasi MAIS dan bertanggungjawab terus kepada Setiausaha MAIS.'),
(481, 'TPAAM', 'DEFINISI', '0.e', '<b>"Pengurus"</b>&nbsp;bermaksud Pegawai yang mengetuai Bahagian-bahagian di dalam struktur organisasi MAIS dan bertanggungjawab terus kepada Pengarah.'),
(482, 'TPAAM', 'DEFINISI', '0.f', '<b>"Penolong Pengurus"</b> bermaksud Pegawai yang mengetuai unit-unit di struktur organisasi MAIS dan bertanggungjawab terus kepada Pengurus/ Pengarah.'),
(483, 'TPAAM', 'DEFINISI', '0.g', '<b>"Pegawai Penerima"&nbsp;</b>bermaksud pegawai yang diberi tanggungjawab untuk menerima dan mengesahkan aset alih yang diperolehi.'),
(484, 'TPAAM', 'DEFINISI', '0.h', '<b>"Pegawai Bertauliah"&nbsp;</b>bermaksud pegawai yang memiliki kepakaran dalam bidang tertentu.'),
(485, 'TPAAM', 'DEFINISI', '0.i', '<b>"Pelupusan"&nbsp;</b>ialah satu proses untuk mengeluarkan aset alih dari milikan, kawalan, simpanan dan rekod mengikut kaedah yang ditetapkan.'),
(486, 'TPAAM', 'DEFINISI', '0.j', '<b>"Kehilangan"</b>&nbsp;bermaksud aset alih yang tiada lagi dalam simpanan disebabkan oleh kecurian, kemalangan, kebakaran, bencana alam, kesusutan, penipuan atau kecuaian pegawai MAIS.'),
(487, 'TPAAM', 'DEFINISI', '0.k', '<b>"Hapus kira"</b>&nbsp;ialah satu proses untuk membatalkan rekod aset alih yang hilang.'),
(488, 'TPAAM', 'DEFINISI', '0.l', '<b>"Aset"</b>&nbsp;bermaksud harta benda kepunyaan atau milikan atau di bawah kawalan MAIS yang dibeli atau yang disewa beli dengan wang Baitulmal, Zakat dan Wakaf yang diterima melalui sumbangan atau hadiah atau diperolehi melalui proses perundangan.'),
(489, 'TPAAM', 'DEFINISI', '0.m', '<b>"Aset Alih"</b>&nbsp;bermaksud aset yang boleh dipindahkan dari satu tempat ke satu'),
(490, 'TPAAM', 'DEFINISI', '0.n', 'tempat yang lain termasuk aset yang dibekalkan atau dipasang bersekali dengan bangunan.'),
(491, 'TPAAM', 'DEFINISI', '0.p', 'Aset Alih terbahagi kepada dua (2) kumpulan iaitu:-'),
(492, 'TPAAM', 'DEFINISI', '0.q', 'Harta Modal\n(a)Aset Alih yang harga perolehan asalnya RM1,000 (Ringgit Malaysia Satu Ribu) dan ke atas setiap satu; atau \n\n(b)Aset Alih yang memerlukan penyelenggaraan secara berjadual tanpa mengira harga perolehan asal. Penyelenggaraan secara berjadual merujuk kepada aset yang memerlukan penyelenggaraan seperti yang telah disyaratkan di dalam manual/buku panduan pengguna. '),
(493, 'TPAAM', 'DEFINISI', '0.r', 'Inventori\n(a)Aset Alih yang harga perolehan asalnya kurang daripada RM1,000 (Ringgit Malaysia Satu Ribu) setiap satu dan tidak memerlukan penyelenggaraan berjadual. \n(b)Hamparan, hiasan, langsir dan pinggan mangkuk tanpa mengira harga perolehan asal. '),
(494, 'TPAAM', 'DEFINISI', '0.s', 'Harta Modal dan Inventori mengandungi kategori yang ditetapkan seperti berikut:-\nKategori bagi Harta Modal ialah:-\n(a)Loji/Jentera Berat; \n(b)Kenderaan; \n(c)Perabot ; dan \n(d)Kelengkapan- pejabat/ICT/Telekomunikasi/Penyiaran/ Perubatan / Alam Sekitar/ Makmal / Bengkel / Dapur / Sukan/Keselamatan \n\n\n'),
(495, 'TPAAM', 'DEFINISI', '0.t', 'Kategori bagi Inventori ialah:-\n(a)Nilai perolehan asal kurang daripada RM1,000 dan tidak memerlukan penyelenggaraan berjadual; \n(b)Kelengkapan merangkumi pejabat/ICT/Telekomunikasi /Penyiaran / Perubatan / Alam Sekitar/ Makmal / Bengkel / Dapur / Sukan/Keselamatan; dan \n(c)Tanpa mengira nilai perolehan asal:- \ni.'),
(496, 'TPAAM', 'DEFINISI', '1', 'PERANAN DAN TANGGUNGJAWAB SETIAUSAHA MAIS'),
(497, 'TPAAM', 'DEFINISI', '1', 'Setiausaha MAIS hendaklah memastikan pengurusan aset alih MAIS dilaksanakan secara teratur, cekap dan berkesan dengan melaksanakan perkara-perkara berikut :- '),
(498, 'TPAAM', 'DEFINISI', '1.1', 'Mewujudkan Unit Pengurusan Aset Alih MAIS di Ibu Pejabat; '),
(499, 'TPAAM', 'DEFINISI', '1.2', 'Melantik:-\na.Pegawai Aset di Ibu Pejabat. \nb.Pegawai Aset di luar Ibu Pejabat. \nc.Pegawai Pemeriksaan Aset bagi menjalankan pemeriksaan ke atas aset alih; \nd.Lembaga Pemeriksa Pelupusan bagi melaksanakan pelupusan aset alih; dan\ne.Jawatankuasa Penyiasat bagi menyiasat kes kehilangan aset alih. '),
(500, 'TPAAM', 'DEFINISI', '1.3', 'Menubuhkan Jawatankuasa Pengurusan Aset Alih MAIS (JKPAAM); '),
(501, 'TPAAM', 'DEFINISI', '1.4', 'Mengemukakan laporan tahunan ke Jawatankuasa Baitulmal sebelum 15 Mac tahun berikutnya iaitu:- \na.Laporan Harta Modal dan Inventori; \nb.Laporan Pemeriksaan Harta Modal dan Inventori; \nc.Laporan Pelupusan Aset Alih MAIS; dan \nd.Laporan Tindakan Surcaj/Tatatertib '),
(502, 'TPAAM', 'DEFINISI', '2', 'TUGAS DAN TANGGUNGJAWAB UNIT PENGURUSAN ASET ALIH'),
(503, 'TPAAM', 'DEFINISI', '2.1', 'Penerimaan; '),
(504, 'TPAAM', 'DEFINISI', '2.2', 'Pendaftaran; '),
(505, 'TPAAM', 'DEFINISI', '2.3', 'Penggunaan, Penyimpanan dan Pemeriksaan; '),
(506, 'TPAAM', 'DEFINISI', '2.4', 'Penyelenggaraan; '),
(507, 'TPAAM', 'DEFINISI', '2.5', 'Pelupusan; dan '),
(508, 'TPAAM', 'DEFINISI', '2.6', 'Kehilangan dan Hapuskira '),
(509, 'TPAAM', 'DEFINISI', '3', 'Menguruskan pelantikan Pegawai Pemeriksa Aset Alih, Lembaga Pemeriksa Pelupusan dan Jawatankuasa Penyiasat '),
(510, 'TPAAM', 'DEFINISI', '4', 'Menjadi Urus Setia kepada Mesyuarat Jawatankuasa Pengurusan Aset Alih MAIS (JKPAAM). '),
(511, 'TPAAM', 'DEFINISI', '5', 'Menguruskan pelupusan aset alih MAIS. '),
(512, 'TPAAM', 'DEFINISI', '6', 'Menguruskan kehilangan dan hapus kira '),
(513, 'TPAAM', 'DEFINISI', '7', 'Menyelaras penyediaan laporan berikut:- '),
(514, 'TPAAM', 'DEFINISI', '7.1', 'Harta Modal dan Inventori; '),
(515, 'TPAAM', 'DEFINISI', '7.2', 'Pemeriksaan Harta Modal dan Inventori '),
(516, 'TPAAM', 'DEFINISI', '7.3', 'Pelupusan Aset Alih MAIS ; dan '),
(517, 'TPAAM', 'DEFINISI', '7.4', 'Tindakan Surcaj/Tatatertib. '),
(518, 'TPAAM', 'DEFINISI', '8', 'JAWATANKUASA PENGURUSAN ASET ALIH MAIS (JKPAAM)\nKeanggotaan JKPAAM di MAIS adalah seperti berikut :- \nPengerusi   : Ketua Jabatan\nSetiausaha  : Pegawai Aset Ibu Pejabat\nAhli                : Ketua Audit Dalam\n                        : Sekurang-kurangnya seorang Pegawai dari Sektor atau Bahagian di MAIS \n                        : Pegawai-pegawai lain yang sesuai '),
(519, 'TPAAM', 'DEFINISI', '9', 'Agenda mesyuarat JKPAAM adalah seperti berikut:- '),
(520, 'TPAAM', 'DEFINISI', '9.1', 'Kedudukan Semasa Aset Alih '),
(521, 'TPAAM', 'DEFINISI', '9.2', 'Laporan Hasil Pemeriksaan Aset Alih '),
(522, 'TPAAM', 'DEFINISI', '9.3', ' Laporan Tindakan Pelupusan; '),
(523, 'TPAAM', 'DEFINISI', '9.4', 'Laporan Tindakan Surcaj/Tatatertib '),
(524, 'TPAAM', 'DEFINISI', '9.5', 'Masalah-masalah berkaitan pengurusan aset alih '),
(525, 'TPAAM', 'DEFINISI', '9.6', 'Laporan Prestasi dan Pengurusan Stor '),
(526, 'TPAAM', 'DEFINISI', '10', 'Bidang tugas JKPAAM adalah merancang, memantau, mengawasi dan menyelia semua aspek pengurusan aset alih MAIS. '),
(527, 'TPAAM', 'DEFINISI', '11', 'Mesyuarat JKPAAM hendaklah diadakan sekurang-kurangnya satu(1) kali dalam tempoh tiga (3) bulan dan hasil mesyuarat hendaklah dilaporkan kepada Jawatankuasa Pengurusan Kewangan dan Akaun MAIS(JKPKAM). '),
(528, 'TPAAM', 'DEFINISI', '12', 'Mesyuarat JKPAAM hendaklah diadakan sekurang-kurangnya satu(1) kali dalam tempoh tiga (3) bulan dan hasil mesyuarat hendaklah dilaporkan kepada Jawatankuasa Pengurusan Kewangan dan Akaun MAIS(JKPKAM). '),
(529, 'TPAAM', 'A : Penerimaan', '1', 'TUJUAN\nTatacara Penerimaan aset alih MAIS adalah bertujuan untuk : '),
(530, 'TPAAM', 'A : Penerimaan', '1.1', 'Memastikan setiap aset alih yang diterima menepati spesifikasi yang ditetapkan; '),
(531, 'TPAAM', 'A : Penerimaan', '1.2', 'Menentukan kualiti dan kuantiti sebenar mengikut pesanan; dan '),
(532, 'TPAAM', 'A : Penerimaan', '1.3', 'Memastikan aset alih diterima dalam keadaan yang baik, sempurna dan selamat untuk digunakan. '),
(533, 'TPAAM', 'A : Penerimaan', '2', 'PERATURAN PENERIMAAN\nPeraturan penerimaan aset alih adalah seperti berikut : '),
(534, 'TPAAM', 'A : Penerimaan', '2.1', 'Penerimaan Aset alih perlu terlebih dahulu dibuat pemeriksaan dengan teliti bagi memastikan ia menepati spesifikasi yang ditetapkan; '),
(535, 'TPAAM', 'A : Penerimaan', '2.2', 'Pemeriksaan teknikal oleh pegawai-pegawai MAIS yang bertauliah hendaklah dilakukan apabila perlu mengikut kaedah yang bersesuaian; '),
(536, 'TPAAM', 'A : Penerimaan', '2.3', 'Pematuhan langkah-langkah berikut hendaklah dipenuhi semasa menerima aset: '),
(537, 'TPAAM', 'A : Penerimaan', '2.3(a)', ' Menyemak butiran di dalam dokumen yang sah bagi memastikan aset alih yang diterima adalah betul; '),
(538, 'TPAAM', 'A : Penerimaan', '2.3(b)', 'Memastikan aset alih diperiksa, dikira, diukur, ditimbang atau diuji serta merta sebelum disahkan penerimaannya; '),
(539, 'TPAAM', 'A : Penerimaan', '2.3(c)', 'Memastikan sekiranya pengesahan tidak dapat dilakukan dengan serta merta maka dokumen tersebut hendaklah dicatatkan ??Diterima dengan syarat ianya disahkan kemudian secara diperiksa, dikira, diukur, ditimbang atau diuji?; '),
(540, 'TPAAM', 'A : Penerimaan', '2.3(d)', 'Setelah disemak dan diperiksa, PEGAWAI ASET/PENERIMA adalah bertanggungjawab menyediakan Borang Laporan Penerimaan Aset Alih MAIS KEW.PA-1 jika terdapat kerosakan atau perselisihan; '),
(541, 'TPAAM', 'A : Penerimaan', '2.3(e)', 'Memastikan borang KEW.PA-1 yang telah disahkan dan ditandatangani oleh pegawai yang dilantik oleh Setiausaha hendaklah dihantar dengan serta merta kepada agen penghantaran atau syarikat pembekal; '),
(542, 'TPAAM', 'A : Penerimaan', '2.3(f)', 'Memastikan Pegawai Penerima hendaklah memastikan bahawa aset alih diterima berserta dengan Surat Jaminan daripada pembekal yang mengesahkan bahawa mutunya menepati spesifikasi seperti dalam perjanjian pembelian; dan '),
(543, 'TPAAM', 'A : Penerimaan', '2.3(g)', 'Pegawai Penerima hendaklah memastikan aset alih yang diterima disertakan manual penggunaan dan penyelenggaraan. '),
(544, 'TPAAM', 'B : Pendaftaran', '3', 'TUJUAN\nPendaftaran aset alih adalah bertujuan untuk:- '),
(545, 'TPAAM', 'B : Pendaftaran', '3.1', 'Mewujudkan pangkalan data yang lengkap, tepat dan kemaskini; '),
(546, 'TPAAM', 'B : Pendaftaran', '3.2', 'Memudahkan pengesanan dan pemantauan; '),
(547, 'TPAAM', 'B : Pendaftaran', '3.3', 'Membolehkan keadaan aset diketahui; dan '),
(548, 'TPAAM', 'B : Pendaftaran', '3.4', 'Memudahkan penyelenggaraan, pelupusan dan penggantian aset alih. '),
(549, 'TPAAM', 'B : Pendaftaran', '4', 'TEMPOH PENDAFTARAN\nSetiap aset alih hendaklah didaftarkan dalam tempoh sepuluh hari (10) hari bekerja dari tarikh pengesahan penerimaan.'),
(550, 'TPAAM', 'B : Pendaftaran', '5', 'PENDAFTARAN ASET ALIH SECARA HADIAH\nAset alih yang diterima secara hadiah bagi tujuan pameran dan latihan/pembelajaran tidak perlu didaftarkan. Walau bagaimanapun MAIS hendaklah mewujudkan satu senarai daftar aset berkenaan bagi tujuan rekod.'),
(551, 'TPAAM', 'B : Pendaftaran', '6', 'PENDAFTARAN ASET LUCUT HAK\nMAIS hendaklah mendaftarkan aset alih lucuthak: '),
(552, 'TPAAM', 'B : Pendaftaran', '6.1', 'Aset alih lucuthak hendaklah didaftarkan jika bercadang menggunakannya. '),
(553, 'TPAAM', 'B : Pendaftaran', '6.2', 'Aset alih lucuthak yang telah dikategorikan di bawah akta tertentu adalah menjadi tanggungjawab Setiausaha untuk menentukan tindakan yang diambil ke atas aset alih berkenaan bagi maksud tersebut sahaja. '),
(554, 'TPAAM', 'B : Pendaftaran', '7', 'PUNCA MAKLUMAT\nBagi mendaftar aset alih , dokumen berikut boleh dirujuk:- '),
(555, 'TPAAM', 'B : Pendaftaran', '7.1', 'Kategori Aset alih yang dibeli:- \n(a)Borang Pesanan Kerajaan atau apa-apa borang pesanan yang ditentukan oleh MAIS \n(b)Nota Serahan; \n(c)Invois; \n(d)Dokumen Kontrak; \n(e)Kad Jaminan; \n(f)Manual Pengguna; dan \n(g)Dokumen-dokumen lain yang berkaitan. '),
(556, 'TPAAM', 'B : Pendaftaran', '7.2', 'Kategori Aset alih yang disewa beli:- \n(a)Surat Ikatan Perjanjian; dan\n(b)Dokumen-dokumen lain yang berkaitan. '),
(557, 'TPAAM', 'B : Pendaftaran', '7.3', 'Kategori Aset alih yang diterima daripada sumber lain:- \n(a)Salinan daftar jika aset alih diterima secara pindahan; \n(b)Surat kelulusan menerima hadiah mengikut peraturan yang sedang berkuatkuasa berserta maklumat lengkap aset alih berkaitan; dan \n(c)Salinan Sijil Lucuthak bagi aset alih yang diterima berikutan Perintah Lucuthak oleh Mahkamah. '),
(558, 'TPAAM', 'B : Pendaftaran', '7.4', 'Kategori Aset alih yang belum berdaftar:- \nSekiranya tiada punca maklumat seperti di para 7.1, 7.2 dan 7.3, pendaftaran hendaklah berasaskan maklumat di fizikal aset alih berkenaan atau sumber-sumber lain yang berkaitan.'),
(559, 'TPAAM', 'B : Pendaftaran', '8', 'DOKUMEN PENDAFTARAN\nDokumen Pendaftaran perlu bagi memudahkan urusan merekod dan mengesan sesuatu aset alih dan dokumen yang digunakan adalah seperti berikut:-'),
(560, 'TPAAM', 'B : Pendaftaran', '8.1', 'Daftar Harta Modal KEW.PA-2; dan '),
(561, 'TPAAM', 'B : Pendaftaran', '8.2', 'Daftar Inventori KEW.PA-3. '),
(562, 'TPAAM', 'B : Pendaftaran', '8.3', 'Daftar pendaftaran melalui sistem berkomputer, keperluan perkara- perkara berikut hendaklah dipatuhi:- \n(a)Format yang sama hendaklah diguna pakai;\n(b)Daftar hendaklah dicetak; dan \n(c)Ditandatangan oleh Ketua Jabatan '),
(563, 'TPAAM', 'B : Pendaftaran', '9', 'BUTIRAN MAKLUMAT\nButiran maklumat di dalam KEW.PA-2 dan KEW.PA-3 hendaklah diisi dengan lengkap dan tepat. '),
(564, 'TPAAM', 'B : Pendaftaran', '10', 'PENGESAHAN PENDAFTARAN\nPengesahan pendaftaran hendaklah dibuat dan dicop pada dokumen punca maklumat yang berkaitan seperti contoh berikut :- \n? Telah direkodkan pada ??(tarikh). Nombor siri pendaftaran aset ?. (seperti nombor pada daftar) ?'),
(565, 'TPAAM', 'B : Pendaftaran', '11', 'URUSAN PENDAFTARAN ASET ALIH\nUrusan Pendaftaran Aset Alih adalah melibatkan senarai seperti berikut: '),
(566, 'TPAAM', 'B : Pendaftaran', '11.1', 'Pendaftaran '),
(567, 'TPAAM', 'B : Pendaftaran', '11.1(a)', 'Semua aset alih hendaklah didaftarkan oleh Pegawai Aset atau pegawai di bawahnya dengan menggunakan dokumen KEW.PA-2 dan KEW.PA-3 ; '),
(568, 'TPAAM', 'B : Pendaftaran', '11.1(b)', 'Satu Senarai Daftar Harta Modal dengan menggunakan KEW.PA-4 dan Senarai Daftar Inventori KEW.PA-5 perlu disediakan;dan '),
(569, 'TPAAM', 'B : Pendaftaran', '11.1( c )', 'Salinan KEW.PA-4 dan KEW.PA-5 hendaklah dikemukakan secara tahunan seperti berikut:- \ni.'),
(570, 'TPAAM', 'B : Pendaftaran', '11.1(d)', 'Daftar aset alih hendaklah dikemaskini apabila berlaku perkara-perkara berikut:- \ni.'),
(571, 'TPAAM', 'B : Pendaftaran', '11.2', 'Pelabelan Aset Alih '),
(572, 'TPAAM', 'B : Pendaftaran', '11.2(a)', 'Semua aset alih hendaklah diberi tanda pengenalan dengan cara melabel, mengecat, mengemboss seperti berikut :\n i.'),
(573, 'TPAAM', 'B : Pendaftaran', '11.2(b)', 'Tanda pengenalan tersebut hendaklah diletakkan di tempat yang mudah dilihat dan sesuai pada aset alih berkenaan. Manakala bagi kenderaan khas yang digunakan untuk tujuan penyamaran dan risikan hendaklah ditandakan di tempat yang tidak mudah dilihat; dan '),
(574, 'TPAAM', 'B : Pendaftaran', '11.2( c )', 'Setiap aset alih hendaklah ditanda dengan Nombor Siri Pendaftaran. \nPanduan menyediakan Nombor Siri Pendaftaran adalah mengikut susunan seperti di bawah:-'),
(575, 'TPAAM', 'B : Pendaftaran', '11.3', 'Pergerakan Aset Alih \nPergerakan aset alih bagi tujuan pinjaman atau penempatan sementara hendaklah direkod pada Buku Daftar Pergerakan Harta Modal/Inventori KEW.PA-6. '),
(576, 'TPAAM', 'B : Pendaftaran', '11.4', 'Penyimpanan Daftar \n(a)KEW.PA-2 dan KEW.PA-3 hendaklah disimpan dalam fail kulit keras di dalam kabinet berkunci; \n(b)Buku KEW.PA-6 hendaklah disimpan di dalam Kabinet berkunci; dan\n(c)Daftar asal disimpan oleh Pegawai Aset. '),
(577, 'TPAAM', 'B : Pendaftaran', '11.5', 'Senarai Aset Alih Di Lokasi '),
(578, 'TPAAM', 'B : Pendaftaran', '11.5(a)', 'Senarai Aset alih mengikut lokasi aset alih itu ditempatkan hendaklah disediakan oleh Pegawai Aset menggunakan KEW.PA-7 dalam dua (2) salinan. '),
(579, 'TPAAM', 'B : Pendaftaran', '11.5(b)', 'Satu (1) salinan senarai tersebut hendaklah disimpan oleh Pegawai Aset manakala satu (1) salinan lagi disimpan oleh pegawai yang bertanggungjawab. '),
(580, 'TPAAM', 'B : Pendaftaran', '11.5( c )', 'Senarai tersebut juga hendaklah dikemaskini dari semasa kesemasa apabila terdapat perubahan kuantiti, lokasi atau pegawai bertanggungjawab. '),
(581, 'TPAAM', 'B : Pendaftaran', '12', 'LAPORAN TAHUNAN HARTA MODAL DAN INVENTORI\nLaporan Tahunan Harta Modal dan Inventori KEW.PA-8 hendaklah dikemukakan ke Jawatankuasa Kewangan dan Akaun sebelum 15 Mac tahun berikutnya.'),
(582, 'TPAAM', 'B : Pendaftaran', '', 'JADUAL 1'),
(583, 'TPAAM', 'B : Pendaftaran', '', 'JADUAL 2'),
(584, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13', 'TUJUAN\nSemua aset alih MAIS hendaklah diguna dan dikendalikan dengan cekap, mahir dan teratur bagi tujuan :-'),
(585, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13.1', 'Mengurangkan pembaziran; '),
(586, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13.2', 'Menjimatkan kos; '),
(587, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13.3', 'Mencapai jangka hayat optimum; '),
(588, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13.4', 'Mencegah penyalahgunaan; dan '),
(589, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '13.5', 'Mengelakkan kehilangan '),
(590, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14', 'PENGGUNAAN\nPenggunaan aset alih Majlis hendaklah mematuhi peraturan berikut :- '),
(591, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.1', 'Penggunaan adalah bagi tujuan rasmi sahaja; '),
(592, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.2', 'Mengikut fungsi / tujuan keperluan pembelian aset alih; '),
(593, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.3', 'Dikendalikan oleh pegawai yang mahir dan berkelayakan; '),
(594, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.4', 'Perlu direkodkan; dan '),
(595, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.5', 'Melapor kerosakan dengan menggunakan Borang Aduan Kerosakan Aset KEW.PA-9. '),
(596, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.6', 'Bagi  aset  alih  yang  dibawa  keluar  dari  pejabat  hendaklah mendapat kebenaran bertulis daripada Ketua Bahagian bagi aset alih berkenaan. Aset alih berkenaan hendaklah dipulangkan semula sebaik selesai penggunaannya atau mengikut tempoh kelulusan mana yang lebih awal.'),
(597, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '14.7', 'Peraturan mengenai penggunaan, pengurusan dan penyelenggaraan kenderaan adalah mengikut peraturan semasa yang berkuatkuasa. '),
(598, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '15', 'PENYIMPANAN\nPenyimpanan Aset alih MAIS hendaklah mematuhi peraturan berikut : '),
(599, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '15.1', 'Aset alih hendaklah disimpan di tempat yang selamat dan sentiasa di bawah kawalan pegawai yang bertanggungjawab. Arahan Keselamatan Kerajaan hendaklah sentiasa dipatuhi bagi mengelak berlakunya kerosakan atau kehilangan aset. '),
(600, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '15.2', 'Setiap pegawai adalah bertanggungjawab terhadap apa-apa kekurangan, kerosakan atau kehilangan aset alih di bawah tanggungjawabnya. '),
(601, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '15.3', 'Aset alih yang sangat menarik atau bernilai tinggi akan terdedah kepada risiko kehilangan, maka hendaklah sentiasa di bawah kawalan yang maksima. '),
(602, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '16', 'PEMATUHAN\nPegawai yang gagal mematuhi peraturan di atas, boleh dikenakan tindakan termasuk surcaj di bawah Seksyen 34, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003 dan Tatacara 359 Amalan Terbaik Pengurusan Kewangan MAIS 2011. '),
(603, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17', 'PEMERIKSAAN\nPemeriksaan aset alih hendaklah mematuhi peraturan berikut:- '),
(604, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.1', 'Pemeriksaan aset alih dilakukan ke atas: \n(a)Fizikal; \n(b)Rekod; dan \n(c)Penempatan '),
(605, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.2', 'Tujuan pemeriksaan adalah untuk:- \n(a)Mengetahui keadaan dan prestasinya; \n(b)Memastikan setiap aset alih mempunyai daftar/rekod yang lengkap,tepat dan kemaskini; dan \n(c)Memastikan setiap aset alih berada di lokasi sama seperti yang tercatat dalam daftar.'),
(606, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.3', 'Setiausaha hendaklah melantik sekurang-kurangnya dua (2) orang Pegawai Pemeriksa yang tidak terlibat secara langsung dalam pengurusan aset alih untuk membuat pemeriksaan ke atas harta modal dan inventori. Pemeriksaan hendaklah dilaksanakan sekurang-kurangnya satu (1) kali setahun. '),
(607, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.4', 'Pegawai Pemeriksa hendaklah mengemukakan Laporan Pemeriksaan Harta Modal KEW.PA-10 dan Laporan Pemeriksaan Inventori KEW.PA-11 kepada Ketua Jabatan. '),
(608, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.5', 'Ketua Jabatan hendaklah mengemukakan KEW.PA-10 dan KEW.PA-11 bersama-sama dengan Sijil Tahunan Pemeriksaan Harta Modal dan Inventori KEW.PA-12 kepada Setiausaha MAIS. '),
(609, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.6', 'KEW.PA-12 hendaklah dikemukakan kepada Jawatankuasa Kewangan Dan Akaun sebelum 15 Mac tahun berikutnya. '),
(610, 'TPAAM', 'C : Penggunaan, Penyimpanan Dan Pemeriksaan', '17.7', 'Ketua Jabatan hendaklah melakukan sendiri pemeriksaan mengejut dari semasa ke semasa bagi memastikan peraturan ini dipatuhi. '),
(611, 'TPAAM', 'D : Penyelenggaraan', '18', 'TUJUAN\nPenyelenggaraan aset alih MAIS bertujuan:- '),
(612, 'TPAAM', 'D : Penyelenggaraan', '18.1', 'Memastikan aset alih berfungsi dengan lancar secara berterusan dan selamat digunakan; '),
(613, 'TPAAM', 'D : Penyelenggaraan', '18.2', 'Memelihara dan memanjangkan jangka hayat; '),
(614, 'TPAAM', 'D : Penyelenggaraan', '18.3', 'Meningkatkan keupayaan; '),
(615, 'TPAAM', 'D : Penyelenggaraan', '18.4', 'Mengurangkan kerosakan; '),
(616, 'TPAAM', 'D : Penyelenggaraan', '18.5', 'Menjimatkan perbelanjaan ; '),
(617, 'TPAAM', 'D : Penyelenggaraan', '18.6', 'Menjamin keselamatan pengguna; '),
(618, 'TPAAM', 'D : Penyelenggaraan', '18.7', 'Mewujudkan suasana kerja yang kondusif serta produktif; dan '),
(619, 'TPAAM', 'D : Penyelenggaraan', '18.8', 'Memelihara imej . '),
(620, 'TPAAM', 'D : Penyelenggaraan', '19', 'JENIS PENYELENGGARAAN\nPenyelenggaraan terbahagi kepada dua (2) jenis seperti berikut:- '),
(621, 'TPAAM', 'D : Penyelenggaraan', '19.1', 'Penyelenggaraan Pencegahan (Preventive Maintenance) ialah tindakan penyelenggaraan berjadual yang diambil untuk memeriksa, menservis, membaiki atau mengganti komponen atau sumber fizikal secara teratur supaya dapat berfungsi dengan lancar secara berterusan; dan '),
(622, 'TPAAM', 'D : Penyelenggaraan', '19.2', 'Penyelenggaraan Pemulihan (Corrective Maintenance) ialah tindakan membaiki atau mengganti komponen atau sumber fizikal supaya dapat berfungsi mengikut standard yang ditetapkan. '),
(623, 'TPAAM', 'D : Penyelenggaraan', '20', 'MANUAL PENYELENGGARAAN\nSetiap manual/buku panduan pengguna/peraturan penggunaan dan penyelenggaraan jika ada disimpan sebagai rujukan.'),
(624, 'TPAAM', 'D : Penyelenggaraan', '21', 'PANDUAN PENYELENGGARAAN\nPegawai Aset adalah bertanggungjawab untuk melaksanakan langkah langkah berikut:-'),
(625, 'TPAAM', 'D : Penyelenggaraan', '21.1', 'Menyediakan Senarai Aset Alih Yang Memerlukan Penyelenggaraan\nAset alih yang memerlukan penyelenggaraan perlu dikenal pasti dan disenaraikan mengikut KEW.PA-13.  '),
(626, 'TPAAM', 'D : Penyelenggaraan', '22.2', 'Merancang Penyelenggaraan \nSetelah aset alih yang memerlukan penyelenggaraan disenaraikan, satu program penyelenggaraan hendaklah dirancang dan disediakan.'),
(627, 'TPAAM', 'D : Penyelenggaraan', '22.3', 'Melaksanakan Program Penyelenggaraan \nPenyelenggaraan hendaklah dilaksanakan mengikut jadual yang telah ditetapkan melainkan sesuatu aset alih itu memerlukan penyelenggaraan segera.'),
(628, 'TPAAM', 'D : Penyelenggaraan', '22.4', 'Merekodkan Penyelenggaraan \nSetiap penyelenggaraan yang dilaksanakan hendaklah direkodkan dalam Daftar Penyelenggaraan Harta Modal KEW.PA-14. '),
(629, 'TPAAM', 'D : Penyelenggaraan', '22.5', 'Menilai Program Penyelenggaraan \nPenilaian hendaklah dibuat ke atas program penyelenggaraan bagi tujuan berikut:-'),
(630, 'TPAAM', 'D : Penyelenggaraan', '22.5(a)', 'Menentukan matlamat program penyelenggaraan tercapai; '),
(631, 'TPAAM', 'D : Penyelenggaraan', '22.5(b)', 'Memastikan penyelenggaraan yang dijalankan berkualiti, bersesuaian dan munasabah dari segi kos dan masa; '),
(632, 'TPAAM', 'D : Penyelenggaraan', '22.5(c)', 'Mengenal pasti penyelenggaraan yang seterusnya; dan '),
(633, 'TPAAM', 'D : Penyelenggaraan', '22.5(d)', 'Membantu pihak pengurusan membuat keputusan tindakan yang sesuai ke atas aset alih berkenaan. '),
(634, 'TPAAM', 'D : Penyelenggaraan', '22.6', 'Menyelia dan memantau penyelenggaraan oleh pihak swasta kerja-kerja penyelenggaraan yang dibuat oleh pihak swasta hendaklah diselia dan dipantau bagi memastikan syarat-syarat yang ditetapkan dalam perjanjian dipatuhi. '),
(635, 'TPAAM', 'E : Pelupusan', '23', 'TUJUAN\nPelupusan aset alih MAIS adalah bertujuan untuk:- \n'),
(636, 'TPAAM', 'E : Pelupusan', '23.1', 'Memastikan MAIS tidak menyimpan aset alih yang tidak boleh diguna atau tidak diperlukan; '),
(637, 'TPAAM', 'E : Pelupusan', '23.2', 'Menjimatkan ruang simpanan/pejabat;dan '),
(638, 'TPAAM', 'E : Pelupusan', '23.3', 'Mendapatkan hasil pulangan yang terbaik; '),
(639, 'TPAAM', 'E : Pelupusan', '24', 'JUSTIFIKASI PELUPUSAN\nAset alih boleh dilupus berdasarkan justifikasi seperti berikut:- '),
(640, 'TPAAM', 'E : Pelupusan', '24.1', 'Aset alih yang tidak ekonomi untuk dibaiki; '),
(641, 'TPAAM', 'E : Pelupusan', '24.2', 'Usang/obselete; '),
(642, 'TPAAM', 'E : Pelupusan', '24.3', 'Rosak dan tidak boleh digunakan; '),
(643, 'TPAAM', 'E : Pelupusan', '24.4', 'Luput dalam tempoh penggunaan; '),
(644, 'TPAAM', 'E : Pelupusan', '24.5', 'Keadaan keupayaan aset alih tidak lagi di peringkat optimum; '),
(645, 'TPAAM', 'E : Pelupusan', '24.6', 'Aset alih tiada alat ganti; '),
(646, 'TPAAM', 'E : Pelupusan', '24.7', 'Pembekal tidak lagi memberi khidmat sokongan; '),
(647, 'TPAAM', 'E : Pelupusan', '24.8', 'Disyor selepas pemeriksaan aset; '),
(648, 'TPAAM', 'E : Pelupusan', '24.9', 'Aset Alih tidak lagi diperlukan; '),
(649, 'TPAAM', 'E : Pelupusan', '24.1', 'Aset alih mengalami perubahan teknologi; dan '),
(650, 'TPAAM', 'E : Pelupusan', '24.11', 'Melebihi keperluan '),
(651, 'TPAAM', 'E : Pelupusan', '25', 'KUASA MELULUS\nKuasa untuk meluluskan pelupusan adalah seperti berikut:- '),
(652, 'TPAAM', 'E : Pelupusan', '25.1', 'Jawatankuasa Baitulmal, dengan; \nNilai perolehan asal satu aset alih melebihi RM50,000 atau jumlah keseluruhannya melebihi RM500,000.\n'),
(653, 'TPAAM', 'E : Pelupusan', '25.2', 'Setiausaha MAIS, dengan; \nNilai perolehan asal satu aset alih tidak melebihi RM50,000 atau jumlah keseluruhannya tidak melebihi RM500,000;\n'),
(654, 'TPAAM', 'E : Pelupusan', '25.3', 'Peraturan dimana pegawai-pegawai yang diwakilkan kuasa di bawah seksyen 362 Amalan Terbaik Pengurusan Kewangan MAIS 2011 tidak boleh mewakilkan kuasa selanjutnya kepada mana-mana pegawai lain. '),
(655, 'TPAAM', 'E : Pelupusan', '26', 'URUS SETIA PELUPUSAN\nUnit Pengurusan Aset MAIS adalah bertanggungjawab sebagai Urus Setia Pelupusan. '),
(656, 'TPAAM', 'E : Pelupusan', '26.1', 'Tugas-tugas Urus Setia Pelupusan adalah seperti berikut:- '),
(657, 'TPAAM', 'E : Pelupusan', '26.1(a)', 'Mengurus pelantikan Ahli Lembaga Pemeriksa mengikut KEW.PA-15; '),
(658, 'TPAAM', 'E : Pelupusan', '26.1(b)', 'Mendapatkan Perakuan Pelupusan (PEP) KEW.PA-16; '),
(659, 'TPAAM', 'E : Pelupusan', '26.1(c)', 'Mendapatkan Laporan Lembaga Pemeriksa KEW.PA-17; '),
(660, 'TPAAM', 'E : Pelupusan', '26.1(d)', 'Menyemak dan memastikan permohonan pelupusan lengkapdan teratur; '),
(661, 'TPAAM', 'E : Pelupusan', '26.1(e)', 'Mengemukakan permohonan pelupusan kepada Kuasa Melulus; '),
(662, 'TPAAM', 'E : Pelupusan', '26.1(f)', 'Memaklumkan kelulusan kepada Bahagian/Unit untuk tindakan; '),
(663, 'TPAAM', 'E : Pelupusan', '26.1(g)', 'Memohon pelanjutan tempoh pelupusan; '),
(664, 'TPAAM', 'E : Pelupusan', '26.1(h)', 'Memohon kelulusan meminda kaedah pelupusan; '),
(665, 'TPAAM', 'E : Pelupusan', '26.1(i)', 'Melantik dua(2) orang saksi bagi pelupusan melalui kaedah pemusnahan; '),
(666, 'TPAAM', 'E : Pelupusan', '26.1(j)', 'Mendapatkan Sijil Penyaksian Pemusnahan Aset KEW.PA-18; '),
(667, 'TPAAM', 'E : Pelupusan', '26.1(k)', 'Memperolehi Sijil Pelupusan Aset KEW.PA-19 bagi semua pelupusan yang telah dilaksanakan; '),
(668, 'TPAAM', 'E : Pelupusan', '26.1(l)', 'Menghantar KEW.PA-19 kepada Kuasa Melulus; dan '),
(669, 'TPAAM', 'E : Pelupusan', '26.1(m)', 'Mengemukakan Laporan Tahunan Pelupusan Aset KEW.PA-20 ke Jawatankuasa Kewangan dan Akaun sebelum 15 Mac tahun berikutnya. '),
(670, 'TPAAM', 'E : Pelupusan', '27', 'PERAKUAN PELUPUSAN (PEP) KEW.PA-16\nPerakuan pelupusan (PEP) diperlukan dalam melaksanakan pelupusan aset alih MAIS; '),
(671, 'TPAAM', 'E : Pelupusan', '27.1', 'PEP diperlukan bagi semua aset Mekanikal, Teknikal, Elektrikal dan Elektronik seperti di KEMBARAN A dan perkakasan komputer seperti di KEMBARAN B. '),
(672, 'TPAAM', 'E : Pelupusan', '27.2', 'MAIS hendaklah merujuk kepada Jabatan Teknikal atau khidmat pakar-pakar khusus untuk pelupusan kenderaan bagi mendapatkan PEP. '),
(673, 'TPAAM', 'E : Pelupusan', '27.3', 'PEP bagi aset alih hendaklah disediakan oleh MAIS/Jabatan yang lain. '),
(674, 'TPAAM', 'E : Pelupusan', '27.4', 'Setiap PEP hendaklah disahkan oleh dua (2) pegawai yang mempunyai pengalaman dan tidak terlibat dengan pengurusan aset alih berkenaan. '),
(675, 'TPAAM', 'E : Pelupusan', '27.5', 'Nilai semasa aset alih yang dinilai dalam PEP hanya sah digunakan untuk tempoh satu (1) tahun sahaja. Sekiranya melebihi tempoh tersebut, MAIS perlu mendapatkan pengesahan nilai semasa yang baru di PEP. '),
(676, 'TPAAM', 'E : Pelupusan', '28', 'LEMBAGA PEMERIKSA\nPelantikan dan tugas-tugas lembaga pemeriksa dibuat berdasarkan keperluan seperti:- \n'),
(677, 'TPAAM', 'E : Pelupusan', '28.1', 'Pelantikan Lembaga Pemeriksa '),
(678, 'TPAAM', 'E : Pelupusan', '28.1(a)', 'Setiausaha MAIS perlu melantik Lembaga Pemeriksa bagi membuat pemeriksaan ke atas aset alih yang hendak dilupuskan. '),
(679, 'TPAAM', 'E : Pelupusan', '28.1(b)', 'Lembaga Pemeriksa yang dilantik berdasarkan jawatan dan tempoh pelantikan tidak melebihi dua (2) tahun. Keanggotaan Lembaga Pemeriksa hendaklah terdiri daripada:- '),
(680, 'TPAAM', 'E : Pelupusan', '28.1(b)i', 'Sekurang-kurangnya dua (2) orang pegawai (seorang pengerusi dan seorang ahli) yang tidak terlibat secara langsung dalam pengendalian aset alih berkenaan; '),
(681, 'TPAAM', 'E : Pelupusan', '28.1(b)ii', 'Pegawai mempunyai kepakaran memeriksa aset alih, Jika perlu; '),
(682, 'TPAAM', 'E : Pelupusan', '28.1(b)iii', 'Pegawai Kumpulan Pengurusan dan kumpulan Eksekutif atau yang setaraf. '),
(683, 'TPAAM', 'E : Pelupusan', '28.2', 'Tugas-tugas Lembaga Pemeriksa adalah seperti berikut:- '),
(684, 'TPAAM', 'E : Pelupusan', '28.2(a)', 'Menyedia dan mengemukakan jadual pemeriksaan kepada Urus Setia Pelupusan ; '),
(685, 'TPAAM', 'E : Pelupusan', '28.2(b)', 'Memeriksa aset alih dan rekod yang berkaitan dalam tempoh satu(1) bulan dari tarikh diarahkan; '),
(686, 'TPAAM', 'E : Pelupusan', '28.2(c)', 'Memastikan segala maklumat sulit dan kerahsiaan dalam peralatan yang hendak dilupuskan telah dikeluarkan; '),
(687, 'TPAAM', 'E : Pelupusan', '28.2(d)', 'Menyediakan Laporan Lembaga Pemeriksa KEW.PA-17; '),
(688, 'TPAAM', 'E : Pelupusan', '28.2(e)', 'Mengesyorkan kaedah pelupusan yang sesuai; dan '),
(689, 'TPAAM', 'E : Pelupusan', '28.2(f)', 'Menandatangani KEW.PA-17 dan mengemukakan kepada Urus Setia Pelupusan. '),
(690, 'TPAAM', 'E : Pelupusan', '29', 'TEMPOH TINDAKAN PELUPUSAN\nTindakan pelupusan mengikut kaedah yang diluluskan hendaklah dilaksanakan dalam tempoh empat (3) bulan dari tarikh surat kelulusan.'),
(691, 'refCode', 'chapter', 'refNo', 'detail'),
(1310, 'ATPK', 'A', '1', 'Tajuk Ringkas Dan Pemakaian'),
(1311, 'ATPK', 'A', '1.1', 'Peraturan ini hendaklah dinamakan Amalan Terbaik Pengurusan Kewangan Majlis Agama Islam Selangor.'),
(1312, 'ATPK', 'A', '2', 'Tafsiran'),
(1313, 'ATPK', 'A', '2.1', 'Berikut adalah senarai tafsiran istilah yang digunakan dalam Peraturan Kewangan ini.'),
(1314, 'ATPK', 'A', '2.2', '<b>Ahli Majlis</b> ertinya ahli-ahli yang dilantik mengikut Seksyen 11, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1315, 'ATPK', 'A', '2.3', '<b>Akauntan </b>ertinya pegawai kewangan utama yang bertanggungjawab atas urusan kewangan dan perakaunan Majlis tanpa mengira gelaran jawatannya.'),
(1316, 'ATPK', 'A', '2.4', '<b>Baitulmal </b>ertinya kumpulan wang yang ditubuhkan di bawah Seksyen 81, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1317, 'ATPK', 'A', '2.5', '<b>Enakmen </b>ertinya Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1318, 'ATPK', 'A', '2.6', '<b>Hukum Syarak</b> ertinya seperti yang dinyatakan pada tafsiran di dalam Seksyen 2,   Enakmen Pentadbiran Agama Islam 2003.'),
(1319, 'ATPK', 'A', '2.7', '<b>Majlis </b>ertinya Majlis Agama Islam Selangor yang ditubuhkan di bawah subseksyen 4 (1) mengikut Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1320, 'ATPK', 'A', '2.8', '<b>Ketua Bahagian</b> ertinya Pegawai yang mengetuai bahagian-bahagian di dalam struktur organisasi Majlis dan bertanggungjawab terus kepada Pegawai Pengawal/ Pegawai Atasannya.'),
(1321, 'ATPK', 'A', '2.9', '<b>Ketua Unit </b>ertinya Pegawai yang mengetuai unit-unit di struktur organisasi Majlis dan bertanggungjawab terus kepada Ketua Bahagian.'),
(1322, 'ATPK', 'A', '2.1', '<b>Mufti </b>ertinya seperti yang dinyatakan pada tafsiran di dalam Seksyen 2, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1323, 'ATPK', 'A', '2.11', '<b>Negeri </b>adalah negeri-negeri di Malaysia termasuklah Wilayah-wilayah Persekutuan Kuala Lumpur, Labuan dan Putrajaya.'),
(1324, 'ATPK', 'A', '2.12', '<b>Pegawai Majlis</b> ertinya semua kategori pegawai yang sedang berkhidmat dalam perkhidmatan Majlis yang dilantik oleh Jawatankuasa secara tetap, sementara atau kontrak.'),
(1325, 'ATPK', 'A', '2.13', '<b>Pemungut </b>ertinya Pegawai Majlis atau mana-mana orang yang oleh kerana jawatannya atau di bawah mana-mana undang-undang bertulis adalah bertanggungjawab bagi memungut wang dari orang awam dan Pegawai Majlis.'),
(1326, 'ATPK', 'A', '2.14', '<b>Pegawai Pembayar</b> ertinya seorang pegawai yang sebenarnya membelanjakan Wang Awam.'),
(1327, 'ATPK', 'A', '2.15', '<b>Pegawai Pengawal </b>ertinya Setiausaha Majlis yang tersebut dalam Seksyen 16, Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003.'),
(1328, 'ATPK', 'A', '2.16', '<b>Tahun Kewangan </b>ertinya jangka masa tidak melebihi 12 bulan berturut-turut dan berakhir pada 31hb Disember bagi tiap-tiap tahun.'),
(1329, 'ATPK', 'A', '2.17', '<b>Wang Awam</b> &nbsp;ertinya mempunyai pengertian yang diberi kepadanya di bawah Seksyen 3, Akta Prosedur Kewangan 1957 (Akta 61) dan termasuklah wang tunai dan kesetaraan tunai serta semua instrumen kewangan dan termasuklah Zakat, Fitrah, Baitulmal atau hasil-hasil lain yang disyariatkan.'),
(1330, 'ATPK', 'A', '2.18', '<b>Kehilangan </b>adalah termasuk kehilangan wang, setem, barang-barang dan harta benda lain yang berharga disebabkan oleh kecurian, penipuan atau kecuaian pegawai, kekurangan, bayaran terlebih, pembayaran tanpa kuasa, kehilangan hasil, hutang yang tidak boleh diperoleh semula dan perbelanjaan tidak berfaedah.'),
(1331, 'ATPK', 'A', '2.19', '<b>Wakaf </b>ertinya menyerahkan apa-apa harta yang boleh dinikmati manfaat atau faedahnya untuk apa-apa tujuan kebajikan sama ada sebagai wakaf am atau khas menurut hukum syarak tetapi tidak termasuk Amanah sebagaimana disebutkan dalam Enakmen Wakaf Negeri Selangor.'),
(1332, 'ATPK', 'A', '2.2', '<b>Wakaf Khas </b>ertinya sesuatu wakaf yang diwujudkan bagi tujuan kebajikan yang khusus menurut hukum syarak.'),
(1333, 'ATPK', 'A', '2.21', '<b>Saham Wakaf</b> ertinya pewujudan suatu wakaf melalui saham-saham yang ditawarkan dan kemudiannya diwakafkan oleh pembeli kepada Majlis.'),
(1334, 'ATPK', 'A', '2.22', '<b>Marad al-maut e</b>rtinya penyakit membawa maut yang memenuhi syarat-syarat yang berikut;<div>(a) &nbsp; &nbsp; Penyakit itu mestilah menyebabkan kematian simati.</div><div>(b) &nbsp; &nbsp; Penyakit itu mestilah menyebabkan kebimbangan kematian dalam minda simati;dan</div><div>(c) &nbsp; &nbsp; &nbsp;Mestilah terdapat beberapa tanda luaran suatu penyakit yang serius.</div>'),
(1335, 'ATPK', 'A', '2.26', '<b>Istibdal </b>ertinya menggantikan suatu harta wakaf dengan harta lain atau wang yang sama atau lebih tinggi nilainya sama ada melalui gantian, belian, jualan atau apa-apa cara lain menurut hukum syarak.'),
(1336, 'ATPK', 'B', '4', 'Pegawai Pengawal bertanggungjawab memeriksa butir-butir Anggaran Pendapatan dan Perbelanjaan dan membuat apa-apa pengubahsuaian yang perlu.'),
(1337, 'ATPK', 'B', '5', 'Anggaran Pendapatan dan Perbelanjaan itu hendaklah disertakan dengan huraian-huraian mengenai setiap butiran yang bersifat luar biasa dan apa-apa perbezaan yang besar di bawah setiap satu butiran di antara perbelanjaan yang dicadangkan atau hasil yang dijangka dengan anggaran-anggaran yang diluluskan bagi tahun sebelumnya.'),
(1338, 'ATPK', 'B', '6', 'Format Anggaran Pendapatan dan Perbelanjaan\r\nAnggaran Pendapatan dan Perbelanjaan hendaklah menunjukkan seberapa tepat yang boleh jumlah wang yang dijangka akan dibelanjakan atau dipungut pada tahun itu dengan menggunakan format-format yang ditetapkan.'),
(1339, 'ATPK', 'B', '6a', 'Maksud Perbelanjaan\r\nSetiap maksud perbelanjaan hendaklah dipecahkan mengikut kod perbelanjaan.'),
(1340, 'ATPK', 'B', '7', 'Kelulusan Anggaran\r\nPegawai Pengawal atau pegawai yang diberi tanggungjawab olehnya hendaklah sebelum bermulanya setiap tahun kewangan, membentangkan kepada Mesyuarat Jawatankuasa Baitulmal Anggaran Pendapatan dan Perbelanjaan bagi tahun yang berikutnya. Anggaran tersebut hendaklah dikemukakan untuk pengesahan mesyuarat Majlis seterusnya dimaklumkan di dalam Majlis Mesyuarat Kerajaan Negeri (MMKN) dan disembahkan kepada DYMM Sultan tidak lewat dari 31hb Oktober dalam setiap tahun dan diwartakan.'),
(1341, 'ATPK', 'B', '8', 'Anggaran Tambahan\r\nSetiap Anggaran Pendapatan dan Perbelanjaan tambahan yang diperlukan pada tahun semasa, bolehlah dipersembahkan kepada DYMM Sultan pada bila-bila masa sebelum 31hb Mac dalam tahun berikutnya, berkenaan tahun sebelumnya. Anggaran disediakan untuk dibentang dalam mesyuarat Baitulmal dan disahkan dalam mesyuarat Majlis seterusnya dimaklumkan di dalam Majlis Mesyuarat Kerajaan Negeri (MMKN) serta dikemukakan untuk kelulusan DYMM Sultan dan diwartakan.'),
(1342, 'ATPK', 'B', '9', 'Pindah Peruntukan\r\nMajlis boleh pindah peruntukan daripada lebihan mana-mana butiran kepada butiran yang lain di dalam kumpulan wang yang sama dengan syarat pindah peruntukan tersebut diluluskan oleh Pegawai Pengawal.'),
(1343, 'ATPK', 'C', '10 (a)', 'Tanggungjawab Pegawai Pengawal \r\nPegawai Pengawal adalah bertanggungjawab melaksanakan kewajipan kewangan pejabat masing-masing dengan sempurna. Tanggungjawab tersebut adalah bagi memungut dengan sewajarnya segala wang awam yang patut diterima oleh atau di bawah arahan mereka, menyimpannya dengan selamat serta juga bertanggungjawab sekiranya akaun yang dikemukakan oleh mereka atau di bawah kuasa mereka didapati tidak betul.'),
(1344, 'ATPK', 'C', '10 (b)', 'Sebarang perwakilan kewajipan ini kepada pegawai lain hendaklah dibuat dengan arahan bertulis. Bagaimanapun, seorang pegawai itu secara amnya tidak terlepas dari tanggungjawabnya sendiri setelah perwakilan dibuat.'),
(1345, 'ATPK', 'C', '11', 'Pegawai Pengawal Dikehendaki Mengawal Perbelanjaan\r\nSeseorang Pegawai Pengawal adalah dikehendaki mengawal perbelanjaan berkenaan dengan sesuatu perkhidmatan yang diperuntukan dalam maksud Perbelanjaan Anggaran Pendapatan dan Perbelanjaan di bawah kawalannya supaya peruntukan tidak terlebih dibelanjakan. Bagaimanapun jika perbelanjaan bagi sesuatu program dan aktiviti atau projek telah dilebihi, suatu laporan lengkap mengenainya hendaklah dibuat oleh Akauntan Majlis.'),
(1346, 'ATPK', 'C', '12', 'Perbelanjaan Darurat\r\nDalam sesuatu keadaan yang luar biasa jika ternyata bagi kepentingan perkhidmatan bahawa perbelanjaan patut dilakukan dengan segera dan masa tidak mengizinkan bagi mendapat kebenaran mengikut cara biasa, seseorang pegawai boleh atas tanggungjawab sendiri melakukan perbelanjaan itu, tetapi ia hendaklah serta merta mendapatkan kelulusan Pegawai Pegawal dan melaporkan kepada Jawatankuasa Baitulmal selepas perbelanjaan dibuat. Dalam tiap-tiap hal seumpama ini, apa-apa pembayaran yang dibuat boleh pada mulanya dipertanggungkan kepada Pendahuluan Diri. Kelulusan Jawatankuasa Baitulmal hendaklah diperolehi terlebih dahulu sebelum pelarasan pendahuluan diri tersebut dilakukan.'),
(1347, 'ATPK', 'C', '13', 'Akaun Kena Bayar\r\nBaki peruntukan yang diberikan kepada sesuatu butiran bagi sesuatu tahun kewangan yang tidak dibelanjakan di penghujung tahun kewangan itu adalah luput. Pada bulan Januari tahun kewangan yang baru pembayaran boleh dibuat di bawah butiran yang berkenaan, untuk menjelaskan tanggungan-tanggungan bagi kerja yang telah diberi sehingga 31hb Disember tahun kewangan itu.'),
(1348, 'ATPK', 'C', '14', 'Tarikh bayaran adalah tarikh perbelanjaan direkodkan dalam akaun. Bagi pembayaran yang dibuat pada bulan Januari tahun kewangan yang baru yang ditanggungkan kepada akaun tahun kewangan yang lama mengikut Tatacara 13 di atas, tarikh perbelanjaan diakaunkan adalah 31hb Disember tahun kewangan lama.'),
(1349, 'ATPK', 'C', '15 (a)', 'Perbelanjaan yang dipertanggungkan dengan sepatutnya kepada akaun bagi sesuatu tahun yang tertentu mestilah dibayar dengan segera sebelum penghujung bulan Januari tahun kewangan yang berikutnya supaya perbelanjaan tersebut tidak dibayar dari peruntukan tahun kewangan semasa.'),
(1350, 'ATPK', 'C', '15 (b)', 'Sebarang baki peruntukan di bawah sesuatu butiran atau program dan aktiviti atau projek yang belum dibelanjakan tidak boleh digunakan pada akhir tahun atau bila-bila masa bagi menjelaskan bayaran yang belum tiba masanya.'),
(1351, 'ATPK', 'C', '15 (c)', 'Baki tersebut tidak boleh juga digunakan atau dipindahkan ke akaun deposit sebagai simpanan.'),
(1352, 'ATPK', 'C', '16 (a)', 'Pengawasan Atas Perbelanjaan\r\nSemua kerja, pembelian dan aktiviti-aktiviti lain yang melibatkan perbelanjaan yang diluluskan hendaklah dijalankan dengan segera dengan seberapa yang boleh dalam tempoh yang dianggarkan baginya, tetapi tiada apa-apa tambahan harga boleh dilakukan semata-mata kerana hendak menyempurnakannya pada atau sebelum akhir tahun kewangan.'),
(1353, 'ATPK', 'C', '16 (b)', 'Pegawai Pengawal bertanggungjawab menentukan pengawasan yang sepatutnya dijalankan atas perbelanjaan di bawah kawalannya. Jika ada kemungkinan sesuatu perbelanjaan yang tidak berulang (one off) dan tidak boleh disiapkan secara ekonomik dalam tahun kewangan semasa, kelulusan daripada Jawatankuasa Berkaitan diperlukan untuk membayar perbelanjaan itu dalam tahun yang berikut.'),
(1354, 'ATPK', 'C', '16a', 'Pengawasan Atas Perbelanjaan\r\nKebenaran Khas dari Pegawai Pengawal adalah dikehendaki bagi membayar baucar yang dikemukakan selepas bulan pertama tahun kewangan semasa, berkenaan dengan tanggungan-tanggungan yang kena dibayar pada atau sebelum hujung tahun kewangan yang lepas. Permohonan bagi kebenaran khas hendaklah disampaikan kepada Pegawai Pengawal bersama dengan baucar penghuraian penuh mengenai sebab-sebab berlaku kelewatan.'),
(1355, 'ATPK', 'C', '16b', 'Tanggungjawab Akauntan\r\nSemua Akauntan adalah bertanggungjawab bahawa tiada pembayaran / pelarasan dibuat dalam tahun kewangan yang baru berkenaan dengan pertanggungan yang kena dibayar pada atau sebelum penghujung tahun kewangan yang lama, tanpa kelulusan khas yang dikehendaki.'),
(1356, 'ATPK', 'C', '17 (a)', 'Pembayaran Bagi Barang Dan Perkhidmatan Yang Dibekal Dengan Suci Hati Tidak Boleh Ditahan\r\nWalau apapun peruntukan mana-mana Tatacara yang lain, pembayaran sesuatu bil yang dikemukakan dengan sempurnanya oleh seseorang bagi barang atau perkhidmatan yang dipesan dan dibekalkan dengan suci hati kepada Majlis tidak boleh ditahan  di atas sebab-sebab keraguan sama ada tanggungan itu telah dilakukan dengan sepatutnya oleh Pegawai Majlis bertanggungjawab.'),
(1357, 'ATPK', 'C', '17 (b)', 'Jika Akauntan Majlis berpuashati bahawa suatu tanggungan kontrak telah dilakukan, tetapi berpendapat, seseorang pegawai mungkin telah bersalah pada melakukannya, bil itu hendaklah dibayar dengan segera dan dipertanggungkan pada mula kepada Akaun Pendahuluan Diri atas nama pegawai yang berkenaan, atau jika nama pegawai yang berkenaan itu tidak ditentukan dengan serta merta, atas nama Pegawai Pengawal sementara menanti keputusan penyelesaian perkara tersebut.'),
(1358, 'ATPK', 'C', '18 (a)', 'Resit Rasmi\r\nResit Rasmi secara manual atau elektronik mestilah dikeluarkan sebagai akuan terima wang yang dibayar kepada seseorang Pegawai Majlis');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(1359, 'ATPK', 'C', '18 (b)', 'Arahan Kewangan Bil.1/2012 Bertasrikh 17 April 2012 (Pindaan) \r\nResit Rasmi mestilah dikeluarkan pada masa urusan dibuat dan jika seseorang pembayar hadir sendiri, Resit Rasmi itu mestilah diserahkan kepadanya. Apabila Wang Awam diterima melalui pos atau penghantar, Resit Rasmi baginya mestilah disediakan pada hari itu juga dan dihantar dalam tempoh 3 hari bekerja. Ia tertakluk kepada maklumat yang diterima adalah lengkap dan boleh dihubungi.'),
(1360, 'ATPK', 'C', '18 (c)', 'Resit rasmi tidak akan dikeluarkan bagi pembayaran yang dibuat oleh pegawai awam dengan cara potongan pada baucar melainkan jika sistem perakaunan yang digunakan mengkehendakinya.'),
(1361, 'ATPK', 'C', '19 (a)', 'Memberitahu Orang Awam\r\nPegawai Pengawal dan Pemungut Wang Awam hendaklah menentukan supaya orang awam diberitahu dengan sepenuhnya bahawa mereka hendaklah:"\r\n(i)       Mendapatkan Resit Rasmi bagi semua wang yang dibayar.\r\n(ii)Hanya menerima Resit Rasmi yang bercetak dan bernombor atau Resit Rasmi yang dikeluarkan oleh mesin daftar wang, jika mesin daftar wang digunakan, atau resit rasmi yang dijana secara elektronik.\r\n(iii)Bagi pembayaran melalui cek, Resit Rasmi hanya sah apabila cek ditunaikan oleh Bank.\r\n'),
(1362, 'ATPK', 'C', '23 (a)(i)', 'Pemberitahuan bagi maksud ini hendaklah dibuat dalam bahasa yang berkenaan dan dipamerkan di tempat yang mudah dilihat oleh orang awam.'),
(1363, 'ATPK', 'C', '19 (b)', 'Dalam persekitaran elektronik orang awam hendaklah diberi pilihan untuk mencetak Resit Rasmi atau mendapatkan maklumat Resit Rasmi melalui media elektronik. Pemberitahuan kepada orang awam bagi maksud ini dalam bahasa yang berkenaan hendaklah disediakan.'),
(1364, 'ATPK', 'C', '20 (a)', 'Pindaan Dan Pembatalan Resit Rasmi\r\nPindaan sesuatu Resit Rasmi adalah dilarang. Jika kerana apa-apa sebab sesuatu Resit Rasmi itu rosak dan tidak boleh dikeluarkan, ia mestilah jangan dimusnahkan tetapi hendaklah dibatalkan dan disimpan. Resit Rasmi batal tersebut berserta salinannya hendaklah dikemukakan untuk audit. Dalam persekitaran elektronik, Resit Rasmi batal dan laporan Resit Rasmi batal perlu dikemukakan untuk diaudit.'),
(1365, 'ATPK', 'C', '20 (b)', 'Resit Rasmi hanya boleh dibatalkan oleh pegawai yang diberi kuasa secara bertulis.'),
(1366, 'ATPK', 'C', '21 (a)(i)', 'Kawalan Terhadap Resit Rasmi\r\n(i)     Apabila Resit Rasmi, lesen atau borang akuan terima yang lain dikeluarkan daripada buku kaunterfoil tiap-tiap seorang pegawai yang menandatangani mana-mana dokumen itu mestilah menurunkan tandatangan ringkasnya pada kaunterfoil itu yang hendaklah mengandungi butir-butir penuh naskah asalnya termasuk amaun yang diterima dan tarikhnya.'),
(1367, 'ATPK', 'C', '21 (a)(ii)', '(ii)    Jika Resit Rasmi, lesen atau borang akuan terima yang lain memerlukan penggunaan kertas karbon, sama ada dalam sebuah buku atau dengan mesin daftar wang, kertas karbon dua belah muka hendaklah digunakan.'),
(1368, 'ATPK', 'C', '21 (b)', 'Apabila Resit Rasmi, lesen atau borang-borang akuan terima yang lain dijana secara elektronik, peraturan di perenggan (a) di atas tidaklah dikehendaki sebagai kawalan. Walau bagaimanapun sistem pengeluaran Resit Rasmi elektronik yang digunakan hendaklah memenuhi ciri-ciri kawalan tertentu yang ditetapkan. Antara maklumat-maklumat kawalan yang perlu diwujudkan ialah kod terimaan, tarikh dan waktu transaksi, kod operator, kod mesin, waktu mula dan akhir, tarikh, jumlah dan cara terimaan, nombor resit rasmi mula hingga akhir serta bilangan dan senarai Resit Rasmi dibatalkan bagi urusniaga berkenaan.'),
(1369, 'ATPK', 'C', '22 (a)', 'Tanggungjawab Pemungut\r\nPemungut adalah bertanggungjawab bagi:\r\nMenyimpan dengan selamat dan menggunakan dengan sepatutnya semua Resit Rasmi.'),
(1370, 'ATPK', 'C', '22 (b)', 'Menyimpan dan mengunci semua stok Resit Rasmi, lesen, dan borang akuan terima yang lain yang belum diguna dalam stok, dengan selamat dibawah kawalannya sendiri atau di bawah kawalan seorang pegawai kanan yang tidak menyediakan Resit Rasmi atau membuat pungutan.'),
(1371, 'ATPK', 'C', '22 (c)', 'Memastikan ciri-ciri keselamatan dan kawalan dalaman dalam persekitaran elektronik adalah mencukupi dan berfungsi serta hanya pegawai yang diberi kuasa sahaja dapat mengakses sistem dan dokumen-dokumen kawalan.'),
(1372, 'ATPK', 'C', '22 (d)', 'Melaksanakan terimaan secara elektronik mengikut pekeliling/arahan yang berkuatkuasa.'),
(1373, 'ATPK', 'C', '24 (a)', 'Tatacara Kawalan Borang-Borang Hasil\r\nPegawai yang bertangungjawab menerima stok Resit Rasmi hendaklah memastikan bahawa nombor di dalamnya adalah lengkap dan betul. Ia hendaklah, jika berkaitan, mencatat dan menandatangani di belakang helai pertama salinan pejabat dalam buku itu satu perakuan dalam bentuk yang berikut:\r\nDiperiksa oleh saya pada hari ini dan didapati mengandungi .. borang Resit Rasmi berpendua/berpeniga yang bernombor dengan betul.\r\nTandatangan Pemungut :...\r\nNama Penuh Pemungut :\r\nBertarikh :..'),
(1374, 'ATPK', 'C', '24 (b)', 'Mana-mana stok yang didapati rosak hendaklah dikembalikan kepada syarikat percetakan yang dilantik oleh Majlis berserta laporan.'),
(1375, 'ATPK', 'C', '24 (c)', 'Hanya stok resit yang sebenarnya dikehendaki bagi kegunaan serta-merta dikeluarkan mengikut susunan nombor yang betul, jika berkaitan.'),
(1376, 'ATPK', 'C', '24 (d)', 'Dalam persekitaran elektronik, Pemungut bertanggungjawab memastikan kawalan dalaman borang-borang hasil secara elektronik adalah mencukupi.'),
(1377, 'ATPK', 'C', '24 (e)', '(a) Penyata Borang Hasil Yang Dikawal di Lampiran 1 hendaklah disediakan setiap akhir bulan dan dihantar ke Bahagian Kewangan Majlis bersama kira-kira bulanan.'),
(1378, 'ATPK', 'C', '24A', 'Apabila Pemungut diganti, stok dokumen itu hendaklah disemak oleh kedua-dua pegawai yang menyerah dan yang mengambil alih. Penyata mengenai bilangannya dengan menunjukkan nombor siri masing-masing mestilah dicetak dan ditandatangani oleh kedua-dua pegawai itu dan satu salinan dihantar kepada Akauntan yang bekenaan. Penyata Bulanan seperti di Lampiran 1 hendaklah digunakan bagi maksud ini.'),
(1379, 'ATPK', 'C', '25', 'Mana-mana buku, Resit Rasmi yang didapati cacat semasa penggunaan hendaklah dibatalkan dan tidak digunakan. Semua buku tersebut hendaklah disimpan bagi tujuan pengauditan.'),
(1380, 'ATPK', 'C', '26', 'Dimansuhkan'),
(1381, 'ATPK', 'C', '27', 'Daftar Borang Hasil\r\nDaftar Borang Hasil mengikut bentuk yang ditetapkan, menunjukkan semua stok Resit Rasmi dan borang akuan terima lain yang diterima dan dikeluarkan, jika berkaitan, mestilah diselenggara oleh tiap-tiap Pemungut. Tiap-tiap satu catatan keluaran mestilah ditandatangani oleh pegawai Majlis di Ibu Pejabat yang kepadanya keluaran itu dibuat atau mengenai keluaran kepada pegawai di cawangan dan selain pegawai Majlis mestilah disokong dengan surat keluaran yang ditanda terima.'),
(1382, 'ATPK', 'C', '28', 'Menyimpan Selamat Buku Resit\r\nSemua buku resit dan borang-borang hasil terkawal jika tidak digunakan hendaklah disimpan dan dikunci dengan selamat.'),
(1383, 'ATPK', 'C', '29', 'Kebenaran Bertulis Menerima Wang\r\nTiada seseorang pegawai boleh menerima wang dalam apa jua bentuk terimaan kecuali diberi kebenaran khusus secara bertulis oleh Pegawai Pengawal. Pemungut hendaklah menyelenggara suatu rekod mengenai kebenaran yang telah diberi.'),
(1384, 'ATPK', 'C', '30', 'Pengeluaran Resit Rasmi\r\nResit Rasmi hendaklah ditulis menggunakan karbon dua muka oleh pegawai menerima wang dalam apa jua bentuk terimaan dan ditandatangani oleh seorang pegawai lain. Pengecualian daripada kaedah ini dibenarkan hanya jika: '),
(1385, 'ATPK', 'C', '30 (a)', 'Resit Rasmi dikeluarkan dengan menggunakan sistem terimaan bermesin atau elektronik yang diluluskan oleh Majlis;'),
(1386, 'ATPK', 'C', '30 (b)', 'Suatu bayaran tetap dan tercetak pada kupon, atau borang-borang hasil terkawal;'),
(1387, 'ATPK', 'C', '30 (c)', 'Seseorang pegawai yang dipertanggungjawabkan dengan kewajipan membuat pungutan luar atau pungutan yang seumpamanya dalam keadaan di mana ia mesti bekerja seorang diri;  atau'),
(1388, 'ATPK', 'C', '30 (d)', 'Seorang pegawai lain tidak ada dan kebenaran khusus telah didapati daripada Majlis.'),
(1389, 'ATPK', 'C', '31', 'Kawalan Mesin Beresit atau Kad Pintar\r\nKunci bagi mesin yang diluluskan jika ada, mestilah disimpan oleh Pemungut sendiri atau oleh seorang pegawai kanan yang lain daripada operator mesin itu atau pegawai yang menerima pungutan. Jika sistem bermesin atau elektronik digunakan, katalaluan dan kata pengenalan: \r\n(i)Hendaklah dirahsiakan;\r\n(ii)    Ditukarkan dari semasa ke semasa; dan\r\n(iii)   Ditukarkan jika terdapat pertukaran pegawai.\r\nDalam persekitaran elektronik di mana kad pintar digunakan pengguna perlu menyimpan selamat kad pintar tersebut dan tidak boleh sama sekali memberikan kad tersebut kepada orang lain.\r\n'),
(1390, 'ATPK', 'C', '32', 'Terimaan Melalui Mel\r\nSetiap pegawai yang bertanggungjawab membuka mel hendaklah menyelenggara suatu daftar bagi maksud merekod terimaan wang dan suratan atau barang berharga yang diterima melalui pos atau penghantar. Kewajipan ini tidak boleh dijalankan oleh pemegang wang atau oleh pegawai yang bertanggungjawab bagi menyediakan Resit Rasmi.\r\nSemua butir yang berkaitan hendaklah dicatatkan dalam daftar itu, seperti berikut:\r\n(a) Tarikh terima\r\n(b) Nama pengirim\r\n(c) Nombor pendaftaran bungkusan\r\n(d) Nombor cek\r\n(e) Amaun\r\n(f) Perihal dokumen atau barangan\r\n(g) Nombor resit akuan penerimaan bagi wang dan barang-barang berharga\r\n(h) Lain-lain butiran yang difikirkan perlu\r\n'),
(1391, 'ATPK', 'C', '34', 'Pegawai yang bertanggungjawab bagi mengakaun butiran itu atau menyimpannya dengan selamat hendaklah:\r\nMemberi tarikh dan nombor Resit Rasmi untuk dicatatkan dalam daftar itu; dan\r\nMenurunkan tandatangan ringkas pada rekod yang berkenaan dalam daftar itu.\r\n'),
(1392, 'ATPK', 'C', '35', 'Mana-mana pegawai lain yang memegang wang, dokumen atau barang selepas mel dibuka dan sebelum butiran itu diakaun hendaklah juga menurunkan tandatangan ringkas pada rekod dalam daftar itu.'),
(1393, 'ATPK', 'C', '36', 'Daftar itu hendaklah disemak tiap-tiap hari dengan buku tunai atau buku tunai elektronik dan daftar simpanan selamat oleh seorang pegawai penyelia.'),
(1394, 'ATPK', 'C', '37', 'Merekod Resit\r\nSetiap Resit Rasmi mestilah direkodkan dalam buku tunai atau buku tunai elektronik pada hari yang sama. Pengecualian daripada kaedah ini dibenarkan hanya:'),
(1395, 'ATPK', 'C', '37 (a)', 'bagi perkara yang termasuk di bawah Tatacara 30(c) di mana jumlah terimaan mesti direkodkan pada hujung hari;'),
(1396, 'ATPK', 'C', '37 (b)', 'Bagi keadaan yang melibatkan pungutan melalui ejen kutipan;'),
(1397, 'ATPK', 'C', '37 (c)', 'Bagi keadaan yang termasuk di bawah Tatacara 30(b), jika seseorang pegawai yang membuat pungutan di luar mesti merekodkan pungutan itu dengan seberapa segera bila ia kembali ke ibu pejabat; dan'),
(1398, 'ATPK', 'C', '37 (d)', 'Bagi keadaan lain, kebenaran khas mestilah diperolehi dari Pegawai Pengawal.'),
(1399, 'ATPK', 'C', '37A', 'Buku Tunai manual perlu menunjukkan Resit Rasmi batal. Perkara ini dikecualikan bagi buku tunai elektronik di mana satu Resit Rasmi batal disediakan oleh sistem.'),
(1400, 'ATPK', 'C', '38', 'Merekod Pungutan\r\nDipindahkan ke Tatacara 47 (a)'),
(1401, 'ATPK', 'C', '38A', 'Semua pungutan hendaklah direkodkan ke dalam buku tunai oleh seorang pegawai yang lain daripada pegawai yang menerima pungutan, kecuali :'),
(1402, 'ATPK', 'C', '38A (a)', 'Jika seseorang pegawai lain tidak ada dan kebenaran khusus telah didapati daripada Pegawai Pengawal; atau'),
(1403, 'ATPK', 'C', '38A (b)', 'Sekiranya sistem bermesin atau elektronik berupaya menjanakan buku atau daftar tunai.'),
(1404, 'ATPK', 'C', '39', 'Laporan Perbezaan Amaun Pungutan\r\nPegawai yang menyediakan penyata pungutan mestilah memberitahu pemungut dengan segera secara bertulis apabila didapati amaun yang dipungut tidak betul.'),
(1405, 'ATPK', 'C', '40', 'Lebihan Pungutan\r\nApa-apa wang lebihan yang mungkin didapati ketika mengimbangkan buku-buku dengan wang tunai mestilah dikreditkan dengan serta merta kepada hasil di bawah butiran Terimaan Pelbagai dan mestilah dilaporkan kepada Pegawai Pengawal.'),
(1406, 'ATPK', 'C', '41', 'Kurangan Pungutan\r\nApa-apa wang kurangan wang yang mungkin didapati ketika mengimbangkan buku-buku dengan wang tunai mestilah didebitkan kepada Akaun Tergantung. Satu laporan hendaklah dibuat kepada Pegawai Pengawal dan seterusnya dibawa kepada Mesyuarat Jawatankuasa  Kewangan untuk keputusan.'),
(1407, 'ATPK', 'C', '42 (a)', 'Penerimaan Cek\r\nPemungut boleh, menurut budi bicaranya, menerima cek sebagai bayaran. Semua cek hendaklah dibuat untuk dibayar kepada Majlis mengikut mana yang berkenaan dan dipalang Akaun Penerima Sahaja. Nombor siri mana-mana cek yang diterima, mestilah dicatatkan pada Resit Rasmi yang dikeluarkan.'),
(1408, 'ATPK', 'C', '42 (b)', 'Cek Tak Laku:'),
(1409, 'ATPK', 'C', '42(b)(i)', 'Sekiranya cek yang diterima tidak laku, adalah menjadi tanggungjawab Pemungut untuk mendapatkan amaun gantian cek itu mengikut peraturan yang berkuatkuasa.'),
(1410, 'ATPK', 'C', '42(b)(ii)', ' Semua pejabat Pemungut hendaklah menyelenggara satu Daftar Cek Tak Laku dan bertanggungjawab menghubungi pembayar/pengeluar cek untuk membatal resit asal dan mengambil tindakan sewajarnya untuk mengutip amaun yang tertunggak.'),
(1411, 'ATPK', 'C', '42(b)(iii)', 'Pemungut hendaklah menyediakan senarai cek tak laku untuk menyokong penyata tunggakan hasil dan melaporkannya kepada Pegawai Pengawal.'),
(1412, 'ATPK', 'C', '42(b)(iv)', 'Pelarasan perlu ditunjukkan di dalam buku tunai atau buku tunai elektronik apabila cek tersebut diketahui tak laku.'),
(1413, 'ATPK', 'C', '43', 'Mata Wang Asing\r\nPenerimaan mata wang asing, hendaklah terlebih dahulu mendapat kelulusan dari Pegawai Pengawal sebelum diterima.'),
(1414, 'ATPK', 'C', '44 (a)', 'Masa Pungutan Dimasukkan Ke Bank\r\nSemua pemungut yang menerima wang Majlis sama ada wang itu kena diperakaunkan sebagai hasil atau tidak adalah dikehendaki dimasukkan ke dalam akaun bank Majlis pada hari itu juga, jika:\r\n(i) Pungutan wang kertas dan duit syiling melebihi RM500; atau\r\n(ii) Jumlah pungutan (termasuk  wang kertas, duit syiling, cek, wang pos dan kiriman wang) melebihi RM2,000.\r\nJika wang pungutan lewat diterima, tugas-tugas ini hendaklah dilakukan pada hari kerja yang berikutnya dan resit-resit berkaitan hendaklah ditandakan Kutipan Lewat. Walau bagaimanapun pungutan menerusi cek mestilah dibayar masuk tidak lewat dari satu (1) minggu dari tarikh terima.'),
(1415, 'ATPK', 'C', '44 (b)', 'Semua terimaan, yang belum lagi dimasukkan ke dalam akaun Bank Majlis hendaklah dimasukkan pada hari kerja yang akhir dalam tempoh akaun bulanan atau dalam apa-apa tempoh akaun yang lebih singkat sebagaimana yang ditetapkan oleh Pegawai Pengawal.'),
(1416, 'ATPK', 'C', '44 (c)', 'Semua terimaan elektronik perlu dimasukkan ke Akaun Bank Majlis mengikut peraturan semasa yang berkuatkuasa.'),
(1417, 'ATPK', 'C', '45', 'Dipindahkan ke Tatacara 44 (b)'),
(1418, 'ATPK', 'C', '46', 'Dipindahkan ke Tatacara 44 (a)'),
(1419, 'ATPK', 'C', '47 (a)', 'Memasukkan Pungutan Ke Dalam Bank\r\nSlip bayaran masuk mestilah disediakan oleh seorang pegawai yang lain daripada pemegang wang, kecuali jika seorang pegawai lain tidak ada dan kelulusan khas telah didapati daripada Pegawai Pengawal.'),
(1420, 'ATPK', 'C', '47 (b)', 'Jika kemasukan itu melibatkan cek, nombor dan amaun setiap satu cek itu mestilah ditunjukkan pada keratan slip bayaran masuk bank itu.'),
(1421, 'ATPK', 'C', '48', 'Amaun setiap kemasukan bank hendaklah direkodkan dalam buku tunai atau buku tunai elektronik pada hari yang sama.'),
(1422, 'ATPK', 'C', '49', 'Menyemak Bayaran Masuk Ke Dalam Bank\r\nSlip bayaran masuk bank hendaklah :\r\n(a) Disemak tarikhnya;\r\n(b) Disahkan bahawa slip itu mengandungi cop bank tersebut;\r\n(c) Tandatangan ringkas pemegang wangnya atau bahawa slip itu telah dicop terima dengan mesin oleh bank itu.'),
(1423, 'ATPK', 'C', '50', 'Penyata Kutipan Daerah\r\nSlip bayar masuk bank hendaklah disimpan sendiri oleh pegawai yang memeriksa sehingga dikehendaki untuk dihantar ke ibu pejabat  bersama-sama dengan Penyata Pemungut yang berkaitan, dan yang mana hendaklah disediakan mengikut catatan dalam ruangan masuk dalam Buku Tunai atau Buku Tunai Elektronik dan disemak oleh pegawai yang memeriksa dengan slip bayar masuk bank.'),
(1424, 'ATPK', 'C', '51', 'Kegagalan Mengakaun Hendaklah Dilapor\r\nAkauntan Majlis hendaklah memastikan pemungut mengakaunkan pungutan mengikut Tatacara ini. Jika berlaku sebaliknya, Akauntan Majlis hendaklah melaporkan kepada Pegawai Pengawal.'),
(1425, 'ATPK', 'C', '52', 'Menyemak Catatan Setiap Hari\r\nSemua pemungut bertanggungjawab memastikan bahawa semua akaun mereka adalah betul. Bagi tujuan ini mereka atau wakilnya hendaklah membuat pemeriksaan dan semakan tiap-tiap hari semua catatan yang dibuat dalam buku Resit Rasmi dan lesen, buku tunai dan buku-buku akaun yang lain atau laporan-laporan yang dijana secara elektronik. Pegawai yang diwakilkan mestilah bukan pegawai yang sebenarnya membuat catatan-catatan itu. Pegawai yang menyemak hendaklah menandatangan ringkas dan mencatat tarikh di tempat setakat mana buku-buku atau laporan-laporan yang dijana secara elektronik itu telah disemak.'),
(1426, 'ATPK', 'C', '53', 'Menyemak Buku Resit Setiap Bulan\r\nSemua buku resit yang dikeluarkan daripada stok hendaklah disemak sekurang-kurangnya setiap bulan untuk memastikan bahawa semua resit yang belum digunakan masih utuh.'),
(1427, 'ATPK', 'C', '54', 'Penerimaan Wang Amanah\r\nSetiap pegawai mestilah melaporkan kepada Pegawai Pengawal jika ia telah menerima atas sifat jawatannya apa-apa wang  untuk dipegang sebagai amanah atau wakaf khas bagi apa-apa maksud. Wang itu mestilah diperakauankan dalam suatu akaun khas dalam Akaun Amanah atau Akaun Wakaf. Jika ada apa-apa keraguan tentang sama ada wang itu dipegang oleh pegawai atas sifat jawatannya atau atas sifat dirinya maka perkara itu hendaklah dirujukkan kepada Pegawai Pengawal untuk mendapat keputusan yang tertentu.'),
(1428, 'ATPK', 'C', '54A', 'Amaun Kasar Hendaklah Dibayar Masuk\r\nDalam semua hal, amaun kasar yang diterima hendaklah dibayar, atau diperakaun. Potongan daripada amaun kasar itu memerlukan kelulusan, dan hendaklah ditunjukkan sebagai perbelanjaan yang disokong dengan baucar yang sepatutnya, kecuali jika arahan khas mengenai perkara itu telah diberi menurut Seksyen 9(3) Akta Tatacara Kewangan 1957.'),
(1429, 'ATPK', 'C', '54B', 'Kaedah Mengakaun Pungutan\r\nSemua pungutan yang diterima daripada orang awam oleh seorang pegawai hendaklah diuruskan dengan cara yang sama seperti lain-lain Wang Awam yang diterima dan jumlahnya dibayar atau diperakaun kepada Majlis, walaupun kebenaran telah diberi bagi sebahagiannya diuntukkan bagi saraan pegawai itu. Amaun yang pegawai itu berhak hendaklah kemudiannya diambil melalui suatu baucar pembayaran daripada Majlis.'),
(1430, 'ATPK', 'C', '55', 'Wang Majlis Tidak Boleh Digunakan\r\nSelepas diterima dan sebelum dibayar masuk ke dalam bank wang Majlis tidak boleh digunakan bagi apa-apa maksud juapun, dan tiada seorang pegawai boleh mendahulu, meminjam atau menukarkan apa-apa wang yang ia bertanggungjawab kepada Majlis.'),
(1431, 'ATPK', 'C', '56', 'Kredit Perbelanjaan\r\nKredit kepada maksud perbelanjaan tahun semasa dibenarkan hanya bagi keadaan seperti berikut:\r\n(a) Melaras bayaran yang tidak dapat disampaikan; atau\r\n(b) Bayaran lebih yang didapati balik; atau\r\n(c) Wang yang telah tersalah dipertanggungkan; atau\r\n(d) Membetulkan salah peruntukan dengan kebenaran Pegawai Pengawal sebelum akaun ditutup.\r\n(e) Bayaran yang didahulukan oleh Majlis dan dibayar balik oleh pembayar.\r\necuali sebagaimana yang diarahkan dengan khususnya oleh Pegawai Pengawal, semua dapatan balik yang lain bagi perbelanjaan yang diperuntukkan mestilah dikreditkan kepada hasil.'),
(1432, 'ATPK', 'C', '57 (a)', 'Pulangan Balik Hasil\r\nHasil boleh dipulangkan balik oleh pemungut tanpa kebenaran selanjutnya jika:\r\n(i) Hasil itu pada mulanya telah dipungut dengan sepatutnya, setakat mana yang dibenarkan dalam sesuatu hal oleh undang-undang atau oleh pihak berkuasa yang layak.\r\n(ii) Hasil itu telah terlebih pungut atau tersalah pungut, dan amaun yang terlebih atau yang tersalah pungut itu tidak melebihi RM1,000 dalam mana-mana satu hal.'),
(1433, 'ATPK', 'C', '57 (b)', '(b) Apa-apa pulangan balik hasil yang lain sehingga ke amaun RM25,000 adalah memerlukan kebenaran Pegawai pengawal dan bagi amaun melebihi RM25,000 memerlukan kebenaran Jawatankuasa Kewangan dan Akaun.'),
(1434, 'ATPK', 'C', '57A', 'Mesin Meresit Tidak Boleh Digunakan Tanpa Kelulusan\r\nTiada sistem resit bermesin atau elektronik atau cara lain mengeluarkan Resit Rasmi boleh digunakan tanpa kelulusan bertulis daripada Majlis.'),
(1435, 'ATPK', 'C', '58', 'Penyelenggaraan Akaun Penghutang\r\nPegawai yang bertanggungjawab atas akaun penghutang hendaklah menyelenggara rekod dengan betul dan sempurna. Jumlah di dalam rekod individu tersebut hendaklah sentiasa bersamaan dengan jumlah dilejar penghutang.'),
(1436, 'ATPK', 'C', '59', 'Akaun Belum Terima\r\nLaporan baki penghutang pada akhir tahun hendaklah disediakan dengan lengkap oleh Akauntan Majlis dan dikemukakan kepada Pegawai Pengawal.'),
(1437, 'ATPK', 'C', '60', 'Pegawai Pengawal hendaklah mengadakan kawalan-kawalan seperti berikut:'),
(1438, 'ATPK', 'C', '60 (a)', 'Mengadakan semakan dalaman yang mencukupi berkenaan dengan rekod akaun belum terima dan membuat pelarasan kepada akaun-akaun.'),
(1439, 'ATPK', 'C', '60 (b)', 'memastikan akaun penghutang yang telah lewat tempoh diambil tindakan susulan dengan berkesan.'),
(1440, 'ATPK', 'C', '61', 'Peruntukan Bagi Hutang Ragu\r\nApa-apa peruntukan hutang ragu hendaklah mendapat kelulusan Mesyuarat Jawatankuasa Baitulmal.'),
(1441, 'ATPK', 'C', '62', 'Penetapan Peruntukan Hutang Ragu\r\nPenetapan hutang ragu hendaklah disediakan berasaskan kaedah tertentu secara konsisten.'),
(1442, 'ATPK', 'C', '63', 'Hapus Kira Hutang Ragu\r\nMana-mana hutang ragu yang telah diluluskan untuk dihapuskira, pelarasan yang sewajarnya hendaklah dibuat di dalam lejar penghutang setelah mendapat kelulusan Mesyuarat Jawatankuasa Baitulmal.'),
(1443, 'ATPK', 'C', '64', 'Kuasa Membuat Bayaran\r\nMajlis hendaklah membuat penurunan kuasa secara bertulis kepada Ketua Bahagian atau wakilnya bagi memperakui dokumen-dokumen sokongan pembayaran sebelum dihantar kepada pegawai pembayar yang bertanggungjawab di Bahagian Kewangan atau pegawai yang diwakilkan dengan kuasa itu.'),
(1444, 'ATPK', 'C', '65', 'Semua bayaran hanya boleh dibayar setelah diluluskan oleh Majlis atau pegawai yang diberi kuasa. Pegawai yang memperakukan adalah bertanggungjawab atas betulnya semua perbelanjaan yang dilakukan berkenaan dengan Maksud Perbelanjaan.'),
(1445, 'ATPK', 'C', '66', 'Cara Membuat Bayaran'),
(1446, 'ATPK', 'C', '66 (a)', 'Melalui suatu baucar pembayaran.'),
(1447, 'ATPK', 'C', '66 (b)', 'Tiada baucar boleh dibayar melainkan disokong dengan dokumen pembayaran, bil atau invois yang menyatakan bahawa sesuatu kerja itu telah dibuat atau barang telah dibekalkan.'),
(1448, 'ATPK', 'C', '66 (c)', 'Dokumen-dokumen lain seperti   penyata gaji dan borang-borang tertentu bagi permohonan untuk pendahuluan-pendahuluan hendaklah dikembarkan kepada baucar-baucar bayaran berkenaan.'),
(1449, 'ATPK', 'C', '67', 'Kawalan Perbelanjaan\r\nSungguhpun semua kerja, pembelian dan aktiviti-aktiviti lain yang melibatkan perbelanjaan yang diluluskan perlu dijalankan dengan seberapa yang boleh dalam tempoh yang dianggarkan, tiada apa-apa tambahan perbelanjaan boleh dilakukan semata-mata kerana hendak menyempurnakan pada atau sebelum akhir tahun kewangan. Adalah menjadi kewajipan Pegawai Pengawal bagi mengawal perbelanjaannya supaya tidak melebihi peruntukan yang diluluskan.'),
(1450, 'ATPK', 'C', '68', 'Pembayaran Yang Dikehendaki Kebenaran Khas\r\nDipindahkan ke Tatacara 16A'),
(1451, 'ATPK', 'C', '69', 'Seseorang pegawai yang membenarkan atau mengarahkan sesuatu perbelanjaan tanpa kuasa yang sepatutnya akan bertanggungjawab sendiri bagi amaun yang dipertanggungjawabkan itu.'),
(1452, 'ATPK', 'C', '70', 'Peringatan Sebelum Membayar Bil\r\nPegawai pembayar yang bertanggungjawab hendaklah sebelum membayar berpuas hati bahawa:'),
(1453, 'ATPK', 'C', '70 (a)', 'Bayaran itu tidak akan menyebabkan lebihan atas amaun yang diperuntukan bagi Maksud Perbelanjaan atau butiran projek atau akaun amanah kepada mana bayaran itu kena dipertanggungkan.'),
(1454, 'ATPK', 'C', '70 (b)', 'Perakuan ditandatangani secara manual atau digital oleh pegawai yang diberi kuasa dan bahawa prima facie pertanggungan itu adalah betul.'),
(1455, 'ATPK', 'C', '70 (c)', 'Potongan yang sepatutnya mengenai caruman, bayaran balik pendahuluan atau tanggungan lain yang telah diberitahu telah dibuat dengan sempurna daripada amaun bayaran itu.'),
(1456, 'ATPK', 'C', '70A (a)', 'Buku Vot Atau Buku Vot Elektronik'),
(1457, 'ATPK', 'C', '', 'Tiap-tiap Pegawai Pengawal atau pegawai yang diamanah mengawal mana-mana bahagian Maksud Perbelanjaan atau Kumpulan Wang Amanah, hendaklah menyelenggara sebuah buku vot atau buku vot elektronik yang menunjukkan dengan jelas pada bila-bila masa mengenai tiap-tiap Program dan aktiviti atau Projek dibawah mana-mana Maksud Perbelanjaan atau Kumpulan wang atau peruntukan di bawah kawalannya:\r\n(i)Amaun yang dibenarkan bagi tahun itu, ditolak amaun yang dipindahkan atau yang dipindahkan kepada program dan aktiviti yang lain dan ditambah kepadanya amaun yang diterima seumpama itu.\r\n(ii) Semua debit dan kredit kepada program dan aktiviti atau kumpulan wang itu mengikut bila ia berlaku, serta dengan jumlah kemaskini mengenai perbelanjaan bersih.\r\n(iii)Baki peruntukan pada setiap masa, dan\r\n(iv) Tanggungan mengikut bila ia dilakukan. Jumlah kemas kini tanggungan yang belum selesai hendaklah dicatat dan dilaraskan apabila tanggungan berubah.'),
(1458, 'ATPK', 'C', '70A (b)', 'Apabila seorang pegawai memperakui baucar untuk dibayar atau dilaraskan, ia hendaklah juga menurunkan tandatangan ringkasnya dalam suatu ruangan yang diperuntukkan khas bagi maksud ini pada catatan yang berkenaan dalam buku vot dan memastikan bahawa penyelarasan yang berkenaan dibuat.'),
(1459, 'ATPK', 'C', '70A (c)', 'Semua tanggungan yang belum selesai pada akhir tahun kewangan sebelumnya hendaklah dibawa ke hadapan ke dalam Buku Vot atau buku vot elektronik  bagi tahun yang baru.'),
(1460, 'ATPK', 'C', '70A (d)', 'Dalam persekitaran elektronik, cetakan laporan buku vot hendaklah dicetak sebulan sekali dan disemak oleh pegawai yang dipertanggungjawab dengan menurunkan tandatangan ringkas.'),
(1461, 'ATPK', 'C', '70B(a)', 'Mengeluarkan Wang Untuk Dibahagi-bahagikan\r\nWang yang dikehendaki oleh Majlis bagi bayaran tunai hendaklah dikeluarkan, dengan menggunakan borang yang ditetapkan dan ditandatangani oleh seorang pegawai diberi kuasa oleh Pegawai Pengawal untuk memperakui baucar, atas nama dua orang pegawai yang dilantik olehnya untuk menerima wang itu. Tiap-tiap permintaan itu mestilah disertai dengan senarai penerima wang atau baucar yang dijadual dan diperakui dengan sempurna sebagai betul supaya pegawai yang bertanggungjawab boleh memastikan bahawa bayaran yang dicadangkan itu adalah betul. Pegawai yang bertanggungjawab hendaklah menyimpan permintaan itu (yang diaku terima oleh pegawai yang dilantik) sementara menanti baucar yang diselesaikan dengan sempurna dipulangkan. Bagi persekitaran elektronik  pegawai Pengawal dan pegawai yang diberi kuasa hendaklah menyimpan selamat daftar pembayaran yang telah dibuat.'),
(1462, 'ATPK', 'C', '70B (b)', 'Tertakluk kepada Tatacara 70A (a), seorang pegawai perlu dilantik untuk menerima dan menunaikan cek tersebut.'),
(1463, 'ATPK', 'C', '70B (c)', 'Jika mana-mana pegawai yang memperakui tidak dapat membahagi-bahagikan dalam tempoh 21 hari atau sesuatu tempoh yang lebih singkat yang ditentukan oleh Pegawai Pengawal kesemua wang tunai atau cek, ia mestilah dengan serta-merta memasukkan semula amaun yang belum dibahagikan itu ke dalam Akaun Bank. Penyata pemungut yang disediakan bagi amaun yang dipulang balik itu hendaklah dilampirkan kepada baucar itu, atau, jika penyata pemungut itu meliputi lebih daripada satu baucar, rujukan kepada penyata pemungut itu hendaklah dinyatakan pada tiap-tiap satu baucar.'),
(1464, 'ATPK', 'C', '71', 'Tertakluk kepada Tatacara 72 dan 72A, semua bayaran kecuali yang dibuat daripada wang runcit hendaklah dibuat dengan menggunakan cek yang dipalang  Akuan Penerima Sahaja atas nama orang yang berhak menerima bayaran itu dan dihantar terus kepadanya oleh Pegawai Pembayar.'),
(1465, 'ATPK', 'C', '72', 'Jika bayaran perlu dibuat kepada sesiapa yang tidak mempunyai akaun bank, maka pegawai yang memperakui baucar itu hendaklah menguruskan supaya suatu cek perintah yang tak berpalang atas nama penerima dihantar kepadanya untuk diserahkan kepada penerima. Sebelum menyerahkan cek itu ia hendaklah mencatatkan di atasnya nombor kad pengenalan penerima bersama tandatangan dan nama jawatannya sendiri.'),
(1466, 'ATPK', 'C', '72A', 'Jika didapati tidak munasabah bagi seseorang penerima menukarkan sesuatu cek di bank, misalnya jika bayaran pampasan dibuat di luar pejabat, pegawai yang memperakui hendaklah menguruskan supaya wang tunai bagi amaun itu didapatkan mengikut Tatacara 70B.'),
(1467, 'ATPK', 'C', '73', 'Pembayaran Daripada Wang Runcit\r\nPembayaran bil yang tidak melebihi RM200 boleh dibuat daripada panjar wang runcit Majlis. Arahan ini tidak menghalang bil yang lebih besar jumlahnya dibayar daripada wang runcit jika praktikal dan berfaedah dan diluluskan oleh Pegawai Pengawal.'),
(1468, 'ATPK', 'C', '73A', 'Pembayaran Melalui Pindahan Dana Elektronik (EFT)\r\nDalam persekitaran elektronik, bayaran boleh dibuat terus kepada akaun penerima melalui pindahan dana elektronik.'),
(1469, 'ATPK', 'C', '74', 'Buku Vot\r\nDipindahkan ke Tatacara 70A'),
(1470, 'ATPK', 'C', '75', 'Dipindahkan ke Tatacara 70A(b)'),
(1471, 'ATPK', 'C', '76', 'Dipindahkan ke Tatacara 70A(c)'),
(1472, 'ATPK', 'C', '76A', 'Daftar Pembayaran'),
(1473, 'ATPK', 'C', '', 'Suatu Daftar Pembayaran yang digaris dengan ruang mengikut tajuk seperti dibawah hendaklah disenggara dalam semua pejabat menunjukkan butir-butir mengenai semua wang tunai dan cek terbuka yang dikeluarkan atau diterima untuk dibahagi-bahagikan. Ruang (a) dan (b) hendaklah diisi selepas sahaja baucar disediakan. Ruang (g) hendaklah digunakan apabila cek telah diperakui sah untuk ditukar sebelum dibahagi-bahagikan kepada penerima:\r\n(a)Nombor Baucar\r\n(b) Perihal Ringkas\r\n(c) Butir-butir cek atau wang tunai\r\n(d) Tarikh diterima untuk dibahagikan\r\n(e) Amaun\r\n(f)  Tarikh diperakui sah dan tandatangan ringkas pegawai\r\n(g)  Tandatangan pegawai yang menerima wang tunai atau cek yang diperakui sah, dan\r\n(h)  Tarikh bayaran disempurnakan dan ditandatangan ringkas oleh pegawai.'),
(1474, 'ATPK', 'C', '76B', 'Daftar itu hendaklah diperiksa tidak kurang daripada sekali sebulan oleh pegawai diberi kuasa dan hendaklah ditandatangan ringkas. Apabila berlaku pertukaran pegawai yang bertanggungjawab bagi memperakui baucar atau memperakui sah cek, daftar itu hendaklah disemak dengan wang tunai, cek dan baucar dalam tangan dan ditandatangani oleh kedua-dua pegawai yang ditukar dan yang menggantikannya. Apa-apa perbezaan hendaklah dilaporkan  kepada Pegawai Pengawal.'),
(1475, 'ATPK', 'C', '77', 'Semua baucar atau baucar elektronik hendaklah disokong dengan dokumen sokongan yang lengkap mengenai setiap satu perkhidmatan, bekalan atau kerja seperti tarikh, nombor, kuantiti jarak, kadar dan rujukan kelulusan ketua Bahagian atau wakilnya yang diberi kuasa untuk membolehkan ianya disemak tanpa merujuk kepada apa-apa dokumen lain daripada apa yang dikembarkan kepadanya. Apabila bayaran dibuat bagi sesuatu kontrak, baucar itu hendaklah mengandungi rujukan mengenai nombor kontrak itu. Dalam persekitaran elektronik, baucar elektronik beserta dokumen sokongan hendaklah disimpan dengan teratur dan selamat.'),
(1476, 'ATPK', 'C', '78', 'Nama seseorang pegawai majlis tidak boleh dicatatkan sebagai penerima bayaran pada sesuatu baucar kecuali berkenaan gaji, pendahuluan dan pinjaman atau tuntutan persendirian yang lain.'),
(1477, 'ATPK', 'C', '79', 'Jika pegawai mengemukakan dokumen bagi ganti bayaran untuk bayaran-bayaran lain yang dibuat olehnya, surat tuntutan yang diluluskan oleh Pegawai Pengawal dan disokong dengan resit hendaklah disertakan jika munasabah. Jika ini tidak boleh diperolehi, butiran yang dirujukkan hendaklah ditandakan *, dan pegawai itu hendaklah membuat perakuan di ruang tepi seperti berikut :\r\nSaya memperakui bahawa perbelanjaan yang ditanda * sejumlah RM telah sebenarnya dilakukan atas urusan rasmi dibayar oleh saya'),
(1478, 'ATPK', 'C', '80', 'Bayaran bagi perolehan bekalan atau perkhidmatan atau kerja hendaklah dibuat mengikut kadar atau harga kontrak yang dinyatakan mengikut perkataan-perkataan yang digunakan dalam kontrak itu. Bayaran bagi bekalan atau perkhidmatan atau kerja itu hendaklah mengikut susunan dalam jadual kepada kontrak itu. Bil yang berasingan hendaklah diminta bagi barang-barang yang tidak termasuk dalam kontrak.'),
(1479, 'ATPK', 'C', '81', 'Baucar bayaran bagi bekalan, perkhidmatan atau kerja hendaklah mengandungi suatu perakuan bahawa bekalan, perkhidmatan atau kerja itu telah dilaksanakan dengan sempurna.'),
(1480, 'ATPK', 'C', '82', 'Bagi suatu pembayaran kemajuan, hendaklah ada satu perakuan bahawa pembayaran itu dibuat mengikut nilai kerja setakat mana yang telah dilakukan atau nilai bahan-bahan yang sebenarnya telah dibekalkan ke tapak bina. Baucar bagi barang atau bekalan hendaklah disokong dengan suatu perakuan bahawa barang itu telah diterima dan sama ada ditanggungkan dalam rekod stor (dengan menyatakan senarai lampiran atau nombor kad) atau dikeluarkan bagi kegunaan segera dengan sempurna.'),
(1481, 'ATPK', 'C', '82A', 'Baucar berkenaan bekalan yang diimport bolehlah disampaikan untuk bayaran sebelum bekalan itu diterima jika baucar itu disertai dengan suatu invois mengandungi harga dan bil muatan yang berkenaan atau bil pengangkutan udara bukti penghantaran. Dalam hal yang demikian selepas bekalan diterima adalah menjadi kewajipan pegawai bertanggungjawab menghantar suatu surat perakuan kesempurnaannya kepada pegawai pembayar dan kepada Audit Dalam untuk disemak dan disimpan. Perakuan itu hendaklah memberi rujukan yang terang mengenai nombor baucar bayaran dan kontrak perjanjian.'),
(1482, 'ATPK', 'C', '82B', 'Baucar tidak boleh dibuat atas nama lebih daripada satu orang atau satu firma kecuali jika lebih dari satu orang atau satu firma adalah pihak-pihak kepada kontrak atau urusan itu juga atau jika kebenaran khusus bertulis telah diberi oleh Pegawai Pengawal.'),
(1483, 'ATPK', 'C', '83', ' Jika bayaran hendak dibuat kepada orang yang lain daripada yang disebutkan namanya dalam baucar, kuasa yang membenarkan pembayaran itu dibuat mestilah dicatatkan pada baucar itu.'),
(1484, 'ATPK', 'C', '84', 'Tiap-tiap satu lembaran baucar yang mengandungi lebih daripada satu lembaran hendaklah ditandatangan ringkas oleh pegawai yang diberi kuasa kecuali baucar elektronik.'),
(1485, 'ATPK', 'C', '85', 'Untuk memastikan bahawa segala pembayaran secara tunai dibuat kepada penerima yang sepatutnya, pegawai pembayar hendaklah memastikan bahawa semua penerima ditentukan pengenalannya dan boleh memahami maksud bayaran itu dibuat. Jika penerima tidak tahu menulis, cop jari bagi memperakui penerimaan bagi pembayaran tersebut mestilah disaksikan oleh dua orang, seorang daripadanya hendaklah pegawai yang membuat pembayaran.'),
(1486, 'ATPK', 'C', '86', 'Tuntutan perjalanan dan kerja lebih masa hendaklah dikemukakan tidak lewat daripada 8 hari bulan dalam bulan berikutnya.'),
(1487, 'ATPK', 'C', '87', 'Hanya satu tuntutan perjalanan dan kerja lebih masa boleh dibuat bagi satu-satu bulan. Jika perjalanan dimulakan pada atau hampir hujung bulan dan disempurnakan dalam bulan yang berikutnya, hitungan kilometer pergi dan balik hendaklah dianggap sebagai perjalanan bagi bulan dalam mana perjalanan itu disempurnakan.'),
(1488, 'ATPK', 'C', '88', 'Tuntutan hendaklah diantaranya memberi butir-butir penuh mengenai tarikh, masa, jarak, kadar, jawatan pegawai dan gaji pokok, cara perjalanan, jenis tugas dan sebagainya.'),
(1489, 'ATPK', 'C', '88A', 'Tuntutan bagi perjalanan rasmi dalam negeri atau luar negeri dan perpindahan rumah hendaklah masing-masing dibuat pada suatu tuntutan yang berasingan.'),
(1490, 'ATPK', 'C', '89', 'Pegawai dibenarkan menuntut elaun perjalanan kenderaan sekiranya dikehendaki atau diarahkan untuk bertugas bekerja selepas waktu pejabat di luar kawasan ibu pejabat.'),
(1491, 'ATPK', 'C', '90', 'Elaun Perjalanan Kenderaan daripada Rumah ke pejabat/tempat bertugas dan sebaliknya dibenarkan tertakluk kepada pengesahan Pegawai Pengawal bahawa tugas tersebut adalah mustahak untuk dilaksanakan dengan serta merta dan tidak boleh ditangguh untuk urusan pada waktu biasa pejabat. Pengesahan ini hendaklah dibuat secara bertulis dan disertakan setiap kali tuntutan.'),
(1492, 'ATPK', 'C', '91', 'Jika tuntutan bagi perjalanan kerana bertugas kena dipertanggungkan kepada beberapa Maksud Perbelanjaan yang kesemuanya dibawah kawalan seorang Pegawai Pengawal, tiap-tiap Maksud Perbelanjaan dan amaun yang kena dipertanggungkan kepada Maksud Perbelanjaan itu hendaklah ditunjukkan dalam baucar.'),
(1493, 'ATPK', 'C', '91A', 'Amaun yang dipertanggungkan kepada Maksud Perbelanjaan masing-masing bagi Elaun Perjalanan Kenderaan atau Tambang Pengangkutan Awam hendaklah mengikut jarak yang kena dipertanggungkan kepada satu-satu Maksud Perbelanjaan itu.'),
(1494, 'ATPK', 'C', '', 'Kuasa Menandatangani Baucar\r\nPegawai-pegawai yang menandatangani baucar pembayaran dan baucar jurnal hendaklah diberi kuasa secara bertulis oleh Pengerusi bagi persekitaran manual dan elektronik.'),
(1495, 'ATPK', 'C', '93', 'Dipindahkan ke Tatacara 92'),
(1496, 'ATPK', 'C', '93A', 'Tandatangan Pegawai Pengawal dan Wakil Yang Diberi Kuasa memperakui tentang betulnya setiap butir dalam baucar itu adalah bertanggungjawab bahawa:\r\n(a) Perkhidmatan, bekalan atau kerja telah diterima dengan sempurnanya;\r\n(b) Harga yang dikenakan adalah sama ada mengikut kontrak atau skala yang diluluskan, atau patut dan munasabah mengikut kadar tempatan semasa;\r\n(c) Kebenaran telah didapati sebagaimana yang dinyatakan;\r\n(d) Penghitungan dan pengiraan adalah betul;\r\n(e) Penerimaan yang dinamakan dalam baucar itu adalah pihak yang berhak menerima bayaran;\r\n(f) Bayaran itu adalah pertanggungan yang sepatutnya kepada program dan aktiviti atau projek atau akaun yang dinyatakan; dan\r\n(g) Peruntukan mencukupi dan tidak dipertanggungkan bagi maksud lain.\r\nBagi persekitaran elektronik, tandatangan manual pada baucar tidak diperlukan, memadai dengan tandatangan digital dengan catatan nama pada baucar.\r\nWalau bagaimanapun di bawah Sistem Bayaran Pukal bayaran boleh dibuat terlebih dahulu atas maklumat yang diberi oleh pembekal dan apa-apa pelarasan (jika ada) hendaklah dibuat kemudiannya.'),
(1497, 'ATPK', 'C', '93B', 'Jika berlaku Kehilangan Wang Awam akibat sesuatu perakuan yang tidak betul pada sesuatu baucar atau dokumen, pegawai yang memperakuinya akan dipertanggungjawabkan bagi kehilangan itu.'),
(1498, 'ATPK', 'C', '94', 'Semua Bil Dan Tuntutan Bayaran Hendaklah Dibayar Dengan Segera'),
(1499, 'ATPK', 'C', '94 (a)', 'Pegawai Pengawal atau pegawai yang bertanggungjawab hendaklah meminta supaya semua bil dan tuntutan disampaikan dengan segera dan hendaklah memastikan bahawa bil itu dibayar dengan segera tidak lewat dari 14 hari bekerja dari tarikh ianya diterima dengan lengkap dan sempurna.'),
(1500, 'ATPK', 'C', '94 (b)', 'Bahagian kewangan hendaklah menyimpan satu Daftar Bil yang mengandungi makulumat-maklumat seperti tarikh penerimaan bil, amaunnya, tarikh bil diperakui oleh pegawai yang berkenaan bahawa perkhidmatan atau bekalan telah diberikan dengan memuaskan, tarikh baucar disediakan, tarikh  baucar disampaikan kepada pihak yang bertanggungjawab untuk menyediakan cek dan tarikh cek itu dikirim kepada pembekal atas kontraktor. Daftar ini hendaklah disemak dari masa ke semasa. Penyenggaraan Daftar Bil secara manual adalah dikecualikan jika penggunaan sistem elektronik dilaksanakan dimana Daftar Bil disediakan secara elektronik.'),
(1501, 'ATPK', 'C', '95', 'Penyata Perubahan Mengenai Pendapatan Seseorang Pekerja\r\nSebelum apa-apa pembayaran gaji dibuat, penyata perubahan gaji hendaklah disediakan sekiranya ada perubahan.'),
(1502, 'ATPK', 'C', '', 'Tarikh Membayar Gaji Dan Elaun\r\nKecuali sebagaimana dibenarkan oleh Pegawai Pengawal, gaji dan elaun tidak boleh dibayar sebelum 24 hari bulan dalam bulan gaji atau elaun itu kena dibayar. Jika 24 hari bulan dalam bulan itu jatuh pada hari Jumaat, Sabtu atau Ahad bayaran boleh dibuat oleh Majlis pada hari kerja akhir sebelum hari Jumaat.'),
(1503, 'ATPK', 'C', '97', 'Gaji bulanan bagi pegawai-pegawai Majlis boleh dibayar terlebih dahulu sempena hari-hari perayaan Islam mengikut budi bicara Pegawai Pengawal. Jika hari perayaan itu jatuhnya selepas hari ke 11 dalam bulan itu, pembayaran boleh dilakukan kepada pegawai-pegawai tidak lebih daripada tujuh hari sebelum tarikh yang sebenar bagi perayaan itu. Bagi hari-hari perayaan yang lain, pembayaran gaji awal boleh dibuat mengikut budi bicara Pegawai Pengawal.'),
(1504, 'ATPK', 'C', '98 (a)', 'Semua pegawai yang dipertanggungjawab bagi membayar gaji adalah dibenarkan jika diminta dengan bertulis oleh pegawai Majlis, membuat potongan mengikut penyata yang disampaikan oleh Syarikat atau agensi yang diluluskan oleh Pegawai Pengawal, dan membayar jumlah yang dipotong itu dengan cek yang dipalang.'),
(1505, 'ATPK', 'C', '98 (b)', 'Semua potongan yang dibuat demikian mestilah dimasukkan bersama potongan lain kepada lembaran gaji yang berkenaan.'),
(1506, 'ATPK', 'C', '99', 'Dimansuhkan'),
(1507, 'ATPK', 'C', '100', 'Setiap pembayaran dalam akaun hendaklah disokong dengan suatu baucar yang sepatutnya. Suatu pembayaran dianggap sebagai perbelanjaan tidak diakui jika tidak ada keterangan bahawa ia telah dilakukan, dibenarkan, atau diselesaikan dengan sepatutnya.'),
(1508, 'ATPK', 'C', '101', 'Baucar Hilang'),
(1509, 'ATPK', 'C', '101 (a)', 'Sekiranya baucar bayaran asal yang telah dibayar hilang, pegawai yang berkenaan hendaklah memastikan :\r\n(i) Salinan baucar tersebut ada disimpan dengan selamat.\r\n(ii) Salinan baucar yang diperakui itu mestilah menunjukkan semula butir-butir yang terdapat pada baucar asal.\r\n(iii) Salinan dokumen sokongan yang diakui sah disertakan bersama.\r\n(iv) Keterangan bayaran telah diterima oleh penerima hendaklah disertakan jika ada.'),
(1510, 'ATPK', 'C', '101 (b)', 'Sesuatu cek yang sudah dibayar dan diperakui sah (endorsed) penerima yang dinamakan dalam baucar itu yang dibayar ke dalam akaun bank atas namanya boleh diterima sebagai keterangan penerimaan oleh penerima.'),
(1511, 'ATPK', 'C', '101 (c)', 'Arahan ini dikecualikan dalam persekitaran elektronik dengan adanya jejak audit (audit trail).'),
(1512, 'ATPK', 'C', '102', 'Laporan Pembayaran Tidak Diakui'),
(1513, 'ATPK', 'C', '102', 'Laporan Pembayaran Tidak Diakui\r\nJika seseorang pegawai pembayar mendapati bahawa sesuatu bayaran dalam akaunnya, sebagaimana yang ternyata pada baucar asal atau salinannya yang dikemukakan tidak diakui dengan cukup dan ia tidak dapat memperolehi baucar yang memuaskan, ia mestilah meminta kelulusan Pegawai Pengawal supaya bayaran itu dibiar dipertanggungkan kepada akaun. Laporan hendaklah dihantar dalam tiga (3) salinan mengikut tajuk yang diberi di bawah dan disertai dengan baucar asal, baucar salinan pejabat atau salinannya yang diperakui.\r\n(a)Tarikh baucar;\r\n(b)Nombor baucar;\r\n(c) Nama penerima bayaran;\r\n(d) Butir-butir perkhidmatan;\r\n(e) Jumlah;\r\n(f) Maksud perbelanjaan atau Akaun; dan \r\n(g) Sebab-sebab mengapa baucar atau salinannya itu dianggap tidak mencukupi.'),
(1514, 'ATPK', 'C', '103', 'Segala usaha hendaklah diambil bagi mendapatkan kelulusan Pegawai Pengawal atas mana-mana bayaran yang tidak diakui sebelum akaun dikemukakan untuk diaudit. Perkara ini tidak harus menyebabkan akaun diserahkan lewat untuk diaudit.'),
(1515, 'ATPK', 'C', '109', 'Sejumlah wang bolehlah didahulukan kepada seseorang pegawai yang diberi kuasa bagi membolehkannya membuat pembayaran yang segera tanpa perlu ditanggungkan terlebih dahulu kepada sesuatu vot.'),
(1516, 'ATPK', 'C', '110', 'Majlis boleh meluluskan melalui Jawatankuasa yang berkaitan sejumlah amaun bagi pengwujudan atau penambahan sesuatu panjar yang didapati perlu bagi seseorang pegawai itu menggunakannya.'),
(1517, 'ATPK', 'C', '111', 'Semua permohonan bagi mengadakan panjar hendaklah dibuat kepada Majlis melalui Jawatankuasa yang berkaitan dengan menjelaskan justifikasi panjar itu dikehendaki dan jumlahnya.'),
(1518, 'ATPK', 'C', '112', 'Seseorang pegawai yang menerima sesuatu panjar kecuali panjar wang runcit mestilah membuka suatu akaun bank atas nama jawatan yang dipegangnya. Melainkan jika dibenarkan sebaliknya oleh Pegawai Pengawal. Akaun bank itu tidak boleh digunakan bagi apa-apa maksud lain.'),
(1519, 'ATPK', 'C', '112A', 'Pemegang panjar hendaklah mendapat baucar dengan dokumen sokongan untuk semua bayaran daripada panjar. Jika tiada, satu perakuan hendaklah diminta daripada pegawai yang menuntut bayaran.'),
(1520, 'ATPK', 'C', '113', 'Apabila panjar dikehendaki dicukupkan kembali, baucar berjadual (disokong dengan suatu baucar runcit) bagi semua pembayaran yang dibuat hendaklah dihantar kepada Bahagian Kewangan yang mengganti bayaran.'),
(1521, 'ATPK', 'C', '114', 'Setiap pegawai yang menerima panjar hendaklah menyelenggara Buku Panjar Wang Runcit mengikut bentuk seperti di Lampiran 3 menunjukkan semua amaun yang diterima dan dibayar. Semua baucar hendaklah dinombor dan dicatatkan ke Buku Panjar Wang Runcit pada hari ianya dibayar. Buku Panjar Wang Runcit hendaklah dijumlah serta diseimbangkan dari semasa ke semasa apabila rekupan perlu dibuat.'),
(1522, 'ATPK', 'C', '114A', 'Pada akhir tiap-tiap bulan, Buku Tunai Panjar hendaklah disesuaikan dengan Penyata Bank jika ada dan pemegang panjar hendaklah mengesahkan baki tunai dengan tunai sebenar. Bayaran-bayaran dari Panjar hendaklah dilaraskan ke akaun perbelanjaan sebelum akhir tahun.'),
(1523, 'ATPK', 'C', '114B', 'Bagi panjar yang mempunyai akaun bank, Pegawai Pembayar adalah bertanggungjawab memastikan baki di buku tunai tidak terlebih dikeluarkan.'),
(1524, 'ATPK', 'C', '115', 'Apabila berlaku pertukaran pegawai yang mengendalikan sesuatu panjar, suatu penyata yang serupa dengan yang ditunjukkan di Lampiran 4 menunjukkan kedudukan panjar pada masa memberi alih hendaklah disediakan, kemudian ditandatangani oleh kedua-dua orang pegawai yang memberi alih dan yang mengambil alih dan disahkan oleh Akauntan Majlis.'),
(1525, 'ATPK', 'C', '116 (a)', 'Pada permulaan setiap bulan berikutnya, setiap pemegang panjar hendaklah menghantar kepada Bahagian Kewangan yang mengganti bayaran suatu penyata menunjukkan kedudukan panjarnya pada akhir bulan yang lepas.'),
(1526, 'ATPK', 'C', '116 (b)', 'Pemegang panjar hendaklah membuat rekupan apabila baki panjar telah berkurangan 50% daripada jumlah panjar yang diluluskan.'),
(1527, 'ATPK', 'C', '116A', 'Pada penghujung tahun semua pemegang panjar hendaklah mengemukakan satu perakuan panjar bersama-sama rekupan terakhir bagi tahun tersebut kepada Bahagian Kewangan.'),
(1528, 'ATPK', 'C', '116B', 'Sekiranya sesuatu panjar tersebut tidak diperlukan lagi, pemegang panjar hendaklah mengemukakan cadangan untuk menutup akaun berkenaan kepada Majlis. Setelah mendapat kelulusan, Panjar itu hendaklah ditutup dan wang Panjar tersebut hendaklah dipulangkan.'),
(1529, 'ATPK', 'C', '117', 'Jika suatu Perintah Mahkamah telah dibuat berkenaan dengan pentadbiran dalam kebankrapan harta pusaka penghutang yang mati atau jika simati itu sebelum kematiannya telah dihukum menjadi bankrap atau jika Perintah Pentadbiran Pemakan Gaji telah dibuat terhadapnya, maka semua aset simati itu adalah terletak hak pada Ketua Pengarah Insolvensi. Oleh itu, semua wang yang kena dibayar kepada simati sebagai gaji, pencen, ganjaran atau lain-lain mestilah dihantar untuk diselesaikan walaupun suatu surat kuasa mentadbir atau pemberian probet yang telah diberi kepada wasi atau orang lain yang diberi kuasa sedemikian. Pegawai Pengawal hendaklah pertamanya melaporkan hal itu kepada Cawangan Jabatan Insolvensi Malaysia yang berkenaan, untuk membolehkan ia membuat tuntutan rasmi.');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(1530, 'ATPK', 'C', '118', 'Stok cek Majlis hendaklah diperiksa dengan serta-merta apabila diterima daripada bank untuk menentukan bahawa tiada cek yang hilang. Pemeriksaan ini hendaklah dilakukan oleh pegawai yang bertanggungjawab menjaga urusan akaun bank dan pegawai itu hendaklah mengaku saksi dengan tandatangan ringkas di belakang kaunterfoil yang akhir atau di dalam daftar stok cek bahawa pemeriksaan itu telah dibuat.'),
(1531, 'ATPK', 'C', '119', 'Mana-mana buku cek yang tidak dikeluarkan untuk digunakan hendaklah disimpan dalam peti besi yang kuncinya dipegang oleh pegawai yang bertanggungjawab. Stok cek yang dikeluarkan hendaklah dikunci apabila tidak sebenarnya digunakan sama ada dalam peti besi atau tempat lain yang selamat tetapi mestilah disimpan di dalam peti besi pada penutupan urusan tiap-tiap hari.'),
(1532, 'ATPK', 'C', '121', 'Mana-mana cek yang tidak ditunaikan dalam tempoh 6 bulan, maka ianya akan terbatal dan tindakan berikut hendaklah diambil:'),
(1533, 'ATPK', 'C', '121 (a)', 'Mana-mana cek yang tidak ditunaikan pada akhir tempoh enam (6) bulan dari tarikh ianya dikeluarkan hendaklah disifatkan sebagai terbatal dan dilaraskan dengan mendebit Akaun Tunai dan mengkredit Akaun Cek Tamat Tempoh. Sekiranya ada tuntutan mengenai cek yang telah dibatalkan itu, maka suatu cek baru hendaklah dikeluarkan dan didebitkan kepada Akaun Cek Tamat Tempoh. Jika cek ganti tidak dikehendaki maka amaun cek asal hendaklah dikredit terus ke Maksud Perbelanjaan atau Akaun Amanah jika dibatalkan dalam tahun yang sama atau dikreditkan ke Akaun Hasil jika cek dibatalkan dalam tahun berikutnya kecuali bagi cek yang pada asalnya didebit kepada Akaun Amanah yang mana ianya harus dikreditkan kepada Akaun Amanah yang berkenaan.'),
(1534, 'ATPK', 'C', '121 (b)', 'Jika sesuatu cek atau pindahan dana elektronik itu dibatalkan dan bayaran ganti akan dikeluarkan maka bayaran tersebut hendaklah pada mulanya dilaraskan dengan mendebit Akaun Tunai dan mengkreditkan Akaun Cek Tamat Tempoh atau Akaun Pindahan Dana Elektronik Terbatal. Akaun ini hendaklah didebit apabila bayaran ganti dikeluarkan. Sekiranya bayaran ganti tidak dikehendaki dan dibatalkan dalam tahun yang sama, amaun bayaran asal hendaklah dikredit terus ke Maksud Perbelanjaan atau Akaun Amanah. Sekiranya bayaran dibatalkan dalam tahun berikutnya kecuali amaun bayaran asal hendaklah dikreditkan  kepada Akaun Hasil kecuali bagi bayaran yang pada asalnya didebitkan kepada Akaun Amanah.'),
(1535, 'ATPK', 'C', '121 (c)', 'Mana-mana amaun cek yang terbatal yang tidak dituntut selepas enam (6) tahun dari tarikh cek hendaklah dikreditkan kepada Akaun Hasil Pelbagai kecuali bagi cek yang pada asalnya didebit kepada Akaun Amanah atau Akaun Pinjaman yang mana ianya harus dikreditkan kepada Akaun Amanah atau Akaun Pinjaman yang berkenaan.'),
(1536, 'ATPK', 'C', '121 (d)', 'Dalam melaksanakan pindahan dana elektronik, pemindahan wang secara elektronik yang dikembalikan oleh bank hendaklah disifatkan sebagai terbatal.'),
(1537, 'ATPK', 'C', '121 (e)', 'Dalam semua hal, pegawai yang membenarkan bayaran yang asal mestilah diberitahu mengenai pembatalan sesuatu cek atau pindahan dana elektronik itu serta hendaklah menyenggara satu daftar cek-cek terbatal atau daftar pindahan dana elektronik terbatal.'),
(1538, 'ATPK', 'C', '122', 'Dimansuhkan'),
(1539, 'ATPK', 'C', '123', 'Cek hendaklah ditandatangani oleh sekurang-kurangnya 2 orang pegawai yang diberi kuasa oleh Majlis secara bertulis dan dimaklumkan kepada pihak bank. Sekiranya mesin menandatangan cek digunakan, kawalan dalaman dan keselamatan yang mencukupi hendaklah diadakan.'),
(1540, 'ATPK', 'C', '123A', 'Urusan pindahan melalui pindahan dana elektronik hendaklah diluluskan oleh pegawai yang diberi kuasa untuk berbuat demikian oleh Pegawai Pengawal. Sekiranya pindahan melalui pindahan dana elektronik dilaksanakan, kawalan dalaman dan keselamatan yang mencukupi hendaklah diadakan.'),
(1541, 'ATPK', 'C', '124', 'Pegawai yang menandatangan cek adalah bertanggungjawab dalam memastikan bahawa cek yang dikeluarkan adalah berasaskan kepada keadaan yang sah dan benar.'),
(1542, 'ATPK', 'C', '124A', 'Pegawai-pegawai yang diberi kuasa untuk membuat pemindahan wang adalah bertanggungjawab secara bersama dan masing-masing bagi memastikan bahawa pindahan itu adalah dikeluarkan dengan sepatutnya.'),
(1543, 'ATPK', 'C', '125', 'Hanya cek yang dikeluarkan atas nama jawatan pegawai yang diberi kuasa oleh Pegawai Pengawal boleh ditukar kepada wang tunai di Bank.'),
(1544, 'ATPK', 'C', '126', 'Cek hendaklah dipalang akaun penerima saja tetapi jika dalam apa-apa bentuk lain cek itu hendaklah, jika dihantar dengan pos, dimasukkan dalam sampul berdaftar. Sebarang permohonan untuk membatalkan palang hendaklah dibuat secara bertulis dan diluluskan oleh pegawai yang diberi kuasa oleh Pegawai Pengawal.'),
(1545, 'ATPK', 'C', '127(a)', 'Jika sesuatu cek Majlis hilang atau salah letak sebelum diniaga, bayaran boleh didapati jika penerima bayaran mengemukakan satu perakuan bertulis bahawa bayaran tidak diterimanya kepada pegawai yang mengeluarkan cek itu, dan:\r\n(i) Pegawai itu hendaklah mengambil tindakan bagi menahan cek itu dari ditunaikan;\r\n(ii) Selepas tindakan menahan bayaran disahkan oleh bank, pegawai itu hendaklah menentukan bahawa cek itu tidak melalui akaun banknya dalam tempoh sehingga masa pengesahan itu; dan\r\n(iii) Kemudiannya suatu cek baru boleh dikeluarkan.'),
(1546, 'ATPK', 'C', '127 (b)', 'Bagaimanapun tindakan menahan bayaran tidak perlu diambil jika tempoh laku cek itu telah luput tetapi masih juga perlu dipastikan bahawa ia tidak melalui akaun bank.'),
(1547, 'ATPK', 'C', '128', 'Draf, pindahan telegraf, surat kredit dan sebagainya hanya boleh diminta mengikut sesuatu kebenaran yang dikeluarkan oleh Pegawai Pengawal atau pegawai yang diberi kuasa olehnya.'),
(1548, 'ATPK', 'C', '129', 'Dalam setiap pejabat hanya peti besi atau bilik kebal yang kukuh dan mempunyai kunci berasingan atau menggunakan kunci berkombinasi boleh digunakan bagi menyimpan wang Majlis. Semua wang hendaklah disimpan di dalam peti besi itu selepas waktu pejabat. Peti besi atau bilik kebal itu hendaklah mempunyai dua kunci yang berasingan dengan anak kunci yang berlainan atau nombor kombinasi yang berlainan dipegang oleh dua orang pegawai yang bertanggungjawab yang diberi kuasa secara bertulis oleh Pegawai Pengawal dan pegawai-pegawai itu hendaklah dari semasa ke semasa mengesah dan memperakui kandungan peti besi itu atau bilik kebal.'),
(1549, 'ATPK', 'C', '130', 'Semua wang tunai, cek, setem, dokumen dan barang-barang berharga lain yang diterima oleh Majlis hendaklah disimpan dalam peti besi atau bilik kebal dengan seberapa segera yang boleh. Manakala wang tunai dan cek yang diterima hendaklah dimasukkan ke dalam bank. Peti wang tunai hendaklah disimpan ke dalam peti besi atau bilik kebal jika ada tempat sedemikian.'),
(1550, 'ATPK', 'C', '131', 'Pemegang anak kunci peti besi adalah sentiasa sendirinya bertanggungjawab menyimpan dengan selamat dan menggunakan dengan sepatutnya. Anak kunci itu tidak boleh sekali-kali ditinggalkan dalam pejabat.'),
(1551, 'ATPK', 'C', '132', 'Nombor kombinasi peti besi hendaklah dirahsiakan dan hanya diketahui oleh Pengawai Pengawal atau seorang pegawai yang diwakilkannya bagi menjaga peti besi. Pegawai yang bertanggungjawab hendaklah memastikan supaya nombor kombinasi itu tidak dapat diketahui oleh pihak lain.'),
(1552, 'ATPK', 'C', '133', 'Pegawai yang didapati bersalah kerana cuai mengenai penyimpanan anak kunci dan nombor kombinasi peti besi atau tidak mematuhi peraturan-peraturan berkenaan boleh dikenakan tindakan tatatertib.'),
(1553, 'ATPK', 'C', '134', 'Jika seseorang pemegang anak kunci/nombor kombinasi tidak dapat menjalankan tugasnya, atau ditukar ke pejabat lain anak kuncinya atau nombor kombinasinya hendaklah diambil alih oleh seorang pegawai yang diarahkan oleh Pegawai Pengawal. Pegawai ini bersama dengan pemegang anak kunci/nombor kombinasi mestilah menyemak kandungan peti besi dan mengesahkannya.'),
(1554, 'ATPK', 'C', '135', 'Bagi peti besi atau bilik kebal yang menggunakan kunci kombinasi, kodnya hendaklah ditukar oleh kedua-dua orang pegawai yang bertanggungjawab setiap satu (1) tahun sekali atau setiap kali terdapat pertukaran pegawai atau pada bila-bila masa disyaki bahawa kombinasi kod telah diketahui oleh orang yang tidak dibenarkan. Kombinasi kod itu hendaklah ditulis pada sekeping kertas dan dimasukkan dalam sampul surat yang dimeteri dan ditanda Rahsia Besar. Nama pegawai yang mengendalikan peti besi atau bilik kebal dan tarikh sampul surat dimeterikan hendaklah juga ditulis di atas sampulnya. Sampul surat ini hendaklah disimpan dengan selamat.'),
(1555, 'ATPK', 'C', '136 (a)', 'Sekiranya kunci bagi suatu peti besi atau bilik kebal hilang, kehilangan itu mestilah dilaporkan dengan serta-merta kepada Pegawai Pengawal. Pegawai Pengawal hendaklah mengarahkan kunci ganti dikeluarkan daripada tempat simpanannya, membuka peti besi itu, menyemak kandungannya dan jika perlu membuat aturan alternatif sementara bagi menyimpan selamat kandungan peti besi itu, dan menguruskan supaya set kunci itu ditukar dengan secepat mungkin.'),
(1556, 'ATPK', 'C', '136 (b)', 'Pegawai Pengawal hendaklah mengambil tindakan mengenai kehilangan itu mengikut Tatacara 342 hingga 369. '),
(1557, 'ATPK', 'C', '136A', 'Bagi pejabat yang tidak ada peti besi, semua wang termasuk Panjar Wang Runcit hendaklah dijaga dengan selamat mengikut apa-apa cara yang dibenarkan oleh Pegawai Pengawal.'),
(1558, 'ATPK', 'C', '137', 'Melainkan jika dibenarkan dengan tertentu oleh Pegawai Pengawal tiada seorang pegawai Majlis boleh menyimpan atau membenarkan disimpan di dalam mana-mana peti besi itu di bawah jagaannya apa-apa aset kecuali aset Majlis atau apa-apa wang yang ia terikat menerima dan mengakaun oleh kerana jawatannya.'),
(1559, 'ATPK', 'C', '138', 'Kunci ganti bagi setiap satu peti besi, mestilah dimasukkan ke dalam sampul berlakri yang pada sebelah luarnya bertulis perihal kandungannya menunjukkan tempat letak peti besi berkenaan. Perihal itu hendaklah ditarikh dan ditandatangani oleh Pegawai Pengawal atau pegawai yang diberi kuasa.'),
(1560, 'ATPK', 'C', '139', 'Kunci ganti peti besi hendaklah disimpan di dalam peti besi Pegawai Pengawal atau pegawai yang diberi kuasa. Penyimpanan dalam hal seperti itu hendaklah diakuterima dengan surat mengikut bentuk yang ditetapkan dan hendaklah direkodkan di dalam daftar kunci. Kunci ganti boleh diambil dengan kebenaran bertulis Pegawai Pengawal.'),
(1561, 'ATPK', 'C', '140', 'Suatu rekod hendaklah diselenggara menunjukkan peti besi Pegawai Pengawal atau pegawai yang diberi kuasa dalam mana kunci itu disimpan, dengan rujukan mengenai surat penyimpanan.'),
(1562, 'ATPK', 'C', '141', 'Surat akuan penyimpanan bagi kunci ganti hendaklah disimpan di suatu tempat yang selamat selain daripada tempat peti  besi itu disimpan.'),
(1563, 'ATPK', 'C', '142', 'Sekurang-kurangnya sekali setahun sampul berlakri hendaklah diambil balik. Kunci ganti itu hendaklah digunakan dan kunci yang digunakan sebelumnya hendaklah disimpan mengikut cara yang diperuntukkan di bawah Tatacara 140 hingga 142. Dengan demikian kunci asal dan kunci pendua hendaklah digunakan berselang tahun.'),
(1564, 'ATPK', 'C', '143', 'Pegawai yang mengambil alih tugas menjaga peti besi yang digunakan bagi penyimpanan aset Majlis, barang-barang, suratan atau barang berharga yang lain hendaklah berpuashati bahawa kunci ganti, jika ada, adalah disimpan mengikut cara yang diperuntukkan. Mereka juga hendaklah menandatangani rekod seperti yang tersebut dalam Tatacara 140.'),
(1565, 'ATPK', 'C', '144', 'Penjagaan selamat wang dalam perjalanan antara suatu pejabat Majlis dan bank atau antara pejabat dan tempat memungut adalah menjadi tanggungjawab pegawai yang berkenaan.'),
(1566, 'ATPK', 'C', '145', 'Bagi maksud perlindungan Takaful, wang dalam perjalanan tidak boleh melebihi jumlah wang yang dihadkan.'),
(1567, 'ATPK', 'C', '146', 'Wang yang hendak dibawa ke dalam bank atau dari bank ke pejabat Majlis atau ke tempat-tempat lain hendaklah dibawa oleh seorang pegawai yang diberikuasa dan ditemani oleh seorang pegawai yang lain atau di mana sesuai ditemani oleh dua pegawai keselamatan.'),
(1568, 'ATPK', 'C', '147', 'Wang yang hendak dimasuk ke dalam bank hendaklah dibawa terus ke bank berkenaan dan tidak dibawa ke tempat-tempat lain terlebih dahulu. Bagi maksud ini, perjalanan wang ialah dari pejabat Majlis ke bank berkenaan dan dari bank ke pejabat Majlis sahaja.'),
(1569, 'ATPK', 'C', '148', 'Pegawai Pengawal atau pegawai yang bertanggungjawab hendaklah mendapatkan perlindungan Takaful wang dalam perjalanan dan menentukan had jumlah wang yang diberi perlindungan dari semasa ke semasa untuk semua pejabat Majlis.'),
(1570, 'ATPK', 'C', '148A', 'Pengkelasan akaun Pendapatan dan Perbelanjaan hendaklah mengikut Anggaran yang diluluskan oleh Majlis.'),
(1571, 'ATPK', 'C', '148B', 'Rekod dalam lejar tunai hendaklah dibuat mengikut susunan berlakunya urusniaga-urusniaga melazimkan rekod itu. Akauntan Majlis adalah bertanggungjawab menentukan bahawa pada penghujung setiap hari bekerja baki bank tidak terlebih dikeluarkan.'),
(1572, 'ATPK', 'C', '148C', 'Pada penghujung setiap bulan urusniaga bank yang telah direkodkan di dalam lejar tunai hendaklah disemak dengan urusniaga yang tercatat di penyata bank dan suatu penyata penyesuaian hendaklah disediakan dan ditandatangani oleh Akauntan Majlis dalam tempoh (2) minggu selepas akhir tiap-tiap satu (1) bulan.'),
(1573, 'ATPK', 'C', '148D(a)', 'Pindaan atau pemadaman angka tidak boleh dibuat dalam apa juga keadaan pada lejar tunai, baucar atau dokumen akaun yang lain. Pembayaran haruslah ditolak sekiranya terdapat pindaan dan pemadaman didokumen tersebut.'),
(1574, 'ATPK', 'C', '148D (b)', 'Sekiranya apa-apa pembetulan diperlukan kecuali bagi baucar bayaran, angka asal hendaklah digariskan dengan satu garisan sahaja untuk menunjukkan dengan terang angka asal itu dan angka yang sebenarnya dituliskan di atas angka asal. Pembetulan ini hendaklah ditandatangani oleh Akauntan Majlis. Kaedah yang sama hendaklah diikuti bagi semua pembetulan yang dibuat di dalam buku akaun, daftar sewa dan kadar.'),
(1575, 'ATPK', 'C', '148E(a)\\', 'Semua buku dan rekod kewangan dan perakaunan termasuk rekod di dalam bentuk media elektronik Majlis hendaklah disimpan dengan cermat sekurang-kurangnya selama tempoh yang ditetapkan di dalam Lampiran 2 selepas pengauditan akaun tahun berkenaan oleh Ketua Audit Negara. Sebelum buku dan rekod dimusnahkan, kebenaran hendaklah diperolehi terlebih dahulu daripada Ketua Arkib Negara dan Ketua Audit Negara sebelum memohon kelulusan daripada Majlis.'),
(1576, 'ATPK', 'C', '148E (b)', 'Bagi dokumen-dokumen yang tidak dicatatkan dalam Lampiran 2, kelulusan Arkib Negara dan Majlis hendaklah diperolehi terlebih dahulu sebelum ianya dimusnahkan. Permintaan bagi kesemua kelulusan tersebut hendaklah disalinkan kepada Ketua Audit Negara.'),
(1577, 'ATPK', 'C', '149', 'Pegawai Pengawal hendaklah menentukan bahawa peraturan dibuat bagi mengendali dengan sepatutnya setiap satu Akaun Amanah atau Arahan Amanah yang diluluskan oleh Majlis.'),
(1578, 'ATPK', 'C', '150', 'Arahan Amanah hendaklah disediakan seperti berikut (jika berkenaan) :\r\n(a) Permohonan;\r\n(b) Persetujuan;\r\n(c) Nama dan tujuan;\r\n(d) Kaedah pengendalian;\r\n(e) Pegawai Pengawal;\r\n(f) Tanggungjawab MAIS;\r\n(g) Sumber Kewangan;\r\n(h) Penutupan;\r\n(i) Kuatkuasa;\r\n(j) Jawatankuasa Pentadbiran Akaun;\r\n(k) Lain-lain perkara.'),
(1579, 'ATPK', 'C', '151', 'Sekiranya Akaun Amanah telah mencapai maksud penubuhannya, tidak dikehendaki lagi, mencukupi tempoh hayat yang ditetapkan (jika ada) ataupun yang tidak aktif lagi untuk tempoh setahun, maka hendaklah ditutup. Pihak urusetia (pemohon) hendaklah membuat surat permohonan penutupan Akaun Amanah ini selepas ianya telah mencapai maksud penubuhannya.'),
(1580, 'ATPK', 'C', '152', 'Akaun-akaun yang betul dan rekod-rekod lain yang berkenaan dengan pengendalian akaun amanah hendaklah disimpan. Selepas 31hb Disember setiap tahun suatu penyata yang menunjukkan baki permulaan, terimaan dan bayaran, dan baki penutup bagi tahun sebelumnya hendaklah disediakan.'),
(1581, 'ATPK', 'C', '153', 'Pegawai Pengawal adalah bertanggungjawab atas penyelenggaraan akaun deposit Majlis di bawah jagaannya. Akaun deposit hendaklah dibuka mengikut jenis deposit yang diterima. Jika sekiranya banyak bilangan deposit yang sama jenisnya diterima, rekod kecil hendaklah diselenggara bagi setiap satu deposit.'),
(1582, 'ATPK', 'C', '154', 'Apabila sesuatu deposit diterima Resit Rasmi hendaklah diberi kepada pendeposit. Pendeposit hendaklah dinasihatkan supaya menyimpan Resit Rasmi itu kerana ia dikehendaki menyerahkannya apabila deposit diambil balik.'),
(1583, 'ATPK', 'C', '155', 'Dimansuhkan'),
(1584, 'ATPK', 'C', '156', 'Apabila seseorang hendak mengambil balik sesuatu deposit atau sebahagian sesuatu deposit, Akauntan Majlis mestilah berpuashati bahawa:'),
(1585, 'ATPK', 'C', '156 (a)', 'Orang itu berhak menerima wang itu; dan'),
(1586, 'ATPK', 'C', '156 (b)', 'Amaun itu ada di dalam deposit setelah merujuk kepada lejar deposit.'),
(1587, 'ATPK', 'C', '157', 'Baucar bagi pembayaran balik deposit hendaklah menyebut nombor dan tarikh resit bagi deposit yang berkenaan. Resit asal hendaklah dikembarkan bersama. Jika resit hilang, satu perakuan berkanun hendaklah dibuat dan dikembarkan.'),
(1588, 'ATPK', 'C', '158', 'Deposit hanya boleh dipulangkan balik setelah tujuannya selesai.'),
(1589, 'ATPK', 'C', '159', 'Baki dalam rekod kecil hendaklah dijumlah dan disamakan dengan akaun utama pada setiap bulan. Senarai baki deposit hendaklah disahkan oleh Akauntan Majlis pada 31hb Disember setiap tahun.'),
(1590, 'ATPK', 'C', '160', 'Pegawai yang bertanggungjawab hendaklah menyemak catatan dalam lejar deposit dan memastikan tiada perbezaan sekurang-kurangnya tiga (3) bulan sekali.'),
(1591, 'ATPK', 'C', '161 (a)', 'Jika tiada sebab-sebab khas pada sebaliknya, deposit yang tidak dituntut selepas tempoh 2 tahun dari tarikh deposit boleh dikembalikan, hendaklah diberitahu dalam Warta Kerajaan.'),
(1592, 'ATPK', 'C', '161 (b)', 'Jika deposit itu masih tidak dituntut dalam masa 6 bulan selepas pemberitahuan itu, maka ia hendaklah dikredit kepada hasil. Adalah menjadi kewajipan Akauntan Majlis bahawa pemberitahuan itu diwartakan.'),
(1593, 'ATPK', 'C', '162', 'Jika didapati bahawa Akauntan tidak menjalankan kewajipannya dengan secukupnya perkara ini hendaklah dilaporkan oleh Pegawai Pengawal kepada Majlis.'),
(1594, 'ATPK', 'C', '163', 'Kuasa Majlis\r\nMajlis adalah menjadi pihak berkuasa bagi pengawalan, penyeliaan dan penyelarasan berkenaan dengan pembelian, penyimpanan, pembahagian, penggunaan, pemindahan antara jabatan, bahagian dan daerah dan pelupusan barang-barang yang diperolehi dari Kumpulan wang Majlis.'),
(1595, 'ATPK', 'C', '164', 'Tanpa menghadkan kuasa am yang diberi sebelum daripada ini, Majlis adalah mempunyai kuasa untuk:'),
(1596, 'ATPK', 'C', '164 (a)', 'Menentukan kriteria syarat-syarat pendaftaran dan mendaftarkan individu, firma, syarikat atau pertubuhan bagi membolehkan mereka berurus niaga dengan Majlis;'),
(1597, 'ATPK', 'C', '164 (b)', 'Membeli atau memperoleh dengan cara yang paling menguntungkan, semua bekalan, perkhidmatan dan kerja yang diperlukan oleh Majlis;'),
(1598, 'ATPK', 'C', '164 (c)', 'Membuat bayaran bagi bekalan, perkhidmatan dan kerja yang diperolehi;'),
(1599, 'ATPK', 'C', '164 (d)', 'Membuat kontrak berdasarkan apa-apa syarat dan bagi apa-apa tempoh yang difikirkannya paling menguntungkan;'),
(1600, 'ATPK', 'C', '164 (e)', 'Memegang, mengurus, mengendali, menyiap, memasang, mentauliah, mengangkut, membaiki, menyelenggara dan menjaga barang, dan memperolehi aset ( alih, tak alih dan hidup) yang perlu bagi maksud itu;'),
(1601, 'ATPK', 'C', '164 (f)', 'Menjual, menukar, memberi pinjaman, mengarah atau memberi kuasa, pemindahan antara jabatan, bahagian dan daerah, atau secara lain melupus atau menguruskan barang-barang atas apa-apa had dan tertakluk kepada apa-apa syarat yang difikirkannya perlu;'),
(1602, 'ATPK', 'C', '164 (g)', 'Mengeluarkan arahan  bagi pengawalan, penguatkuasaan penyeliaan, dan penyelarasan yang sepatutnya mengenai pembelian, penyimpanan, pembahagian, penggunaan, pemindahan antara jabatan, bahagian daerah dan pelupusan barang-barang;'),
(1603, 'ATPK', 'C', '164 (h)', 'Menjalankan pemeriksaan atas kerja-kerja pembelian, penyimpanan, penyeliaan, penyelenggaraan, pembahagian, penggunaan, pemindahan antara agensi dan pelupusan barang-barang;'),
(1604, 'ATPK', 'C', '164 (i)', 'Menjalankan penyiasatan ke atas  kerja-kerja menguruskan pembelian, penyimpanan, pembahagian, penggunaan, pemindahan antara jabatan, bahagian dan daerah dan pelupusan aset dengan  lebih cekap dan berekonomi;'),
(1605, 'ATPK', 'C', '164 (j)', 'Mengambil tindakan termasuk menggantung atau membatalkan pendaftaran ke atas individu, firma, syarikat dan pertubuhan serta perunding yang didapati oleh Majlis telah bersalah kerana keinggkaran dalam melaksanakan sesuatu kontrak.'),
(1606, 'ATPK', 'C', '164A', 'Pegawai Pengawal adalah bertanggungjawab sendiri termasuk bertanggungjawab membayar balik jika berlaku kerugian berhubung dengan penyeliaan, penyenggaraan dan pengawalan am bekalan, perkhidmatan dan kerja yang diterima atau dipegang yang perbelanjaannya dikawal olehnya.'),
(1607, 'ATPK', 'C', '164B', 'Pegawai Awam adalah bertanggungjawab sendiri termasuk bertanggungjawab membayar balik jika berlaku kerugian berhubung dengan bekalan, perkhidmatan dan kerja yang diterima dibawah kawalannya atau simpanannya.'),
(1608, 'ATPK', 'C', '165', 'Pegawai yang diberi kuasa boleh mengizinkan membuat perolehan yang mana peruntukan telah disediakan dalam anggaran yang diluluskan. Sebarang pengecualian daripada mana-mana peraturan yang ditetapkan dalam Peraturan Kewangan yang berkuatkuasa hendaklah mendapat kelulusan daripada Majlis.'),
(1609, 'ATPK', 'C', '165A', 'Majlis hendaklah menentukan di peringkat perancangan perolehan sama ada perolehan dibuat secara manual sahaja atau juga melalui sistem elektronik. Perolehan melalui sistem elektronik hendaklah mematuhi peraturan yang telah ditetapkan dan tatacara pelaksanaannya dikeluarkan melalui Surat Pekeliling dari masa ke semasa.'),
(1610, 'ATPK', 'C', '166', 'Adalah menjadi kewajipan setiap pegawai yang menguruskan perolehan membuat kajian pasaran yang berpatutan supaya perolehan itu adalah yang paling sesuai dan menguntungkan. Kajian hendaklah mengambil kira aspek kualiti, harga, kegunaan, kos penyenggaraan dan faktor-faktor lain yang berkaitan. Kos pembelian adalah disifatkan meliputi duti kastam, tambang, takaful, pemasangan dan kos-kos lain yang berkaitan.'),
(1611, 'ATPK', 'C', '166A', 'Bahan, barangan dan perkhidmatan tempatan hendaklah digunakan sepenuhnya oleh Majlis dalam perolehannya. Perolehan melalui import hanya akan dipertimbangkan setelah dipastikan ianya tidak boleh diperolehi secara tempatan. Pegawai Pengawal adalah bertanggungjawab sepenuhnya memantau dan memastikan penggunaan bahan, barangan dan perkhidmatan tempatan dalam perolehan Majlis.'),
(1612, 'ATPK', 'C', '168', 'Pembelian setiap jenis item yang melibatkan jumlah perbelanjaan tahunan lebih daripada RM50,000 tetapi tidak lebih daripada RM500,000 boleh dibuat dengan mempelawa sebut harga daripada sekurang-kurangnya lima (5) pembekal yang berdaftar dengan Majlis dengan menggunakan Lampiran 5.'),
(1613, 'ATPK', 'C', '168 (a)', 'Bagi pembelian setiap jenis yang bernilai melebihi RM50,000 hingga RM100,000 hendaklah dipelawa secara sebut harga dikalangan pembekal bertaraf Bumiputera yang berdaftar dengan Kementerian Kewangan.'),
(1614, 'ATPK', 'C', '168 (b)', 'Bagi pembelian setiap jenis item yang bernilai RM100,000 hingga RM500,000 hendaklah dipelawa secara sebut harga dikalangan pembekal yang berdaftar dengan Majlis dan  Kementerian Kewangan.'),
(1615, 'ATPK', 'C', '168', 'Jika bilangan sebut harga yang dikehendaki tidak boleh diperolehi,  Jawantankuasa Sebut Harga sebagaimana yang ditakrif dalam Tatacara 170, mestilah berpuashati bahawa bilangan pembekal yang ada adalah kurang dari lima.'),
(1616, 'ATPK', 'C', '169 (a)', 'Satu kaedah yang seragam hendaklah diamalkan kepada penyebutharga yang telah dikenalpasti bagi sesuatu urusan sebut harga untuk memastikan prinsip kesaksamaan dan adil dilaksanakan ke atas penyebut harga.Majlis hendaklah menetapkan kaedah tawaran sebut harga dari pembekal sama ada melalui pos berdaftar, pos laju atau serahan tangan.'),
(1617, 'ATPK', 'C', '169 (b)', 'Majlis hendaklah mempamerkan notis tawaran sebut harga di papan kenyataan awam Majlis, portal MAIS selain daripada mempelawa sebut harga kepada penyebut harga yang telah dikenalpasti. Ini adalah untuk memberi peluang kepada penyebut harga lain yang berkelayakan dan berminat untuk mengambil bahagian. Pemberitahuan tersebut hendaklah dipamerkan pada tarikh yang sama pelawaan dibuat kepada penyebut harga yang dikenalpasti.'),
(1618, 'ATPK', 'C', '169 (c)', '"Pemberitahuan sebut harga di papan kenyataan awam hendaklah menyatakan:\r\n(i)Jabatan/Bahagian yang memanggil sebut harga;"\r\n(ii) Tajuk sebut harga;\r\n(iii) Syarat kelayakan penyebut harga;\r\n(iv) Bidang/kelas kepala pendaftaran; dan\r\n(v) Tempat, tarikh dan waktu sebut harga diterima dan ditutup.\r\n'),
(1619, 'ATPK', 'C', '169 (d)', '(d) Tempoh sebut harga hendaklah sekurang-kurangnya tujuh (7) hari berturut-turut dan pemberitahuan itu hendaklah sentiasa berada di papan kenyataan sepanjang tempoh tersebut.'),
(1620, 'ATPK', 'C', '169 (e)', '(e) Sebut harga hendaklah disampaikan, diterima, dibuka dan dijadualkan dibuat bagi tender mengikut Peraturan Kewangan Tatacara 207 sehingga 219.'),
(1621, 'ATPK', 'C', '169A', 'Jawatankuasa Sebut Harga hendaklah terdiri daripada mereka yang dilantik secara bertulis oleh Pegawai Pengawal, seperti yang berikut :\r\nPengerusi - Setiausaha Majlis\r\nAhli - Dua Orang Ketua Bahagian\r\nSetiausaha - Bahagian/Unit/Seksyen Perolehan.'),
(1622, 'ATPK', 'C', '169B', 'Bidang kuasa Jawatankuasa Sebut Harga ialah bagi menimbang dan membuat keputusan mengenai sebut harga.'),
(1623, 'ATPK', 'C', '170 (a)', 'Jawatankuasa Sebut harga hendaklah menerima tawaran yang paling baik dari segi harga dan kualiti mengikut keperluan Majlis dan spesifikasi sebut harga. Jika tawaran yang terendah tidak disetuju terima, sebab-sebab penolakan tawaran hendaklah dinyatakan dalam keputusan sebut harga.'),
(1624, 'ATPK', 'C', '170 (b)', 'Jawatankuasa Sebut harga hanya boleh mempertimbang dan menyetuju terima sebut harga dengan syarat keputusan yang dibuat adalah sebulat suara dan tidak melebihi had nilai sebut harga. Keputusan yang tidak sebulat suara dan /atau melebihi had nilai sebut harga hendaklah dikemukakan kepada Jawatankuasa yang lebih tinggi untuk keputusan. Pertimbangan sebut harga secara edaran adalah tidak dibenarkan.'),
(1625, 'ATPK', 'C', '170 (c)', 'Satu rekod yang lengkap mengenai pertimbangan dan keputusan sebut harga hendaklah disimpan oleh Urusetia untuk tujuan audit.'),
(1626, 'ATPK', 'C', '171', 'Tender terbuka hendaklah dipelawa untuk Bekalan, Perkhidmatan dan Kerja yang nilainya melebihi RM500,000 setahun bagi setiap jenis item.'),
(1627, 'ATPK', 'C', '172 (a)', 'Apabila mempelawa tender, publisiti yang seluas-luasnya hendaklah dibuat supaya semua pembekal yang bona fide berpeluang untuk menyertai tender dan dengan demikian membolehkan Majlis mendapat tawaran yang terbaik.'),
(1628, 'ATPK', 'C', '172 (b)', 'Tender tempatan hendaklah diiklankan dalam sekurang-kurangnya satu (1) akbar harian utama Bahasa Melayu. Walaubagaimanapun untuk tender antarabangsa sekurang-kurangnya di dalam dua (2) akhbar harian utama, dimana satu (1) Bahasa Melayu dan Satu (1) Bahasa Inggeris. Majlis hendaklah juga  mengiklankan tawaran tender dalam laman webnya.'),
(1629, 'ATPK', 'C', '172 (c)', 'Walaupun publisiti meluas digalakkan bagi urusan tender, pertimbangan sewajarnya mengenai kos iklan hendaklah di bawah satu-satu bidang adalah diketahui tidak lebih lima (5) pembekal, iklan tidak perlu dibuat. Dalam hal demikian kelulusan Pegawai Pengawal hendaklah diperolehi terlebih dahulu. Setelah mendapat kelulusan, pemberitahuan tender kepada pembekal yang diluluskan hendaklah dihantar dengan surat berdaftar atau pos laju.'),
(1630, 'ATPK', 'C', '173A (a)', 'Pembelian setiap jenis item yang melibatkan jumlah perbelanjaan tahunan tidak lebih daripada RM50,000.00 boleh dibuat dengan membuat pesanan terus kepada  pembekal yang berdaftar dengan MAIS yang diketahui sentiasa mengenakan harga yang berpatutan. Walaubagaimanapun Majlis hendaklah mengutamakan pembekal yang bertaraf bumiputra.'),
(1631, 'ATPK', 'C', '173 (b)', 'Kaedah pembelian terus hendaklah mengikut Manual Perolehan MAIS yang telah diluluskan oleh Majlis.'),
(1632, 'ATPK', 'C', '173B', 'Pengecualian dalam Peraturan Kewangan 168, 171, 172, 173A, 186 dan187 adalah dibenarkan dalam hal darurat. Perolehan darurat bermaksud perolehan yang perlu dibuat segera atau serta merta di mana kelewatan perolehan akan memudaratkan dan menjejaskan perkhidmatan dan kepentingan awam. Dalam hal demikian perolehan hendaklah dihadkan kepada kuantiti bekalan yang mencukupi dan skop perkhidmatan serta kerja setakat menampung keperluan darurat yang berkenaan. Laporan perlunya perolehan darurat itu dibuat hendaklah dikemukakan kepada Pegawai Pengawal dalam tempoh satu (1) bulan dari tarikh perolehan untuk tujuan penyiasatan dan audit.'),
(1633, 'ATPK', 'C', '174', 'Dalam keadaan tertentu, setelah Jawatankuasa yang berkenaan berpuas hati bolehlah ia menawarkan sesuatu kerja atau pembelian itu secara rundingan terus.'),
(1634, 'ATPK', 'C', '175', 'Majlis hendaklah menggunakan syarat-syarat am tender sebagaimana yang telah ditentukan. Syarat-syarat tertentu boleh ditambah asalkan ianya tidak bercanggah dengan syarat-syarat am'),
(1635, 'ATPK', 'C', '176', 'Apabila tender atau sebut harga dipelawa, spesifikasinya hendaklah jelas supaya petender mendapat gambaran yang jelas mengenai kehendak-kehendak Majlis. Contohnya perolehan Kain Sejadah (Sembahyang), spesifikasinya hendaklah mengandungi butir-butir jenis kain samada daripada kapas atau wool atau campuran kedua-duanya, ukuran dan beratnya, warnanya dan maklumat lain yang berkaitan supaya pembekal tidak ragu-ragu.'),
(1636, 'ATPK', 'C', '177', 'Sekiranya Majlis ingin membeli kelengkapan atau jentera di mana spesifikasi penuh tidak dapat dikemukakan, butir-butir lain hendaklah diberikan kepada pembekal supaya pembekal ada satu asas yang saksama untuk menyertai tender. Misalnya jika suatu kelengkapan dikehendaki, keupayaan minimum kelengkapan itu perlu dinyatakan dan terpulanglah kepada pembekal untuk memberi butir-butir kelengkapan yang mereka tawarkan beserta dengan harga.'),
(1637, 'ATPK', 'C', '178', 'Nama dagangan, jenama atau buatan tidak boleh digunakan dalam spesifikasi tender. Jika ini tidak dapat dielakkan, ungkapan atau persamaan hendaklah ditulis selepas nama dagangan, jenama atau buatan di atas suratan tender itu.'),
(1638, 'ATPK', 'C', '180 (a)', 'Pembekal atau kontraktor yang berdaftar dengan Kementerian Kewangan atau Pusat Khidmat Kontraktor seperti yang diperuntukkan di bawah Peraturan Kewangan Tatacara 189 dan 190 adalah dikecualikan daripada deposit tender bagi tender tempatan dan antarabangsa.'),
(1639, 'ATPK', 'C', '180 (c)', 'Bagi petender tempatan yang telah mendapat pengecualian pendaftaran, deposit tender hendaklah dikenakan sebanyak dua setengah peratus (2.5%) daripada tawarannya atau maksimum RM10,000. Deposit tender hendaklah dalam bentuk draf bank atau jaminan bank.'),
(1640, 'ATPK', 'C', '180 (d)', 'Sekiranya pembekal atau kontraktor menolak tawaran setelah tawaran dibuat kepadanya dalam tempoh sah laku tawaran berkenaan, tindakan tatatertib akan diambil terhadap mereka. Majlis hendaklah melaporkan kesalahan pembekal atau kontraktor kepada Pusat Khidmat Kontraktor untuk diambil tindakan tatatertib.'),
(1641, 'ATPK', 'C', '180A (a)', 'Dalam pelawaan tender hendaklah disyaratkan bahawa deposit tendernya akan dirampas jika petender enggan menandatangani sesuatu kontrak selepas tendernya disetuju terima atau menarik balik tendernya sebelum keputusan dibuat.'),
(1642, 'ATPK', 'C', '180 (b)', 'Deposit tender bagi petender yang tidak berjaya hendaklah dikembalikan setelah tender itu disetuju terima oleh petender yang berjaya atau selepas tamatnya tarikh sah laku tender. Deposit petender yang berjaya hendaklah dikembalikan selepas Bon Pelaksanaan diterima.'),
(1643, 'ATPK', 'C', '180B (a)', 'Sebelum sesuatu tender atau sebut harga disetuju terima, Pegawai Pengawal hendaklah berpuas hati bahawa syarat-syarat perolehan itu telah diputuskan dan dinyatakan dengan jelas. Perhatian khusus hendaklah diberikan kepada maklumat penuh mengenai perolehan, harga seunit yang dipersetujui, kuantiti yang dikehendaki, tarikh, masa dan tempat serahan, belanja serahan, perkhidmatan lepas jualan, kemudahan mendapatkan alat ganti dan lain-lain yang berkenaan. Selepas setuju terima oleh pihak berkuasa yang berkenaan, satu kontrak formal hendaklah disempurnakan oleh Pegawai Pengawal.'),
(1644, 'ATPK', 'C', '180B (b)', 'Sekiranya nilai perolehan tidak melebihi RM50,000 di mana satu kontrak formal tidak sesuai dibuat adalah memadai bagi Majlis menggunakan borang untuk pesanan pembelian sahaja dengan memastikan kepentingan Majlis terjamin/terpelihara.'),
(1645, 'ATPK', 'C', '180B (c)', 'Bagi semua perolehan bekalan atau perkhidmatan bermasa kontrak formal hendaklah diadakan walaupun nilai perolehan kurang daripada RM50,000.'),
(1646, 'ATPK', 'C', '180B (d)', 'Pesanan Pembelian mestilah disediakan dalam tiga (3) salinan pada masa barang-barang dipesan:\r\n\r\n(i)Salinan asal dan pendua dihantar kepada pembekal atau kontraktor. Salinan asal pesanan hendaklah dikembalikan oleh pembekal atau kontraktor  dengan dikembarkan kepada bil atau invois. Pembayaran tidak boleh dibuat jika salinan asal Pesanan Pembelian tidak dikembarkan bil asal atau invois; dan\r\n(ii)Salinan kedua boleh disimpan oleh pembekal dan salinan ketiga hendaklah ditinggalkan dalam buku untuk rekod pejabat.\r\n\r\nBuku Pesanan Pembelian hendaklah disimpan di tempat yang berkunci. Dokumen yang disediakan melalui komputer juga hendaklah disimpan dengan selamat. Pesanan mestilah ditandatangani hanya oleh pegawai yang diberi kuasa untuk berbuat demikian oleh Pegawai Pengawal.\r\n'),
(1647, 'ATPK', 'C', '180B (e)', 'Sekiranya salinan asal Pesanan Pembelian hilang, pegawai Pengawal boleh mengeluarkan satu salinan yang diperakui benar bagi pesanan asal itu. Pegawai Pengawal mestilah mencatatkan pada salinan pejabat bahawa salinan asal telah hilang dan satu salinan yang diperakui telah dikeluarkan olehnya. Salinan yang diperakui itu hendaklah dikembarkan kepada bil untuk pembayaran dan suatu perakuan hendaklah diberi bahawa pesanan itu belum pernah dibayar sebelum itu.'),
(1648, 'ATPK', 'C', '182A (a)', 'Bon pelaksanaan hanya dikenakan bagi kontrak bekalan atau perkhidmatan yang melebihi RM50,000. Sebelum sesuatu kontrak bekalan atau perkhidmatan yang melebihi RM50,000 tetapi tidak melebihi RM500,000 disempurnakan, pembekal yang berjaya hendaklah memberi Bon Pelaksanaan sebanyak dua setengah peratus (2.5%) daripada nilai kontrak. Untuk kontrak yang melebihi RM500,000 pula, Bon Pelaksanaan yang diperlukan ialah sebanyak lima peratus (5%) daripada nilai kontrak. Bagi kontrak bekalan atau perkhidmatan bermasa yang berkuatkuasa selama dua (2) tahun atau lebih, kadar peratus dan pengiraan Bon Pelaksanaan hendaklah ditentukan mengikut harga anggaran kontrak bagi setahun sahaja dan tidak daripada keseluruhan harga kontrak. Walau bagaimanapun, Bon Pelaksanaan yang dikemukakan oleh pembekal hendaklah meliputi keseluruhan tempoh kontrak bermasa.'),
(1649, 'ATPK', 'C', '182A (b)', 'Bon Pelaksanaan ini hendaklah dalam bentuk tunai atau Bank Draf atau Jaminan Bank atau Jaminan Bank Islam atau Jaminan Syarikat Kewangan atau Jaminan Insurans atau Jaminan Takaful, dalam Ringgit Malaysia (RM) dari bank-bank atau institusi kewangan yang dibenarkan oleh Majlis.'),
(1650, 'ATPK', 'C', '182A (c)', 'Bon Pelaksanaan ini akan dikembalikan sesudah kontrak disempurnakan dengan memuaskan.'),
(1651, 'ATPK', 'C', '183 (a)', 'Pegawai yang menandatangani sesuatu Borang Pesanan Pembelian hendaklah mencatatkan jumlah harga dengan perkataan-perkataan selepas sahaja butiran akhir dan hendaklah menandatangani ringkas catatan itu. Sebelum pembayaran dibuat, pesanan asal hendaklah dibandingkan dengan salinan pejabat. Nombor pesanan hendaklah dicatatkan pada baucar bayaran.'),
(1652, 'ATPK', 'C', '183 (b)', 'Bekalan yang diterima hendaklah direkod mengikut Tatacara Pengurusan Aset dan Pengurusan Stor semasa yang berkuatkuasa.'),
(1653, 'ATPK', 'C', '184', 'Sekiranya didapati lebih menguntungkan dan menjimatkan, pembelian bolehlah dibuat dibawah pembekal di bawah kontrak pusat Kerajaan atau Jabatan-jabatan lain yang banyak memegang barang-barang tersebut.'),
(1654, 'ATPK', 'C', '185 (a)', 'Majlis boleh menggunakan Jadual Kadar Kejuruteraan Awam dan Bangunan yang diluluskan oleh Perbendaharaan Malaysia dan juga menjalankan kerja-kerja mengikut jadual yang diluluskan itu dengan cara  pesanan kerja atau inden.'),
(1655, 'ATPK', 'C', '185 (b)', 'Pesanan kerja atau inden yang dikeluarkan mengikut jadual kadar  Kejuruteraan Awam dan Bangunan  tidak boleh melebihi RM100,000. Pegawai Pengawal boleh mewakili kuasa kepada pegawai yang sesuai bagi menandatangani pesanan kerja atau inden itu setakat  RM100,000.'),
(1656, 'ATPK', 'C', '185A (a)', 'Majlis boleh menggunakan Jadual Kadar Kerja Elektrik yang diluluskan oleh Perbendaharaan Malaysia dan juga menjalankan kerja-kerja mengikut jadual yang diluluskan itu dengan cara  pesanan kerja atau inden.'),
(1657, 'ATPK', 'C', '185A (b)', 'Pesanan kerja atau inden yang dikeluarkan mengikut jadual kadar kerja elektrik tidak boleh melebihi RM50,000. Pegawai Pengawal boleh mewakili kuasa kepada pegawai yang sesuai bagi menandatangani pesanan kerja atau inden itu setakat  RM50,000.'),
(1658, 'ATPK', 'C', '186(a)', 'Bagi kerja-kerja dan perkhidmatan yang tidak melebihi RM10,000 yang telah berdaftar dengan Pusat Khidmat Kontraktor dan Majlis boleh dibuat lantikan terus.'),
(1659, 'ATPK', 'C', '186(b)', 'Bagi kerja-kerja dan perkhidmatan daripada RM10,001 hingga RM20,000 perlu mempelawa sekurang-kurangnya tiga (3) syarikat kontraktor hendaklah dipelawa.'),
(1660, 'ATPK', 'C', '186 ( c)', 'Bagi kerja-kerja dan perkhidmatan daripada RM20,001 hingga RM500,000. \r\nKaedah sebut harga adalah  mengikut Manual Perolehan Majlis.'),
(1661, 'ATPK', 'C', '187', 'Tender terbuka hendaklah dipelawa untuk kerja dan perkhidmatan yang nilainya melebihi RM500,000 setahun bagi setiap jenis item dan hendaklah diproses mengikut Manual Perolehan Majlis. Adalah dilarang memecah kecilkan nilai kerja bagi mengelakkan pelawaan tender.'),
(1662, 'ATPK', 'C', '188', 'Dalam sesuatu hal Majlis boleh membenarkan kerja-kerja dan perkhidmatan mendapatkan nasihat teknikal daripada firma perunding atau pengamal persendirian yang berkenaan dengan kerja-kerja itu mengikut Tatacara 192. Majlis hendaklah dirujuk jika nasihat firma perunding atau pengamal persendirian berkenaan mahu diketepikan.'),
(1663, 'ATPK', 'C', '189', 'Daftar kontraktor yang diselenggara oleh Kerajaan bolehlah diterima pakai oleh Majlis.'),
(1664, 'ATPK', 'C', '190 (c)', 'Majlis dikehendaki mempelawa hanya kontraktor kerja awam dan kerja elektrik yang berdaftar dengan PKK di bawah kepala dan sub kepala yang berkaitan dan CIDB dalam gred dan kategori yang berkaitan untuk mengambil bahagian di dalam urusan perolehan berkenaan.'),
(1665, 'ATPK', 'C', '191 (a)', 'Perunding Pembangunan Fizikal.\r\n(i)Arkitek, kejuruteraan awam dan struktur, kejuruteraan mekanikal dan elektrikal, ukur bahan, perancang bandar dan desa, landskap, hiasan dalaman, akustik, pengurusan projek, kajian alam sekitar dan lain-lain perkhidmatan teknikal  yang berkaitan dengan kejuruteraan dan /atau pembinaan;\r\n(ii)Kerja-kerja ukur tanah dan kerja ukur khusus sepeti hydrography, aerial photogrammetry, remote sensing dan lain-lain;\r\n(iii)Perkhidmatan penialian fizikal seperti penilaian harta, audit & assessment dan lain-lain;\r\n(iv) Kajian-kajian yang berkaitan dengan kerja-kerja kejuruteraan dan/atau pembinaan.'),
(1666, 'ATPK', 'C', '191 (b)', 'Perunding Bukan Fizikal.\r\n(i) Kajian kemungkinan, kajian ekonomi, kajian penswastaan dan kajian-kajian lain;\r\n(ii) Perkhidmatan pengurusan sumber manusia, kewangan dan perakaunan, percukaian, pengurusan kualiti, kerja-kerja audit pengurusan, teknologi maklumat dan komunikasi dan bidang-bidang pengurusan lain;\r\n(iii) Perkhidmatan perundangan.'),
(1667, 'ATPK', 'C', '191 (c )', 'Perkhidmatan perunding lain mengikut kategori yang diperlukan oleh Majlis dan ditentukan dari semasa kesemasa.'),
(1668, 'ATPK', 'C', '191A', 'Perkhidmatan bukan perunding\r\n(a) Pengendalian kursus dan latihan.\r\n(b) Penyenggaraan dan pembaikan.\r\n(c) Pencucian dan pembersihan.\r\n(d) Penyewaan dan pengurusan bangunan.\r\n(e) Kawalan keselamatan.\r\n(f) Perkhidmatan-perkhidmatan lain.'),
(1669, 'ATPK', 'C', '192', 'Apabila perlantikan perunding, Perkhidmatan Bukan Perunding  dan Pengamal Persendirian  telah diluluskan oleh Majlis. Satu  perjanjian hendaklah dibuat antara kedua-dua pihak. Dalam perjanjian tersebut, hendaklah dinyatakan dengan jelas, antara lain, perkara-perkara seperti skop kerja, kos perkhidmatan, syarat-syarat pembayaran dan butiran-butiran yang berkaitan. Proses  Pelantikan Perunding, Perkhidmatan Bukan Perunding  dan Pengamal Persendirian hendaklah merujuk kepada Manual Perolehan yang telah diluluskan oleh pihak Majlis.'),
(1670, 'ATPK', 'C', '192A', 'Bagi projek atau kajian yang diuruskan oleh perunding, majlis hendaklah memastikan peraturan-peraturan perolehan yang sedang berkuatkuasa dipatuhi sepenuhnya oleh perunding.'),
(1671, 'ATPK', 'C', '193', 'Perunding dan Pengamal Persendirian itu hendaklah diminta menyediakan Pelan Lakar dan Pelan Susun Atur Am dan menghantar pelan-pelan itu kepada Pegawai Pengawal bersama dengan satu anggaran permulaan. Ringkasan Perbutiran Permulaan hendaklah meliputi jumlah belanja semua kerja yang dicadangkan termasuk bayaran professional dan semua perbelanjaan pemasangan ala-alat yang perlu. Butir-butir ini, hendaklah dihantar kepada Setiausaha Majlis, Pelan Lakar dan Pelan Susun Atur mana yang berkenaan hendaklah dihantar kepada Pentadbir Tanah Daerah dan Pihak Berkuasa Tempatan, bagi mendapatkan kelulusan. Selepas kelulusan diperolehi, perunding atau pengamal persendirian hendaklah diminta membuat pelan kerja.'),
(1672, 'ATPK', 'C', '194', 'Dalam menyediakan dokumen tender, perunding atau pengamal persendirian hendaklah diminta menunjukkan dengan jelas butir Kos Utama dan Peruntukan Sementara. Butiran Kos Utama itu hendaklah dianggar dengan tepat dan, jika boleh, hendaklah ditender serentak, jika ianya tidak ditender terdahulu daripada kerja induk. Jika butir-butir ada, tender hendaklah dipelawa bagi kerja-kerja khas yang diliputi oleh Peruntukan Sementara dalam Jadual Bahan. Bayaran professional, gaji kakitangan penyeliaan dan luar jangka hendaklah ditunjukkan sebagai butiran yang berasingan dalam Ringkasan Perbutiran.'),
(1673, 'ATPK', 'C', '195', 'Dokumen tender hendaklah disediakan oleh perunding atau Pengamal Persendirian dengan menggunakan Borang Kontrak yang berkenaan dan Borang Tender yang berkaitan. Tender hendaklah dipelawa daripada Kontraktor Berdaftar mengikut Tatacara 189. Dokumen hendaklah diterima di Pejabat Majlis dan hendaklah dibuka dan dijadualkan mengikut amalan biasa oleh Jawatankuasa Membuka Tender yang berkenaan mengikut Tatacara 214. Dokumen tender itu hendaklah kemudiannya dihantar kepada Perunding atau Pengamal Persendirian untuk pemeriksaan dan ulasan.'),
(1674, 'ATPK', 'C', '196', 'Perunding atau Pengamal Persendirian hendaklah kemudiannya menyampaikan kepada  Pegawai Pengawal dokumen tender itu, Jadual Tender dan syornya mengenai tender itu. Dokumen ini serta dengan syor-syor  Pegawai Pengawal hendaklah disampaikan kepada Jawatankuasa Tender Majlis yang berkenaan. Syor-syor  Jawatankuasa  itu hendaklah dikemukakan kepada Majlis untuk disetuju terima. Apabila ianya disetuju terima oleh Majlis, Perunding atau pengamal persendirian akan diberitahu dan ia hendaklah menyediakan pada borang yang berkenaan suatu Ringkasan Perbutiran Sebagaimana Ditender yang akan disampaikan melalui  Pegawai Pengawal.'),
(1675, 'ATPK', 'C', '197', 'Jika ada apa-apa tambahan atau pindaan dikehendaki kepada kerja-kerja selepas kontrak ditandatangani, Pegawai Pengawal hendaklah meminta Perunding atau Pengamal Persendirian menyediakan suatu anggaran bagi perubahan yang dicadangkan itu dengan syarat kos tambahan kerja tersebut tidak melebihi 20%. Jika wang yang mencukupi ada bagi membayar belanja perubahan itu,  Pegawai Pengawal boleh membenarkan Perunding atau Pengamal Persendirian memulakan kerja itu selepas kebenaran diperolehi mengikut Tatacara 223 dan 225. Perintah Perubahan hendaklah ditandatangani oleh  Pegawai Pengawal dan dimaklumkan kepada Majlis mengikut Tatacara 223 hingga 227.'),
(1676, 'ATPK', 'C', '198', 'Jika pindaan atau tambahan tersebut melebihi 20%, maka hendaklah dirujuk kepada  Jawatankuasa Tender Majlis untuk kelulusan.'),
(1677, 'ATPK', 'C', '199', 'Setiap baucar bayaran bagi kerja yang dibuat di bawah sesuatu kontrak hendaklah disokong dengan satu perakuan yang ditandatangani oleh Perunding atau Pengamal Persendirian menyatakan bahawa kerja yang mengenainya bayaran dituntut telah dibuat dengan sempurna mengikut syarat-syarat kontrak dan kos yang diperakui adalah sampai masanya untuk dijelaskan.'),
(1678, 'ATPK', 'C', '200', 'Semua perolehan secara tender hendaklah ditimbangkan dalam mesyuarat Jawatankuasa Tender Majlis. Pertimbangan tender secara edaran adalah tidak dibenarkan.'),
(1679, 'ATPK', 'C', '201', 'Jawatankuasa Tender Majlis hendaklah terdiri daripada mereka yang dilantik oleh Majlis secara bertulis, seperti yang berikut:\r\nPengerusi     :     Pengerusi Majlis\r\nAhli                :     Tiga Orang Ahli Majlis \r\nSetiausaha   :     Urusetia'),
(1680, 'ATPK', 'C', '201A', 'Jawatankuasa Tender Majlis boleh menjemput wakil daripada mana-mana Kementerian/Jabatan untuk memberi nasihat sekiranya diperlukan. Semasa keputusan hendak dibuat, ahli  Jawatankuasa Tender Majlis sahaja boleh berada dalam bilik mesyuarat.'),
(1681, 'ATPK', 'C', '202', 'Jawatankuasa Tender Majlis adalah bertanggungjawab mempertimbang dan setuju terima tender berasaskan kepada dasar, syarat-syarat utama, nilai tender yang terbaik dan tertakluk kepada had kuasa yang diluluskan.Tugas Lembaga tender adalah seperti berikut:'),
(1682, 'ATPK', 'C', '202 (a)', 'Memeriksa dan meneliti salinan pendaftaran dengan Kementerian Kewangan/PKK/CIBD, iklan pelawaan tender, spesifikasi tender, dokumen tender, jadual tender, laporan penilaian teknikal dan laporan penilaian kewangan, jika berkenaan.'),
(1683, 'ATPK', 'C', '202 (b)', 'Menyemak keupayaan teknikal dan kewangan petender.'),
(1684, 'ATPK', 'C', '202 (c)', 'Mempertimbang dan memutuskan tender yang menguntungkan dengan mengambil kira harga, perkhidmatan atau kegunaan barangan, kuantiti, kualiti, tempoh penyerahan atau penyiapan kos penyenggaraan dan faktor-faktor lain yang berkaitan.'),
(1685, 'ATPK', 'C', '202 (d)', 'Jika  Jawatankuasa Tender Majlis mendapati bahawa tatacara perolehan tersebut tidak mengikuti peraturan yang ditetapkan atau mengesyaki perkara luar aturan berlaku, ia boleh memutuskan untuk ditender semula atau mengesyorkan cara-cara tender yang lain.'),
(1686, 'ATPK', 'C', '202 (e)', 'Mempertimbang dan setuju terima tender dengan syarat keputusan yang dibuat adalah secara sebulat suara sahaja dan selaras dengan had nilai yang diluluskan.'),
(1687, 'ATPK', 'C', '202 (f)', 'Mempertimbang dan setuju terima mana-mana sebut harga yang diperakukan oleh Jawatankuasa Sebut Harga yang had nilainya melebihi had nilai sebut harga.'),
(1688, 'ATPK', 'C', '202 (g)', 'Dalam keadaan tertentu dimana ahli-ahli  Jawatankuasa Tender Majlis atau pegawai masih memerlukan maklumat lanjut daripada petender mengenai sesuatu perkara selepas tender ditutup, kelulusan bertulis daripada Pengerusi Jawatankuasa Tender Majlis hendaklah diperolehi terlebih dahulu.'),
(1689, 'ATPK', 'C', '202 (h)', 'Mana-mana ahli  Jawatankuasa Tender Majlis yang mempunyai apa-apa kepentingan persendirian atau kepentingan terletak hak mengenai sesuatu tender itu, ia hendaklah mengistiharkan kepentingannya serta menarik diri dari mesyuarat tersebut dan perkara ini hendaklah diminitkan.'),
(1690, 'ATPK', 'C', '202 (i)', 'Memastikan ahli  Jawatankuasa Tender Majlis sahaja berada dalam bilik mesyuarat semasa keputusan tender hendak dibuat.'),
(1691, 'ATPK', 'C', '207', 'Kenyataan tender/pemberitahuan sebut harga hendaklah menyatakan bahawa dokumen tender/sebut harga hendaklah dimasukkan kedalam sampul berlakri dan dituliskan rujukan tertentu supaya boleh dikenali sebelum ianya dibuka. Waktu tender atau sebut harga ditutup hendaklah ditetapkan pada jam 12.00 tengah hari pada hari bekerja. Dokumen tender atau sebut harga yang sampai atau diserahkan selepas jam 12.00 tengah hari dianggap tidak diterima.');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(1692, 'ATPK', 'C', '208', 'Tender atau sebut harga yang diterima tanpa dibuka hendaklah dimasukkan ke dalam Peti Tawaran berkunci. Peti Tawaran hendaklah dilabelkan tarikh dan waktu peti tawaran ditutup. Dua kunci hendaklah diadakan untuk peti itu dan kuncinya hendaklah dipegang berasingan oleh dua orang pegawai kanan yang berkenaan.'),
(1693, 'ATPK', 'C', '209', 'Jawatankuasa Pembuka Tender/Sebut Harga  hendaklah terdiri daripada yang berikut:\r\nPengerusi    : Pegawai Kumpulan Profesional\r\nAhli             : Sekurang-kurangnya satu (1)   orang pegawai Gred 27 dan  keatas     \r\nSetiausaha : Urusetia Perolehan\r\n'),
(1694, 'ATPK', 'C', '212', 'Jawatankuasa Pembuka Tender/Jawatankuasa Sebut Harga hendaklah membuka Peti Tender/Sebut Harga pada tarikh dan waktu yang ditetapkan. Jika ini tidak dapat diuruskan, Setiausaha Jawatankuasa Pembuka Tender/Jawatankuasa Sebut Harga hendaklah menguruskan supaya Peti Tender itu dilakri pada waktu tutup yang ditetapkan dan mengatur supaya peti itu dibuka oleh Jawatankuasa Pembuka Tender/Jawatankuasa Sebut Harga dengan secepat mungkin. Jawatankuasa Pembuka Tender/Jawatankuasa Sebut Harga hendaklah:\r\n(a)Membuka peti tawaran pada waktu yang ditetapkan.\r\n(b) Memberi nombor kod untuk setiap dokumen tender/sebut harga.\r\n(c) Menandatangani ringkas pada lembaran yang menyatakan harga dan pada apa-apa pindaan tulisan atau taipan bertindih dalam tender/sebut harga.\r\n(d)Merekod pada borang jadual tender/sebut harga dengan mencatatkan nama petender/penyebut harga, amaun harga yang ditawarkan, tempoh siap atau tempoh penyerahan dan menandatangani borang tersebut, dan;\r\n(e) Memastikan bahawa tawaran harga dan tawaran teknikal dikemukakan dalam dua (2) sampul surat yang berasingan dan berlakri bagi tender/sebut harga bekalan dan perkhidmatan. Tender/ sebut harga kerja memadai dikemukakan dalam satu (1) sampul surat berlakri sahaja.\r\n'),
(1695, 'ATPK', 'C', '213', 'Dimansuhkan'),
(1696, 'ATPK', 'C', '214', 'Jadual tender dan kertas-kertas yang berkaitan diserahkan kepada Setiausaha Jawatankuasa Pembuka Tender/Jawatankuasa Sebut Harga pejabat yang akan bertanggungjawab bagi menyimpannya secara sulit sehingga ianya dikemukakan kepada Jawatankuasa Tender Majlis/Jawatankuasa Sebut Harga.'),
(1697, 'ATPK', 'C', '215', 'Segala langkah hendaklah diambil bagi memastikan supaya semua tender/sebut harga yang diterima, sama ada disampaikan dengan cara yang ditentukan atau tidak, diuruskan mengikut peraturan-peraturan ini. Walaubagaimanapun, jika sesuatu tender /sebut harga dibuka dengan tidak sengaja pegawai yang bertanggungjawab hendaklah membuat suatu perakuan mengenainya dengan menyatakan hal keadaan tender/sebut harga itu dibuka dan nama siapa jua mungkin mengetahui kandungan tender/sebut harga akibat tender itu dibuka. Kemudian perakuan dan sampul asal tender itu hendaklah dilakri bersama-sama dengan tender/sebut harga itu dan dimasukkan dalam satu sampul baru dan dimasukkan ke dalam Peti Tender/Sebut Harga.'),
(1698, 'ATPK', 'C', '216', 'Dimansuhkan'),
(1699, 'ATPK', 'C', '217', 'Adalah menjadi kewajipan Urusetia yang menguruskan tender atau sebut harga mengkaji tender atau sebut harga yang diterima dan membuat syor kepada Jawatankuasa Tender Majlis /Jawatankuasa Sebut Harga, mengikut mana yang berkenaan. Urusetia hendaklah mengesyorkan satu (1) petender atau penyebut harga yang paling menguntungkan dari segi harga, menepati spesifikasi dan boleh membekal atau menyiapkan kerja dalam jangka waktu yang dikehendaki.'),
(1700, 'ATPK', 'C', '217A', 'Bagi perolehan bekalan, untuk menilai sama ada sesuatu tender itu boleh disetuju terima, pertimbangan hendaklah diberi kepada harga, tambang, pengendalian, pembungkusan, alat ganti, servis, penyenggaraan, kuasa, penggunaan bahan api, bahan mentah, nilai jual semula dan kos-kos lain mengenai barang itu. Kos-kos ini hendaklah dikira mengikut kadar pengeluaran seunit dan dibandingkan dengan spesifikasi kos yang seumpamanya bagi tawaran petender atau penyebut harga yang lain. Jika perlu nasihat dari Jabatan yang berkenaan hendaklah diperolehi.'),
(1701, 'ATPK', 'C', '218', 'Mana-mana pegawai yang terlibat dalam memproses sesuatu tender atau sebut harga hendaklah memastikan bahawa beliau tidak mempunyai apa-apa kepentingan persendirian atau kepentingan terletak hak mengenai tender atau sebut harga itu. Mana-mana pegawai yang mempunyai kepentingan dalam apa-apa cara pun dengan petender atau penyebut harga, ia hendaklah mengisytiharkan kepentingan dan menarik diri secara bertulis daripada menguruskannya.'),
(1702, 'ATPK', 'C', '219', 'Urusetia hendaklah menyediakan kertas taklimat untuk pertimbangan  Jawatankuasa Tender Majlis atau Jawatankuasa Sebut Harga yang antaranya mengandungi perkara berikut:\r\n(a) Satu salinan tawaran pelawaan tender/sebut harga;\r\n(b) Satu salinan iklan surat khabar jika ada;\r\n(c) Satu salinan penentu tender;\r\n(d) Satu jadual tender/sebut harga yang diterima;\r\n(e) Syor setuju terima bagi satu tawaran tertentu dengan penerangan mengenai pilihan itu;\r\n(f) Ulasan kepada analisis Perunding atau Pengamal Persendirian jika ada; dan;\r\n(g) Maklumat-maklumat lain yang mungkin dikehendaki atau disyaratkan oleh  Jawatankuasa Tender Majlis.\r\n'),
(1703, 'ATPK', 'C', '220', 'Semua kontrak  hendaklah dibuat di atas nama Majlis dan ditandatangani oleh pegawai yang diberi kuasa mengikut kaedah-kaedah yang dibuat di bawah Akta Kontrak Kerajaan 1949 (Akta 120).'),
(1704, 'ATPK', 'C', '221 (a)', 'Bon pelaksanaan hanya dikenakan bagi kontrak yang melebihi RM200,000.  Sebelum sesuatu kontrak kerja yang bernilai melebihi RM200,000 dimulakan, kontraktor hendaklah mengemukakan Bon Pelaksanaan sebanyak lima peratus (5%).'),
(1705, 'ATPK', 'C', '221 (b)', 'Bon Pelaksanaan ini hendaklah dalam bentuk jaminan Bank atau Jaminan Bank Islam atau Jaminan Syarikat Kewangan atau Jaminan Insurans atau Jaminan Takaful, dalam Ringgit Malaysia (RM) dari bank-bank atau institusi kewangan yang dibenarkan oleh Majlis.'),
(1706, 'ATPK', 'C', '222', 'Setiap kontrak kerja hendaklah mempunyai tempoh  tanggungan kecacatan yang tetap sebagaimana ditentukan dalam Tatacara 224. Dalam tempoh ini kontraktor hendaklah membaiki apa-apa kecacatan atas kerja yang dilakukan. Jika sekiranya kontraktor tidak dapat membaiki apa-apa kecacatan dalam tempoh tanggungan kecacatan tersebut, Pegawai Pengawal hendaklah menguruskan supaya kecacatan dapat dibaiki dan perbelanjaan yang dilakukan sedemikian hendaklah ditolak daripada Bon Pelaksanaan atau Wang Tahanan, yang disediakan bagi maksud ini.'),
(1707, 'ATPK', 'C', '223', ' Wang Tahanan ialah jumlah yang ditolak berdasarkan peratus yang dipersetujui dari pembayaran-pembayaran kemajuan yang dibuat kepada kontraktor sehingga jumlah harga kontrak tercapai. Amaun yang masih ada dalam Wang Tahanan hendaklah dipulangkan selepas tamat tempoh tanggungan kecacatan, atau selepas semua kecacatan diperbaiki, mengikut mana yang terkemudian.'),
(1708, 'ATPK', 'C', '224', 'Amaun Wang Tahanan dan lamanya Tempoh Tanggungan Kecacatan hendaklah di dalam setiap satu hal ditentukan mengikut besarnya dan kerumitan projek sebagaimana yang diperuntukkan di bawah:'),
(1709, 'ATPK', 'C', '224 (a)', 'Bagi kontrak yang nilainya kurang dari RM50,000, Tempoh Tanggungan Kecacatan ialah tiga (3) bulan selepas tarikh penyelesaian praktik kerja-kerja itu, dan Wang Tahanan hendaklah berjumlah sebanyak 5% daripada jumlah harga kontrak;'),
(1710, 'ATPK', 'C', '224 (b)', 'Bagi kontrak yang nilainya antara RM50,000 dan RM500,000, Tempoh Tanggungan Kecacatan ialah enam (6) bulan selepas tarikh penyelesaian praktik kerja-kerja dan Wang Tahanan hendaklah berjumlah sebanyak 5% dari jumlah harga kontrak;'),
(1711, 'ATPK', 'C', '224 (c)', 'Bagi kontrak yang nilainya melebihi RM500,000, Tempoh Tanggungan Kecacatan selepas tarikh penyelesaian praktik kerja-kerja serta mengikut pekeliling-pekeliling yang diguna pakai oleh pihak Majlis dan Wang Tahanan hendaklah sebanyak 10% daripada nilai setiap bayaran kemajuan tetapi had keseluruhan wang yang ditahan itu hendaklah tidak melebihi 5% dari jumlah harga kontrak;'),
(1712, 'ATPK', 'C', '224 (d)', 'Jika jumlah harga kontrak melebihi RM1 juta, Tempoh Tanggungan Kecacatan adalah 12 bulan tetapi jika jumlah harga kontrak yang melebihi RM1 juta itu semata-mata kerana kerja-kerja berulangan, misalnya sebilangan bangunan yang serupa, Tempoh Tanggungan Kecacatan boleh dikurangkan kepada 6 bulan, dan Wang Tahanan hendaklah seperti yang dinyatakan di dalam Tatacara 224 di atas.'),
(1713, 'ATPK', 'C', '224 (e)', 'Dalam kes-kes tertentu Majlis boleh memanjangkan tempoh tanggungan kecacatan sesuai dengan jenis dan kerumitan kerja tersebut.'),
(1714, 'ATPK', 'C', '224 (f)', 'Tempoh tanggungan kecacatan bagi kerja yang dilaksanakan oleh subkontraktor dinamakan adalah mengikut yang ditetapkan dalam kontrak utama.'),
(1715, 'ATPK', 'C', '225 (a)', 'Perubahan kontrak dan pelanjutan tempoh kontrak bekalan dan perkhidmatan boleh diluluskan oleh pihak berkuasa yang meluluskan tender asal dengan syarat-syarat berikut:'),
(1716, 'ATPK', 'C', '225(a)(i)', '(i) Tempoh sah laku kontrak masih berkuatkuasa semasa permohonan perubahan kontrak dan pelanjutan tempoh kontrak dipertimbangkan.'),
(1717, 'ATPK', 'C', '225(a)(ii)', '(ii) Perubahan kontrak dan pelanjutan tempoh kontrak tidak melibatkan perubahan kadar harga dan syarat-syarat lain dalam kontrak.'),
(1718, 'ATPK', 'C', '225(a)(iii)', '(iii) Pelanjutan tempoh kontrak hanya diberi sekali sahaja dan tempoh maksimum adalah sehingga dua (2) tahun; dan'),
(1719, 'ATPK', 'C', '225(a)(iv)', '(iv) Bagi kontrak bekalan, tambahan kuantiti tidak melebihi 50% daripada kontrak asal tertakluk kepada nilai maksimum RM7.5 juta dengan syarat jumlah keseluruhan tambahan ini dan nilai kontrak asal tidak melebihi RM25 juta; atau.'),
(1720, 'ATPK', 'C', '225(a)(iv)', '(v) Bagi kontrak perkhidmatan, nilai tambahan tidak melebihi 50% daripada kontrak asal tertakluk kepada nilai maksimum RM7.5 juta dengan syarat jumlah keseluruhan tambahan ini dan nilai kontrak asal tidak melebihi RM25 juta.'),
(1721, 'ATPK', 'C', '225 (b)', 'Semua perubahan kontrak dan pelanjutan tempoh kontrak selain dari yang disebutkan dalam Peraturan Kewangan 225 (a), hendaklah dirujuk kepada Majlis untuk kelulusan.'),
(1722, 'ATPK', 'C', '225 (c)', 'Perubahan kontrak dan pelanjutan tempoh kontrak tidak dibenarkan bagi perolehan yang diuruskan secara sebut harga.'),
(1723, 'ATPK', 'C', '225 (d)', 'Walaubagaimanapun, perubahan kontrak dan pelanjutan tempoh kontrak yang tidak melibatkan tambahan kos boleh dibenarkan bagi sebut harga dan tender oleh pentadbir kontrak tertakluk kepada syarat-syarat didalam dokumen kontrak.'),
(1724, 'ATPK', 'C', '226', 'Sesuatu perubahan daripada kontrak kerja bermaksud sebarang penyimpangan daripada perjanjian kontrak berkaitan dengan pelan, spesifikasi dan/atau senarai kuantiti sama ada dipinda, ditambah atau dikurangkan.'),
(1725, 'ATPK', 'C', '227', 'Setiap perubahan daripada kontrak kerja hendaklah memenuhi kriteria berikut:'),
(1726, 'ATPK', 'C', '227 (a)', 'Tempoh kontrak masih berjalan semasa Arahan Perubahan Kerja dikeluarkan, kecuali dibenarkan di bawah syarat-syarat kontrak;'),
(1727, 'ATPK', 'C', '227 (b)', 'Bagi kerja tambahan, pelaksanaannya hendaklah di tapak bina kontrak; dan'),
(1728, 'ATPK', 'C', '227 (c )', 'Perubahan tidak menukar sebahagian besar skop kerja asal;'),
(1729, 'ATPK', 'C', '227 (d)', 'Bagi kerja gantian, perubahan adalah di dalam skop kontrak asal dan kerja asal yang ditentukan dalam skop kontrak asal hendaklah dapat dikenal pasti dalam kerja-kerja gantian tersebut;'),
(1730, 'ATPK', 'C', '227A', 'Kerja tambahan hendaklah seboleh-bolehnya dilaksanakan secara tender atau sebut harga berasingan mengikut kehendak-kehendak peraturan yang berkenaan dengannya. Perubahan kerja hanyalah dibenarkan sekiranya pelaksanaannya secara tender atau sebut harga berasingan akan mengganggu atau menjejaskan kontrak yang sedang berjalan.'),
(1731, 'ATPK', 'C', '227B', 'Amalan mengeluarkan kerja yang terkandung dalam sesuatu kontrak untuk dilaksanakan oleh pihak lain atau pihak Majlis sendiri adalah tidak dibenarkan.'),
(1732, 'ATPK', 'C', '227C', 'Sebelum mengeluarkan arahan perubahan kerja, pegawai yang bertanggungjawab hendaklah memastikan yang kerja tersebut benar-benar perlu dan ada peruntukan yang mencukupi bagi menampung perbelanjaan tambahan dan kelulusan hendaklah diperolehi terlebih dahulu daripada Pihak Berkuasa seperti yang dinyatakan didalam Manual Perolehan.'),
(1733, 'ATPK', 'C', '228', 'Apabila sesuatu bentuk kontrak tertentu telah diluluskan oleh peguam yang dilantik oleh Majlis bagi kegunaan am, satu kontrak yang dibuat mengikut bentuk itu bolehlah digunakan oleh pegawai yang diberi kuasa tanpa nasihat undang-undang selanjutnya, tetapi persetujuan Pegawai Pengawal hendaklah didapatkan dalam hal-hal berikut:'),
(1734, 'ATPK', 'C', '228 (a)', 'Dibuat pindaan matang pada sesuatu bentuk kontrak tertentu;'),
(1735, 'ATPK', 'C', '228 (b)', 'Kontrak, walau bagaimana kecil sekalipun, yang menunjukkannya ada mengandungi kesulitan dari segi undang-undang atau politik; atau'),
(1736, 'ATPK', 'C', '228 (c)', 'Sesuatu kontrak memasukkan prinsip atau dasar baru, atau kontrak bagi sesuatu jenis perkhidmatan yang belum pernah dikontrakkan.'),
(1737, 'ATPK', 'C', '228A.', 'Aset bermaksud harta benda kepunyaan atau milikan atau di bawah kawalan Majlis yang dibeli atau yang disewa beli dengan wang Baitulmal, Zakat dan Wakaf yang diterima melalui sumbangan atau hadiah atau diperolehi melalui proses perundangan. Aset terbahagi kepada dua (2):'),
(1738, 'ATPK', 'C', '228A (a)', 'Aset Alih bermaksud aset yang boleh dipindahkan dari satu tempat ke satu tempat yang lain termasuk aset yang dibekalkan atau dipasang bersekali dengan bangunan. Terbahagi kepada dua (2) kategori :\r\n(i) Harta Modal\r\n(ii) Inventori\r\n'),
(1739, 'ATPK', 'C', '228A (b)', 'Aset tetap bermaksud aset yang berbentuk kekal dan selain maksud aset  seperti di Tatacara 228A (a).'),
(1740, 'ATPK', 'C', '228B.', 'Majlis hendaklah menguruskan aset berpandukan kepada pekeliling-pekeliling atau arahan-arahan pentadbiran lain dari masa ke semasa yang telah diluluskan oleh Majlis.'),
(1741, 'ATPK', 'C', '229', 'Bagi tujuan pembangunan umat Islam di semua bidang seperti mana yang diperuntukkan dalam Enakmen, Majlis boleh memperolehi atau membeli mana-mana aset sama ada dalam bentuk wang, saham atau harta benda dengan menggunakan wang dari Kumpulan Wang Majlis bagi tempoh tertentu.'),
(1742, 'ATPK', 'C', '230', 'Satu Jawatankuasa Pelaburan hendaklah ditubuhkan bagi meneliti, mengkaji dan seterusnya memperakukan kepada Majlis sebelum sesuatu pelaburan dibuat. Tiada pelaburan boleh dibuat melainkan dengan kelulusan Majlis.'),
(1743, 'ATPK', 'C', '231', 'Majlis dari semasa ke semasa boleh menjual apa-apa saham dan harta benda milik Majlis sebagaimana yang diperuntukan oleh Enakmen.'),
(1744, 'ATPK', 'C', '232', 'Sesuatu penjualan hartanah/pelaburan selain daripada simpanan tetap Majlis hendaklah melalui satu Jawatankuasa Pelaburan bagi meneliti, mengkaji dan seterusnya memperakukan kepada Majlis sebelum sesuatu saham atau hartanah Majlis dijual.'),
(1745, 'ATPK', 'C', '233-243.', 'Telah dipindahkan kepada Tatacara Pengurusan Aset Alih MAIS dan pekeliling-pekiling yang berkaitan yang dikeluarkan dari semasa ke semasa'),
(1746, 'ATPK', 'C', '244', 'Harta Luqatah adalah harta yang ditemui di suatu kawasan, tidak dijaga dan penemu tidak mengetahui pemiliknya.'),
(1747, 'ATPK', 'C', '245', 'Pegawai yang bertanggungjawab hendaklah merekod terimaan Harta Luqatah yang diserahkan kepada Majlis. Pegawai tersebut hendaklah mengeluarkan satu surat akuterima, jika harta tersebut berbentuk wang tuna,i satu resit hendaklah dikeluarkan.'),
(1748, 'ATPK', 'C', '246', 'Pegawai yang bertanggungjawab hendaklah menyimpan Harta Luqatah tersebut di dalam peti besi atau tempat yang selamat dan bersesuaian, dan bagi wang hendaklah dimasukkan ke dalam bank dan diakaunkan ke dalam Kumpulan Wang Baitulmal.'),
(1749, 'ATPK', 'C', '247', 'Pegawai Pengawal hendak mengaturkan dengan segera hebahan semua terimaan Harta Luqatah kepada orang awam melalui cara-cara yang sesuai mengikut Hukum Syarak.'),
(1750, 'ATPK', 'C', '248', 'Selepas setahun dari tarikh Harta Luqatah dihebahkan, Jawatankuasa Pengurusan Harta Luqatah bolehlah mengaturkan apa-apa kaedah pelupusan bagi harta tersebut.'),
(1751, 'ATPK', 'C', '249', 'Jawatankuasa Pengurusan Harta Luqatah hendaklah terdiri daripada empat (4) orang pegawai yang dilantik oleh Pegawai Pengawal Jawatankuasa ini akan melaksanakan kuasa yang telah diberikan.'),
(1752, 'ATPK', 'C', '250', 'Semua hasil pelupusan hendaklah dimasukkan ke dalam Kumpulan Wang Baitulmal.'),
(1753, 'ATPK', 'C', '250A.', 'Majlis hendaklah mengurus stor berpandukan kepada Tatacara Pengurusan Stor MAIS yang berkuatkuasa dari masa ke semasa dan diluluskan oleh Majlis.'),
(1754, 'ATPK', 'C', '251-282', 'Telah dipindahkan kepada Tatacara Pengurusan Stor dan pekeliling-pekiling yang berkaitan yang dikeluarkan dari semasa ke semasa'),
(1755, 'ATPK', 'C', '283', 'Semua cadangan untuk mengadakan borang-borang kewangan cetakan baru atau meminda borang-borang dan buku-buku rekod yang sedia ada hendaklah dirujuk terlebih dahulu kepada Pegawai Pengawal untuk mendapatkan kelulusan sebelum dicetak.'),
(1756, 'ATPK', 'C', '284', 'Apabila sesuatu resit atau borang hasil baru hendak dicetak, Pegawai Pengawal hendaklah meminta nasihat Ketua Audit Negara untuk memastikan resit atau borang hasil itu patut dikawal bagi maksud audit. Pegawai Pengawal hendaklah memberitahu syarikat percetakan mengenai kawalan keselamatan yang perlu diambil semasa percetakan. Jika borang baru itu ditetapkan oleh undang-undang, nasihat dari Penasihat Undang-Undang Majlis hendaklah diminta terlebih dahulu.'),
(1757, 'ATPK', 'C', '285', 'Semua bekalan Resit Rasmi, dan borang hasil dikawal hendaklah diperolehi daripada pembekal yang diberi konsesi tertentu yang diluluskan oleh Pegawai Pengawal.'),
(1758, 'ATPK', 'C', '288 (a)', 'Kelulusan Jabatan Audit Negara hendaklah diperolehi sebelum Resit Rasmi, lesen dan borang seumpamanya yang belum digunakan atau usang hendak dimusnahkan. Majlis yang berkenaan hendaklah menghantar kepada Jabatan Audit Negara senarai lengkap mengenai semua Resit Rasmi, dan borang seumpamanya yang hendak dimusnahkan dengan memberi nombor kaunterfoil dan menyatakan justifikasi mengapa borang-borang itu tidak boleh digunakan lagi.'),
(1759, 'ATPK', 'C', '288 (b)', 'Pegawai Pengawal hendaklah menubuhkan Lembaga Pemeriksa. Lembaga Pemeriksa hendaklah memusnahkan borang itu dan menyediakan perakuan pemusnahan dalam dua (2) salinan. Salinan pertama untuk dikemukakan kepada Jabatan Audit Negara dan salinan yang kedua hendaklah disimpan oleh Majlis.'),
(1760, 'ATPK', 'C', '289 (a)', 'Sekiranya sesuatu buku Resit Rasmi atau buku yang seumpamanya atau sebahagian daripadanya hilang, hal itu hendaklah dilaporkan dengan segera mengikut cara yang sama seperti di bawah bab kehilangan dan hapus kira Tatacara 342 hingga 369.'),
(1761, 'ATPK', 'C', '289 (b)', 'Selain itu, Pegawai Pengawal hendaklah mengeluarkan notis pemberitahuan melalui iklan dalam satu (1) akhbar harian utama berbahasa Melayu.'),
(1762, 'ATPK', 'C', 'VII', 'VERIFIKASI STOK'),
(1763, 'ATPK', 'C', '290-302.', 'Telah dipindahkan kepada Tatacara Pengurusan Stor dan pekeliling-pekeliling yang berkaitan yang dikeluarkan dari semasa ke semasa'),
(1764, 'ATPK', 'D', 'BAB D', 'BAB D - TATACARA ZAKAT'),
(1765, 'ATPK', 'D', '303', 'Majlis hendaklah berkuasa memungut zakat dan fitrah daripada setiap orang Islam yang kena dibayar di dalam negeri Selangor mengikut hukum syarak bagi pihak Duli Yang Maha Mulia Sultan seperti yang disebutkan  di dalam Enakmen Pentadbiran Agama Islam (Negeri Selangor) 2003 (Seksyen 86).'),
(1766, 'ATPK', 'D', '304', 'Majlis melalui Lembaga  Zakat Selangor (LZS) hendaklah menyelenggara Rekod dan Daftar Pembayar Zakat.'),
(1767, 'ATPK', 'D', '305 (a)', 'Pungutan zakat yang diterima oleh setiap kaunter zakat akan disimpan di dalam bank.'),
(1768, 'ATPK', 'D', '', 'Arahan kepada bank akan dibuat oleh Lembaga Zakat Selangor untuk pemindahan kutipan/pungutan kepada akaun induk Zakat Majlis dalam tempoh  yang telah ditetapkan.'),
(1769, 'ATPK', 'D', '305 (b)', 'Pihak Lembaga Zakat Selangor (LZS) akan menyediakan laporan kutipan zakat kepada Majlis pada setiap 10 haribulan berikutnya.'),
(1770, 'ATPK', 'D', '305 (c)', 'Laporan kutipan yang dikemukakan adalah mengikut jenis jenis kutipan zakat seperti :-\r\n(i) Zakat Fitrah\r\n(ii) Zakat Tanaman\r\n(iii) Zakat Perniagaan\r\n(iv) Zakat Harta\r\n(v) Zakat Emas, Perak\r\n(vi) Zakat Ternakan\r\n(vii) Zakat Saham\r\n(viii) Zakat Pendapatan\r\n(ix) Zakat Wang Simpanan\r\n(x) Lain-lain Terimaan'),
(1771, 'ATPK', 'D', '306', 'Prosedur perakaunan wang zakat di peringkat ibu pejabat Majlis/Lembaga Zakat Selangor  dan cawangannya di daerah-daerah adalah sebagaimana yang dinyatakan dalam Prosedur Untuk Terimaan atau mana-mana prosedur yang telah diluluskan.'),
(1772, 'ATPK', 'D', '307', 'Dimansuhkan'),
(1773, 'ATPK', 'D', '308', 'Bagi pungutan zakat oleh  agen yang dilantik, perjanjian atau surat lantikan dengan syarat-syarat tertentu hendaklah disediakan.'),
(1774, 'ATPK', 'D', '309', 'Majlis hendaklah menyediakan garis panduan bertulis untuk menentukan orang-orang Islam fakir, miskin, amil, mualaf , riqab, gharimin, ibnu sabil dan fi-sabilillah yang berhak menerima zakat sebagaimana yang disebut dalam Enakmen Pentadbiran Agama Islam  (Negeri Selangor) 2003.'),
(1775, 'ATPK', 'D', '310', 'Rekod dan butiran lengkap penerima zakat hendaklah disimpan dan dikemaskini oleh Amil yang dilantik.'),
(1776, 'ATPK', 'D', '311', 'Pengagihan zakat kepada penerima hendaklah melalui baucar bayaran dan disokong dengan dokumen yang berkaitan.'),
(1777, 'ATPK', 'D', '311 (a)', 'Pihak Amil akan mendapatkan peruntukan agihan zakat berasaskan kepada peruntukan anggaran tahunan Majlis.'),
(1778, 'ATPK', 'D', '311 (b)', 'Agihan akan dilaksanakan oleh Amil kepada 8 asnaf yang layak menerima.'),
(1779, 'ATPK', 'D', '311 (c)', 'Laporan agihan kepada 8 asnaf hendaklah dikemukakan kepada Majlis pada setiap bulan berikutnya untuk diakaunkan aktiviti agihan tersebut ke dalam akaun induk Majlis.'),
(1780, 'ATPK', 'D', '312', 'Segala pungutan dan agihan wang zakat hendaklah diakaunkan ke dalam akaun Kumpulan Wang Zakat.'),
(1781, 'ATPK', 'D', '313', 'Pada akhir setiap tahun kewangan, satu Penyata Kewangan (kutipan dan agihan),  hendaklah disediakan, Penyata Kewangan ini hendaklah dikemukakan ke Jabatan Audit Negara cawangan Negeri mengikut tarikh yang ditetapkan. Sebelum dikemukakan kepada Majlis untuk disatukan dengan Penyata Kewangan Majlis.'),
(1782, 'ATPK', 'D', '314', 'Dipindahkan ke Tatacara 313'),
(1783, 'ATPK', 'D', '315', 'Wang Zakat Terkumpul adalah sejumlah baki wang zakat yang belum dibelanjakan dalam tahun semasa kepada 8 asnaf yang berhak menerimanya mengikut hukum syarak dan kekal di dalam Kumpulan Wang Zakat. Wang ini hendaklah diagihkan pada tahun berikutnya.'),
(1784, 'ATPK', 'D', '316', 'Lembaga Zakat Selangor adalah dikehendaki pada setiap tahun menyediakan Penyata Wang Zakat Terkumpul untuk dibentangkan kepada mesyuarat Jawatankuasa Baitulmal dan dibawa ke Majlis untuk makluman.'),
(1785, 'ATPK', 'D', '317', 'Penyata tersebut hendaklah antara lain mengandungi butir-butir berikut:\r\n(a) Nilai kumulatif pada awal tahun;\r\n(b) Butir-butir perbelanjaan yang dikeluarkan;\r\n(c) Nilai baki zakat bagi tahun semasa yang belum digunakan; dan\r\n(d) Nilai bersih kumulatif pada setiap akhir tahun.'),
(1786, 'ATPK', 'E', 'BAB E', 'TATACARA WAKAF'),
(1787, 'ATPK', 'E', '318', 'Majlis boleh menerima mana-mana individu atau pemilik yang telah mencapai umur lapan belas tahun, sempurna akal dan rasyid boleh mewakafkan hartanya untuk apa-apa tujuan khairat menurut hukum syarak.'),
(1788, 'ATPK', 'E', '319', 'Setiap permohonan  untuk mewakafkan hendaklah mengisi Borang Permohonan seperti yang telah ditetapkan berdasarkan Enakmen Wakaf (Negeri Selangor) No.7 1999.'),
(1789, 'ATPK', 'E', '320', 'Majlis boleh menerima wakaf bagi harta tak alih dan harta alih seperti tanah, bangunan dan lain-lain yang dibenarkan oleh Majlis.'),
(1790, 'ATPK', 'E', '321', 'Setiap permohonan mewujudkan wakaf hanya akan diluluskan setelah semua syarat-syarat yang ditetapkan oleh Majlis dipenuhi.'),
(1791, 'ATPK', 'E', '322', 'Harta yang diwakafkan setelah diluluskan hendaklah didaftarkan atas nama Majlis.'),
(1792, 'ATPK', 'E', '322A', 'Majlis boleh menawarkan saham wakaf terhadap apa-apa harta yang diperolehinya atau yg akan diperolehinya kpd mana mana orang utk saham-saham itu dibeli dan kemudiannya diwakaf kan kepada majlis.'),
(1793, 'ATPK', 'E', '322B', 'Majlis hendaklah menubuhkan Jawatankuasa Pengurusan Wakaf yang akan mentadbir dan menguruskan semua perkara berhubung dengan wakaf di Negeri Selangor.'),
(1794, 'ATPK', 'E', '323', 'Permohonan mewujudkan Wakaf Khas hendaklah mendapatkan;'),
(1795, 'ATPK', 'E', '323 (a)', 'Kebenaran dan pengesahan dari Duli Yang Maha Mulia Sultan, atas nasihat Majlis; atau'),
(1796, 'ATPK', 'E', '323 (b)', 'Pengesahan bahawa ia telah dibuat semasa waqif dalam keadaan marad al-maut dan kemudiannya meninggal dunia akibat sakit itu dan di hadapan dua saksi.'),
(1797, 'ATPK', 'E', '324', 'Setiap wakaf istibdal hendaklah mendapat kelulusan Majlis.'),
(1798, 'ATPK', 'E', '325', 'Apa-apa hasil atau keuntungan diperolehi daripada mana-mana harta atau penjualan harta wakaf ia hendaklah diakaunkan dalam akaun wakaf.'),
(1799, 'ATPK', 'E', '326', 'Mana-mana perbelanjaan berkaitan harta wakaf boleh dibayar daripada akaun wakaf yang berkaitan dengannya. ( jika berkenaan).'),
(1800, 'ATPK', 'E', '327', 'Kumpulan wang yang terdiri daripada semua wang dan harta yang diwakafkan atau apa-apa hasil yang diperolehi daripada mawquf atau melalui istibdal.'),
(1801, 'ATPK', 'F', 'BAB F', 'AUDIT, KEHILANGAN DAN PROSEDUR HAPUS KIRA'),
(1802, 'ATPK', 'F', 'I', 'AUDIT'),
(1803, 'ATPK', 'F', '329', 'Pegawai Pengawal adalah bertanggungjawab kepada Majlis keatas :'),
(1804, 'ATPK', 'F', '329 (a)', 'Perbelanjaan,Pemungutan,penyimpanan dan perakaunan yang sepatutnya mengenai Wang Awam; dan'),
(1805, 'ATPK', 'F', '329 (b)', 'Penyimpanan,pelupusan dan perakaunan yang sepatutnya mengenai Aset Awam dibawah jagaan mereka.'),
(1806, 'ATPK', 'F', '', 'Tanggungjawab ini termasuk mengadakan sistem yang sepatutnya mengenai perwakilan kuasa dan kewajipan kepada pegawai di bawah jagaan mereka dan mengeluarkan arahan-arahan dalaman yang sesuai untuk memastikan bahawa urusan kewangan dijalankan dengan cekap dan seragam.'),
(1807, 'ATPK', 'F', '330', 'Pegawai Pengawal hendaklah melaporkan dengan serta merta kepada Majlis sesuatu hal yang diketahuinya bahawa salah laku atau ketidakcekapan pada amnya seseorang pegawai di bawah kawalannya telah menyebabkan atau mungkin menyebabkan kerugian kewangan kepada Majlis. Jika laporan seperti itu tidak dibuat, ia boleh menyebabkan Setiausaha Majlis sendiri disurcaj bersama-sama.'),
(1808, 'ATPK', 'F', '331', 'Apabila Laporan Ketua Audit Negara mengenai Penyata Kewangan Tahunan Majlis diterima, Pegawai Pengawal hendaklah mengemukakan laporan tersebut serta ulasannya kepada Majlis untuk mendapat pandangan. Majlis boleh juga menghendaki seseorang pegawai Majlis memberi keterangan di hadapannya dan mengambil tindakan sewajarnya atas perkara yang ditimbulkan dalam Laporan Ketua Audit Negara.'),
(1809, 'ATPK', 'F', '332', 'Majlis hendaklah menghantar senaskah Penyata Kewangan Tahunan Majlis yang diaudit kepada mana-mana pihak yang dinyatakan dalam Enakmen.'),
(1810, 'ATPK', 'F', '333', 'Ketua Audit Negara atau seseorang pegawai yang diwakilkan olehnya dengan bertulis adalah berhak melihat dan memeriksa segala rekod, buku baucar, dokumen, wang tunai, setem, cagaran, barang-barang atau harta benda lain yang tertakluk kepada auditannya.'),
(1811, 'ATPK', 'F', '334', 'Adalah menjadi kewajipan semua pegawai Majlis memberi kepada Ketua Audit Negara segala kemudahan yang perlu dalam masa melaksanakan kewajipannya dan menjawab dengan segera iaitu tidak lewat dari satu (1) bulan dari tarikh pertanyaan audit dialamatkan kepada mereka.'),
(1812, 'ATPK', 'F', '334A', 'Pegawai Pengawal adalah bertanggungjawab untuk memastikan bahawa segala buku, rekod dan dokumen yang tertakluk kepada pengauditan dikemukakan kepada Ketua audit atau wakilnya apabila dikehendaki. Suatu senarai lengkap yang ditandatangani oleh ketua Jabatan/Bahagian hendaklah disediakan dan diserahkan bersama-sama dengan buku-buku itu kepada Ketua audit atau wakilnya ingin mengambil sesuatu buku, rekod dan/atau dokumen, maka suatu akuan terima hendaklah diberikan kepada Majlis.'),
(1813, 'ATPK', 'F', '335', 'Pegawai Pengawal dalam tempoh empat bulan selepas berakhirnya setiap tahun kewangan hendaklah mengemukakan Penyata Kewangan Tahunan lengkap yang telah ditandatangani kepada Ketua Audit Negara untuk diaudit.'),
(1814, 'ATPK', 'F', '336', 'Ketua Audit Dalam adalah bertanggungjawab memeriksa dan melaporkan kepada Pegawai Pengawal apa-apa perkara yang tidak memuaskan yang berkaitan dengan pengurusan dan kewangan Majlis, tetapi ini tidaklah melepaskan seseorang pegawai dari tanggungjawab untuk mematuhi atau untuk mendapatkan pematuhan arahan-arahan dalam bidang kuasanya.'),
(1815, 'ATPK', 'F', '337', '336. Pegawai Pengawal dan Ketua Bahagian selain daripada menjalankan penyeliaan biasa urusan pejabat setiap hari hendaklah dari semasa ke semasa menjalankan atau melantik seorang pegawai lain untuk menjalankan pemeriksaan mengejut mengenai penyelenggaraan buku perakaunan (termasuk pengakaunan barang-barang) yang dilakukan oleh pegawai-pegawai Majlis, bagi maksud mengetahui sama ada acara-acara yang betul difahami dengan sepatutnya dan sentiasa diikuti. Perihal mengenai kewajipan yang diperiksa bersama dengan catatan pemeriksaan itu hendaklah dimasukkan ke dalam Daftar Pemeriksaan Mengejut seperti yang dinyatakan dalam Peraturan Kewangan berikut.'),
(1816, 'ATPK', 'F', '339', 'Pegawai Pengawal atau pegawai yang diberi kuasa olehnya hendaklah mengadakan pemeriksaan mengejut secara berkala atas perkara dalam jagaan seseorang pegawai dalam mana-mana Bahagian yang diamanahkan. Perkara tersebut adalah seperti berikut :\r\n(a)      peti besi;\r\n(b) Bilik kebal;\r\n(c) Peti wang tunai;\r\n(d) Laci-laci atau bekas-bekas lain untuk menyimpan wang atau barang-barang berharga.'),
(1817, 'ATPK', 'F', '340', 'Pemeriksaan mengejut itu hendaklah dilakukan tidak kurang sekali dalam enam bulan, dan hasil pemeriksaan itu hendaklah direkodkan dalam Daftar Pemeriksaan Mengejut.'),
(1818, 'ATPK', 'F', '341', 'Butir-butir yang dikehendaki dicatatkan dalam Daftar Pemeriksaan Mengejut adalah seperti berikut \r\n(a) tarikh dan masa pemeriksaan;\r\n(b) nama pegawai yang diperiksa;\r\n(c) amaun wang dan setem yang diamanahkan kepadanya;\r\n(d)  perihal barang-barang berharga yang diamanahkan kepadanya;\r\n(e) hasil pemeriksaan; dan\r\n(f) tandatangan dan jawatan pegawai pemeriksa.'),
(1819, 'ATPK', 'F', '341A', 'Sekiranya perlu, Lembaga Pemeriksa Khas hendaklah dilantik oleh Pegawai Pengawal mengikut mana yang berkenaan untuk memeriksa pungutan hasil, wang instrument dan rekod kewangan serta harta dalam simpanan jabatan berkenaan. Pemeriksaan mengejut oleh Lembaga Pemeriksa Khas itu hendaklah dirahsiakan oleh semua pihak yang berkenaan.'),
(1820, 'ATPK', 'F', '341B', 'Sesuatu jejak audit (audit trail) yang nyata hendaklah dibuat daripada bil dan resit asal kepada akaun kecil dan kepada akaun kawalan. Perkara ini dipastikan seperti berikut :\r\n(a)Setiap satu bil direkod dalam Daftar Bil;\r\n(b) Daftar Baucar Bayaran menunjukkan nombor-nombor baucar.\r\n(c) Setiap satu terimaan hendaklah mempunyai rujukan asas.\r\n(d) Semua pelarasan seperti nota kredit, taksiran penalti, hapus kira dan sebagainya boleh dikesan terus kepada perubahan-perubahan dalam akaun individu;\r\n(e) Kawalan yang mencukupi diadakan berhubung dengan pindaan kepada resit, pelarasan dan perkara-perkara berkaitan untuk mengelakkan manipulasi tanpa kuasa dan kemungkinan penipuan ke atas akaun-akaun.\r\n(f) Jumlah di dalam akaun subsidiari hendaklah sama dengan jumlah di dalam  akaun kawalan.\r\n'),
(1821, 'ATPK', 'F', 'II', 'KEHILANGAN DAN HAPUS KIRA'),
(1822, 'ATPK', 'F', '342', 'Dipindahkan ke Tatacara 2'),
(1823, 'ATPK', 'F', '343', 'Melaporkan Kehilangan'),
(1824, 'ATPK', 'F', '', 'Apabila terdapat kehilangan apa-apa wang, barang atau harta benda Majlis yang lain adalah menjadi tanggungjawab Pengawai Pengawal untuk memastikan laporan polis dibuat dalam tempoh (24)jam dari tarikh kehilangan telah diketahui jika ia ada alasan untuk mempercayai bahawa sesuatu kesalahan jenayah telah dilakukan berhubung dengan kehilangan itu. Apa-apa tindakan yang diambil atau dicadangkan di bawah Tatacara ini hendaklah dimasukkan ke dalam laporan awal di bawah Tatacara 345 hingga 349.'),
(1825, 'ATPK', 'F', '344', 'Dimansuhkan'),
(1826, 'ATPK', 'F', '345', 'Ketua Bahagian di mana sesuatu kehilangan telah berlaku hendaklah dengan serta merta menghantar satu laporan awal dan salinan laporan polis terus kepada Pegawai Pengawal. Salinan laporan itu hendaklah dihantar kepada ketua akauntan dan ketua audit dalam.'),
(1827, 'ATPK', 'F', '346', 'Bagi kes kehilangan yang melibatkan seseorang ketua jabatan laporan awal hendaklah disediakan oleh pegawai atasan.'),
(1828, 'ATPK', 'F', '347', 'Laporan awal hendaklah mengandungi butir-butir kehilangan seberapa yang dapat dipastikan pada masa itu seperti di Lampiran 6.'),
(1829, 'ATPK', 'F', '348', 'Dimansuhkan'),
(1830, 'ATPK', 'F', '349', 'Dimansuhkan'),
(1831, 'ATPK', 'F', '349A', 'Selepas laporan awal itu dibuat, adalah menjadi tanggungjawab Ketua Bahagian untuk mendapatkan laporan penyiasatan polis untuk dikemukakan kepada pegawai pengawal sesudah sahaja penyiasatan itu selesai atau empat (4) bulan daripada tarikh laporan itu sekiranya penyiasatan itu belum selesai.'),
(1832, 'ATPK', 'F', '350', 'Pegawai Pengawal  hendaklah  mengaturkan  penyiasatan mengenai  Kehilangan  itu  dengan  melantik  Pegawai Penyiasat  atau  Jawatankuasa  Penyiasat  secara  bertulis dan mengemukakan  laporan akhir yang lengkap dalam tempoh  empat (4) bulan dari  tarikh  laporan awal dikemukakan.  Laporan akhir itu hendaklah mengandungi, jika berkenaan, salinan Laporan Polis yang lengkap dan suatu ringkasan perbicaraan mahkamah bagi dikemukakan kepada Pegawai Pengawal.'),
(1833, 'ATPK', 'F', '351', 'Laporan itu hendaklah mengandungi segala butir yang berkenaan seperti dalam Lampiran 7 bersama dengan pandangan Ketua Bahagian mengenai keadaan bagaimana kehilangan itu berlaku. Kenyataan dan surat dari pegawai yang terlibat boleh dilampirkan kepada laporan itu tetapi tidak boleh dianggap sebagai pengganti sesuatu laporan lengkap mengenai pandangan dan syor Pegawai Pengawal.'),
(1834, 'ATPK', 'F', '351A', 'Salinan Laporan akhir itu hendaklah dihantar oleh Ketua Bahagian kepada Ketua Akauntan dan Ketua Audit Dalam'),
(1835, 'ATPK', 'F', '355', 'Jika difikirkan bahawa pegawai yang terbabit dengan kehilangan itu patut ditahan kerja, Pegawai Pengawal hendaklah membuat suatu laporan segera kepada Jawatankuasa yang berkaitan untuk tindakan sewajarnya.'),
(1836, 'ATPK', 'F', '355A', 'Pegawai Pengawal boleh memilih seorang pekerja atau pekerja-pekerja dari jabatan lain daripada jabatan yang berkenaan dengan kehilangan itu untuk turut serta dalam penyiasatan sesuatu laporan kehilangan wang atau harta Majlis. Dalam hal demikian suatu laporan bersama hendaklah dihantar oleh Pegawai Pengawal dan Pegawai Penyiasat atau Jawatankuasa Penyiasat yang dilantik itu.'),
(1837, 'ATPK', 'F', '356', 'Jika sesuatu kehilangan telah dipastikan, Setiausaha Majlis hendaklah :'),
(1838, 'ATPK', 'F', '356 (a)', 'Jika kehilangan itu adalah wang tunai, memohon sesuatu pendahuluan diri yang sama banyaknya dan didebitkan pada kepada akaun Pendahuluan Diri (Pegawai Majlis) atas nama pegawai yang berkenaan. Jika nama pegawai berkenaan tidak dapat ditentukan dengan serta merta, pendahuluan diri atas nama bahagian dimana kehilangan itu berlaku.'),
(1839, 'ATPK', 'F', '356 (b)', 'Jika kehilangan itu adalah harta, tindakan serupa hendaklah diambil dan akaun harta yang berkenaan dikreditkan dengan nilai kehilangan itu; atau'),
(1840, 'ATPK', 'F', '356 (c )', 'Jika kehilangan itu adalah dari sesuatu Kumpulanwang Amanah tindakan serupa hendaklah diambil dan akaun yang berkenaan dikreditkan dengan nilai kehilangan.'),
(1841, 'ATPK', 'F', '357', 'Pendahuluan ini hendaklah diselaraskan selepas kelulusan hapus kira didapati dengan mendebit Peruntukan Maksud Perbelanjaan Kehilangan yang berkenaan kepunyaan Majlis yang kena menanggung kehilangan itu dan mengkredit ke Akaun Wang Pendahuluan Diri.'),
(1842, 'ATPK', 'F', '358', 'Pegawai Pengawal hendaklah selepas menimbangkan Laporan Akhir di bawah Tatacara 350-351, mengambil tindakan untuk menghapus kira kehilangan. Jika ia berpendapat bahawa hal itu memerlukan surcaj atau lain-lain, tindakan tatatertib terhadap seseorang pegawai yang bertanggungjawab atas kehilangan itu, ia hendaklah menghantar ulasan dan syornya kepada Jawatankuasa     Tatatertib yang berkenaan bersama dengan salinan Laporan Awal dan Laporan Akhir.'),
(1843, 'ATPK', 'F', '359', 'Mendapatkan mana-mana surcaj yang dikenakan oleh Jawatankuasa Tatatertib yang berkenaan adalah menjadi tanggungjawab Pegawai Pengawal. Jika didapati perlu untuk mendapatkan sesuatu surcaj dari pencen atau ganjaran, urusan dengan jabatan yang berkenaan hendaklah dibuat. Segala surcaj hendaklah dikredit kepada butir hasil yang sepatutnya kepunyaan Majlis yang menanggung kehilangan itu.'),
(1844, 'ATPK', 'F', '360', 'Majlis, tertakluk kepada apa-apa arahan yang dikeluarkan dari semasa ke semasa, diberi kuasa untuk menghapus kira nilai barang-barang yang hilang, kurang, kondem atau tidak boleh guna yang dipunyai oleh Majlis.'),
(1845, 'ATPK', 'F', '361', 'Jika Majlis telah mewakilkan kuasa untuk menghapus kira dan melupuskan itu Pegawai Pengawal dan pegawai-pegawai lain, perwakilan itu  adalah tertakluk kepada syarat berikut :'),
(1846, 'ATPK', 'F', '361 (a)', 'Kuasa yang diwakilkan itu tidak boleh diwakilkan selanjutnya kepada mana-mana pegawai lain.'),
(1847, 'ATPK', 'F', '361 (b)', 'Dalam mana-mana satu hal harga asal harta yang hendak dilupus itu hendaklah berdasarkan kepada kadar dan amaun semasa yang ditetapkan oleh arahan semasa yang berkuatkuasa.'),
(1848, 'ATPK', 'F', '', 'Sebelum meluluskan apa-apa hapuskira, pegawai yang diberi kuasa meluluskan mesti berpuas hati bahawa soal kecurian penipuan atau kecuaian tidak melinbatkan pegawai Majlis.'),
(1849, 'ATPK', 'F', '361 (c )', 'Berkenaan dengan hapus kira Aset Majlis yang hilang, arahan dan peraturan yang masih berkuat kuasa mestilah diikuti.'),
(1850, 'ATPK', 'F', '361 (d)', 'Dalam hal-hal yang lain mengenai kehilangan barang-barang, Tatacara 347 dan 348 mestilah diikuti.'),
(1851, 'ATPK', 'F', '362', 'Apabila satu pembayaran lebih atau pembayaran yang salah dibuat, pembayaran balik hendaklah biasanya didapatkan balik daripada penerima pembayaran. Jika pembayaran balik sepenuhnya didapatkan balik, laporan tidak perlu dihantar kepada Pegawai Pengawal melainkan jika Pegawai Pengawal berpendapat bahawa suatu kes tindakan tatatertib hendak diambil ke atas seseorang pegawai kerana kecuaian berkaitan dengan pembayaran lebih itu.'),
(1852, 'ATPK', 'F', '362A', 'Jika penerima bayaran itu adalah seorang Pegawai Majlis, Pegawai Pengawal boleh membenarkan Pegawai tiu memulang balik pembayaran lebih itu dengan ansuran bulanan dengan syarat pulangan balik sepenuhnya dilakukan dengan secepat mungkin dan tidak lewat dari 18 bulan selepas bayaran lebih itu diketahui atau sebelum pegawai itu meninggalkan Majlis atau mana yang lebih awal. Perlanjutan tempoh ansuran hendaklah mendapat kelulusan Pegawai Pengawal. Jika pulangan balik dengan ansuran diluluskan, suatu permohonan hendaklah dibuat untuk satu pendahuluan atas nama penerima.'),
(1853, 'ATPK', 'F', '363', 'Jika pulangan balik sepenuhnya tidak boleh diperolehi atau jika Pegawai Pengawal berpendapat bahawa pembayaran lebih itu dibuat melainkan dengan suci hati atau bahawa amaun yang terlebih bayar itu tidak boleh atau tidak patut diperolehi balik dengan sepenuhnya daripada penerima bayaran, suatu penyiasatan hendaklah dibuat suatu laporan dihantar kepada Pegawai Pengawal menyatakan dengan tepat bagaimana bayaran lebih itu berlaku, siapa pegawai atau pegawai-pegawai yang bertanggungjawab mengenainya dan memberi sebab penuh mengapa sesuatu jumlah wang tidak boleh atau tiadak patut diperolehi balik dari penerima bayaran itu. Laporan itu hendaklah juga menyatakan sama ada pembayaran lebih itu disebabkan oleh kecuaian seseorang pegawai dan jika demikian, Pegawai Pengawal hendaklah membuat syor untuk surcaj atau tindakan tatatertib yang lain.'),
(1854, 'ATPK', 'F', '364', 'Apabila menerima laporan itu, Pegawai Pengawal hendaklah memutuskan apakah amaun, jika ada, yang patut diperolehi balik dari penerima bayaran itu (jika seorang pegawai Majlis) dan sama ada apa-apa tindakan lanjut patut diambil bagi mendapatkan pulangan balik wang yang tidak boleh diperolehi balik itu. Pegawai Pengawal hendaklah kemudiannya mendapatkan kelulusan dari Majlis untuk ditanggungkan akaun itu kepada akaun.'),
(1855, 'ATPK', 'F', '364A', 'Jika sesuatu hal itu menghendaki surcaj atau tindakan tatatertib lain terhadap mana-mana pegawai, Pegawai Pengawal hendaklah membuat syor dengan sewajarnya kepada Jawatankuasa  Tatatertib.'),
(1856, 'ATPK', 'F', '366', 'Jika sesuatu hutang atau amaun hasil yang tidak boleh diperolehi, Setiausaha Majlis hendaklah melaporkan hal tersebut kepada Majlis untuk keputusan membiarkan hutang tersebut.'),
(1857, 'ATPK', 'F', '367', 'Laporan itu hendaklah mengandungi maklumat seperti dalam Lampiran 23'),
(1858, 'ATPK', 'F', '368', 'Majlis selepas menimbangkan laporan itu, boleh memberikan kelulusan untuk menghapus kira hutang itu jika diputuskan bahawa tindakan lanjut untuk mendapatkannya tidak perlu diambil.'),
(1859, 'ATPK', 'F', '369', 'Jika Majlis berpendapat bahawa surcaj perlu dikenakan terhadap seseorang pegawai kerana kecuaian dalam menjalankan kewajipannya yang mengakibatkan kehilangan hasil, Majlis boleh mengarahkan Setiausaha Majlis untuk mengambil tindakan selanjutnya seperti dalam Tatacara 358.'),
(1860, 'ATPK', 'G', 'BAB G', 'PERSEMBAHAN PENYATA KEWANGAN'),
(1861, 'ATPK', 'G', '370', 'Tujuan amalan terbaik persembahan penyata kewangan adalah :'),
(1862, 'ATPK', 'G', '370 (a)', 'Untuk menyeragamkan asas-asas perakaunan Majlis Agama Islam dari segi penyimpanan akaun dan persembahan penyata kewangan;'),
(1863, 'ATPK', 'G', '370 (b)', 'Bagi meningkatkan akauntabiliti awam melalui penyediaan dan persembahan laporan kewangan yang lebih meyakinkan dengan menggunakan prinsip perakaunan yang diterima umum (GAAP);'),
(1864, 'ATPK', 'G', '370 (c )', 'Memberi gambaran yang lebih jelas dan lengkap dalam mempersembahkan maklumat kewangan kepada pengguna penyata kewangan; dan'),
(1865, 'ATPK', 'G', '370 (d)', 'Untuk meningkatkan lagi mutu dan standard perakaunan Majlis Agama Islam.'),
(1866, 'ATPK', 'G', '370', 'Amalan ini perlu dikemaskini dari semasa ke semasa selari dengan perubahan yang dikeluarkan oleh badan perakaunan profesional.'),
(1867, 'ATPK', 'G', '371', 'Secara umumnya, penyediaan dan persembahan penyata kewangan Majlis hendaklah berdasarkan kepada prinsip perakaunan yang diterima umum seperti mana yang telah diguna pakai bagi semua Badan Berkanun Persekutuan, Badan Berkanun Negeri dan Pihak Berkuasa Tempatan.'),
(1868, 'ATPK', 'G', '372', 'Bagaimanapun, selari dengan aktiviti dan peranan yang dilaksanakan oleh Majlis yang berbeza dengan aktiviti yang dilaksanakan oleh Badan Berkanun Persekutuan, Badan Berkanun Negeri dan Pihak Berkuasa Tempatan, amalan terbaik ini menyeragamkan persembahan penyata kewangan berdasarkan kesesuaian aktiviti tersebut terutamanya urusan zakat, fitrah dan harta wakaf.'),
(1869, 'ATPK', 'G', '373', 'Pengguna Penyata Kewangan'),
(1870, 'ATPK', 'G', '', 'Pada lazimnya, pengguna penyata kewangan Majlis adalah seperti berikut :\r\n(b) Pihak Pengurusan Majlis;\r\n(a) Ahli Majlis;\r\n(c) Juruaudit; dan\r\n(d) Umat Islam yang berminat dengan urusan Majlis.'),
(1871, 'ATPK', 'G', '374', 'Pematuhan Standard Perakaunan Penyata Kewangan Majlis'),
(1872, 'ATPK', 'G', '', 'Sebelum penyata kewangan disediakan, langkah-langkah yang munasabah hendaklah diambil untuk mematuhi perkara-perkara tersebut :\r\n(a) Enakmen penubuhan Majlis;\r\n(b) Standard yang dikeluarkan oleh Lembaga Piawaian Perakaunan Malaysia (MASB);\r\n(c) Pekeliling Perbendaharaan Bil 2/1985;\r\n(d) Tatacara Kewangan Majlis Agama Islam Selangor; dan\r\n(e) Standard yang dikeluarkan ke atas pengurusan kewangan Badan Berkanun diperingkat Persekutuan dan Negeri.'),
(1873, 'ATPK', 'G', '375', 'Penyata Kewangan Majlis perlu dibentangkan setahun sekali. Tahun yang digunakan adalah berdasarkan tahun masihi iaitu bagi tahun berakhir 31 Disember.'),
(1874, 'ATPK', 'G', '376', 'Majlis hendaklah menyediakan penyata kewangan sebaik sahaja tamat tempoh sesuatu tahun kewangan mengikut Enakmen penubuhan Majlis. Sebaik-baiknya penyata kewangan hendaklah disediakan dengan lengkap dan diaudit tidak lewat dari 6 bulan dari tarikh tamat tempoh perakaunan sesuatu tahun.'),
(1875, 'ATPK', 'G', '377', 'Format Dan Kandungan'),
(1876, 'ATPK', 'G', '', 'Penyata Kewangan hendaklah menepati Keperluan Pendedahan Am (General Disclosure Requirements) sebagaimana yang digariskan di dalam Prinsip Perakaunan Yang Diterima Umum. Penyata kewangan yang telah siap diaudit hendaklah dibentangkan sekurang-kurangnya mengikut susunan berikut :\r\n(a) Penyata Pengakuan Majlis;\r\n(b) Penyata Pengakuan Oleh Pegawai Utama;\r\n(c) Laporan Juruaudit;\r\n(d) Kunci Kira-Kira Majlis;\r\n(e) Penyata Pendapatan Majlis;\r\n(f) Penyata Aliran Tunai Malis;\r\n(g) Nota Kepada Penyata Kewangan;\r\n(h) Nota Penjelasan Harta;\r\n(i) Penyata Kumpulanwang Zakat dan Fitrah, Wakaf dan lain-lain kumpulanwang; dan\r\n(j) Penyata-penyata lain yang difikirkan perlu.\r\nPenyata Pendapatan & Kunci Kira-kira Majlis merujuk kepada Perkara-perkara yang berkaitan pentadbiran am Majlis dan apa-apa maksud bagi kumpulanwang ini. Peruntukan tatacara ini hendaklah dibaca bersama Tatacara 383(a)-(c).'),
(1877, 'ATPK', 'G', '378', 'Memandangkan Majlis mengandungi beberapa kumpulanwang utama seperti zakat & fitrah, wakaf dan lain-lain, penyata kumpulanwang bagi kumpulanwang masing-masing hendaklah disediakan. Penyata tersebut seterusnya akan dipindahkan ke kumpulanwang terkumpul masing-masing di bahagian Ekuiti Majlis (Dibiayai Oleh) di dalam Kunci Kira-kira Majlis.'),
(1878, 'ATPK', 'G', '379', 'Sekiranya Majlis mempunyai anak syarikat, penyata kewangan yang disatukan hendaklah disediakan dan tertakluk pada standard yang telah ditetapkan oleh badan perakaunan professional. Sekiranya penyata kewangan tidak disatukan dengan syarikat subsidiarinya, sebab-sebab tidak disatukan hendaklah dinyatakan di dalam Nota kepada Penyata Kewangan.'),
(1879, 'ATPK', 'G', '380', 'Pindahan Kumpulan wang ke Kumpulan wang Majlis\r\nLebihan pendapatan bagi setiap Penyata Kumpulanwang Majlis adalah menggunakan prinsip seperti berikut :\r\n(a) Kumpulanwang Zakat dipindahkan ke butiran Kunci Kira-kira Majlis;\r\n(b) Kumpulanwang Wakaf juga hendaklah dipindahkan ke butiran Kunci Kira-kira Majlis.\r\n(c) Kumpulanwang Baitulmal dipindahkan ke butiran Kunci Kira-kira Majlis.\r\n'),
(1880, 'ATPK', 'G', '381', 'Kunci Kira-kira, Penyata Pendapatan dan Penyata Aliran Tunai hendaklah disediakan dengan cara yang dapat menunjukkan perbandingan dengan angka-angka tahun sebelumnya. Sekiranya terdapat perubahan mengenai cara persembahan butiran tertentu bagi sesuatu akaun telah dibuat dalam tahun semasa, angka bagi tahun sebelumnya hendaklah dinyatakan semula supaya kesan sesuatu perubahan itu ditunjukkan sama ada di dalam akaun-akaun atau sebagai nota di dalam salah satu dari dua tahun perbandingan yang terlibat.'),
(1881, 'ATPK', 'G', '382', 'Contoh Penyata Kewangan Tahunan Majlis Agama Islam adalah seperti di Lampiran A hingga Lampiran E.');
INSERT INTO `reference_detail` (`refyID`, `refCode`, `chapter`, `refNo`, `detail`) VALUES
(1882, 'ATPK', 'G', '383', 'Penyata Kewangan Tahunan yang lengkap hendaklah mengandungi semua kumpulan wang berikut :\r\n(a) Kumpulanwang Baitulmal;\r\n(b) Kumpulanwang Zakat dan Fitrah; dan\r\n(c) Kumpulanwang Wakaf.'),
(1883, 'ATPK', 'G', '383(a)', 'Kumpulan wang ini merujuk kepada Kumpulanwang Majlis yang berkaitan dengan urusan pentadbiran am sesuatu Majlis bagi sesuatu tempoh perakaunan dan perbelanjaan yang digunakan bagi apa-apa maksud di dalam kumpulanwang ini hendaklah berkaitan dengan urusan pengurusan Majlis secara keseluruhan. Contoh format Penyata Kewangan Majlis adalah seperti di Lampiran A1-A6.'),
(1884, 'ATPK', 'G', '383(b)', 'Kumpulan wang ini merujuk kepada urusan perolehan sumber-sumber pungutan zakat oleh pegawai yang diberi kuasa bagi pengagihan kepada golongan asnaf yang layak. Pemilikan atau pemerolehan sesuatu aset bagi tujuan atau maksud kumpulanwang ini merupakan perbelanjaan hangus dan didedahkan bagi tujuan pengagihan kepada asnaf. Tidak ada apa-apa di dalam peruntukan kumpulanwang ini yang membolehkan pemilikan atau pemerolehan bagi sesuatu aset bagi Majlis menggunakan kumpulanwang ini. \r\n\r\nSetiap akhir tahun penutupan sesuatu akaun Majlis, baki akhir Kumpulanwang Zakat dan Fitrah yang belum diagihkan setiap tahun hendaklah dipindahkan terus ke Penyata Pendapatan Kumpulanwang Majlis. Nota kepada penyata kewangan hendaklah ditunjukkan berkaitan pemindahan kumpulanwang tersebut. Contoh format Penyata Kumpulanwang Zakat dan Fitrah adalah seperti di Lampiran B.'),
(1885, 'ATPK', 'G', '383( c)', 'Kumpulanwang ini merujuk kepada urusan pengurusan harta wakaf yang diamanahkan oleh pihak tertentu samada wakaf am atau wakaf khas serta perbelanjaan yang berkaitan dengannya.\r\n\r\nTidak ada apa-apa di dalam peruntukan kumpulanwang ini boleh digunakan bagi pemilikan atau pemerolehan aset kecuali diperolehi daripada :\r\n\r\n(i) Harta wakaf yang diamanahkan oleh pihak tertentu sama ada wakaf am atau wakaf khas; dan\r\n(ii) Keuntungan dari hasil pelaburan harta wakaf dari sumber wakaf am dengan kebenaran Mufti atau pihak-pihak yang berkaitan\r\n\r\nPemilikan atau pemerolehan sesuatu aset bagi tujuan atau maksud kumpulanwang ini merupakan perbelanjaan hangus dan didedahkan di dalam Nota Penjelasan Harta di akhir tempoh perakaunan seperti di Lampiran A6.\r\n\r\nSetiap akhir tahun penutupan sesuatu akaun Majlis, baki terkumpul lebihan Kumpulanwang Wakaf hendaklah dipindahkan ke kumpulanwang Wakaf Terkumpul di dalam Kunci Kira-kira Kumpulanwang Majlis. Nota kepada penyata kewangan hendaklah ditunjukkan berkaitan kumpulanwang tersebut. Contoh format Penyata Kumpulanwang Wakaf adalah seperti di Lampiran C.'),
(1886, 'ATPK', 'G', '384', 'Penyata Kewangan Tahunan hendaklah disediakan dalam bentuk menegak dan menggunakan istilah serta pengkelasan butiran yang membolehkan maklumat penting dikenalpasti dengan mudah. Butiran yang tidak penting hendaklah dikumpulkan dengan butiran lain, yang mempunyai sifat atau jenis yang lebih kurang sama. Penyata Kewangan Tahunan tersebut mestilah menyatakan polisi-polisi perakaunan yang digunakan di dalam penyediaannya.'),
(1887, 'ATPK', 'G', '385', 'Majlis perlu memilih dan mengguna pakai dasar perakaunan yang mematuhi Prinsip Perakaunan Yang Diterima Umum (GAAP). Sekiranya tiada keperluan tertentu, Majlis perlu membentuk dasar untuk memastikan penyediaan penyata kewangan mengandungi maklumat yang :\r\n(a) Relevan dengan keperluan membuat keputusan oleh pengguna; dan\r\n(b) Boleh dipercayai asalkan ia :\r\n(i) menggambarkan kejujuran terhadap keputusan dan kedudukan kewangan Majlis;\r\n(ii) mencerminkan intipati ekonomi bagi sesuatu peristiwa dan urusniaga dan bukan semata-mata bentuk sahnya;\r\n(iii) berkecuali dan bebas daripada berat sebelah;\r\n(iv) berhemat; dan\r\n(v) lengkap dalam semua perkara yang matan.'),
(1888, 'ATPK', 'G', '386', 'Dasar perakaunan merupakan prinsip, asas, konvensyen, peraturan dan amalan tertentu yang diterima pakai oleh Majlis untuk menyediakan dan membentangkan penyata kewangan. Tiga pertimbangan mempengaruhi pemilihan dan penggunaan polisi-polisi perakaunan yang bersesuaian dan juga penyediaan Penyata Kewangan Tahunan iaitu :'),
(1889, 'ATPK', 'G', '386(a)', 'Ketidaktentuan mempengaruhi dalam kebanyakan urusniaga. Dalam hal ini, penghematan hendaklah digunakan dalam menyediakan Penyata Kewangan Tahunan selagi ianya tidak bertentangan dengan kehendak undang-undang Majlis.'),
(1890, 'ATPK', 'G', '386(b)', 'Urusniaga dan kejadian lain mestilah diakaun dan ditunjukkan selaras dengan kepentingan nilai (substance) dan kewangan sebenar (financial reality). Jika kerana sesuatu sebab perkara ini tidak dapat dilakukan, fakta itu hendaklah dinyatakan dengan jelas di dalam Nota kepada penyata kewangan.'),
(1891, 'ATPK', 'G', '386(c )', 'Penyata Kewangan Tahunan hendaklah menunjukkan semua butiran matan yang berkemungkinan akan mempengaruhi penilaian dan keputusan. Butiran yang tidak matan perlu dikumpulkan dengan amaun dan sifat atau fungsi yang serupa dan tidak perlu dibentangkan secara berasingan.'),
(1892, 'ATPK', 'G', '387', 'Penyediaan Penyata Kewangan Tahunan adalah berdasarkan kepada beberapa andaian asas perakaunan. Andaian-andaian ini jika tidak diikuti perlu dinyatakan sebab dan kesan tidak mengikutinya. Andaian-andaian asas perakaunan yang diterima umum adalah seperti berikut :'),
(1893, 'ATPK', 'G', '387(a)', 'Majlis hendaklah dianggap sebagai suatu badan yang terus bergiat menjalankan operasinya untuk masa yang akan datang. Majlis diandaikan sebagai tidak mempunyai niat untuk mengurangkan tahap kepentingan operasinya dalam tempoh masa yang boleh diramalkan.'),
(1894, 'ATPK', 'G', '387(b)', 'Adalah diandaikan polisi-polisi perakaunan Majlis bersifat tekal (tidak berubah) di antara satu jangka masa perakaunan ke satu jangka masa yang lain kecuali :\r\n(i) Perubahan yang ketara sifat aktiviti Majlis atau kajian semula pembentangan penyata kewangannya menunjukkan bahawa perubahan tersebut akan menghasilkan pembentangan peristiwa atau urusniaga yang lebih sesuai; atau\r\n(ii) Perubahan dalam pembentangan butiran yang dikehendaki oleh piawaian badan profesional perakaunan yang diterima umum dan sebarang pengumuman teknikal lain yang diterbitkannya.'),
(1895, 'ATPK', 'G', '387(c )', 'Majlis perlu menyediakan penyata kewangannya dengan menggunakan perakaunan asas akruan melainkan bagi maklumat aliran tunai. Dengan menggunakan kaedah ini, urusniaga dan peristiwa diiktiraf apabila ia berlaku (dan bukan semasa tunai atau keserataannya diterima atau dibayar) dan ia direkodkan dalam rekod perakaunan dan dilaporkan dalam penyata kewangan bagi tempoh yang berkaitan. Belanja diiktiraf pada penyata pendapatan berasaskan hubungan langsung antara belanja yang terlibat dan perolehan butiran pendapatan tertentu (prinsip pemadanan).\r\nTerimaan zakat, wakaf, harta yang diserah selain zakat diiktiraf sebagai pendapatan pada andaian bayaran berdasarkan asas tunai.'),
(1896, 'ATPK', 'G', '388', 'Penyata kewangan bagi sesebuah Majlis hendaklah memasukkan dua sijil :\r\n(a) sijil pertama, ditandatangani oleh dua orang daripada Ahli Majlis dan  hendaklah dibaca sebagaimana  Lampiran D.\r\n(b) sijil kedua, ditandatangani oleh Setiausaha Majlis yang bertanggungjawab keatas pengurusan kewangan hendaklah di baca sebagaimana di  Lampiran E.'),
(1897, 'ATPK', 'G', '389', 'Kunci Kira-kira hendaklah memberi gambaran yang benar dan saksama mengenai kedudukan kewangan Majlis pada suatu tarikh tertentu. Gambaran yang benar dan saksama bermakna pemberitahuan, pengumpulan serta pengkelasan semua butiran yang matan dan tepat serta penggunaan prinsip piawai perakaunan yang tekal.'),
(1898, 'ATPK', 'G', '390', 'Pelaporan Harta Wakaf'),
(1899, 'ATPK', 'G', '390', 'Bagi menggantikan harta wakaf adalah dipegang oleh Majlis sebagai harta yang diamanahkan kepada Majlis untuk menjaga keselamatan harta tersebut, pelaporan harta wakaf di dalam penyata kewangan tidak dapat disediakan dengan menggunakan prinsip-prinsip perakaunan yang sedia ada. Bagi mewujudkan keseragaman dan akauntabiliti Majlis sebagai pemegang  amanah, harta-harta tersebut perlu didedahkan di dalam nota penjelasan harta bersama-sama penyata kewangan majlis yang lain.\r\n\r\nPendedahan harta wakaf di dalam nota kepada penyata kewangan hendaklah mengandungi perkara-perkara berikut :\r\n(a) jenis harta;\r\n(b) kuantiti yang dipegang;\r\n(c) nombor rujukan harta (No. Lot tanah atau bangunan); dan\r\n(d) anggaran nilai pasaran harta (sekiranya ada)\r\n\r\nBagaimanapun aset-aset yang diperolehi menggunakan hasil dari pelaburan harta wakaf mengikut peruntukan tatacara 381(b) tertakluk kepada jenis wakaf hendaklah direkodkan mengikut kaedah perakaunan yang sedia ada.'),
(1900, 'ATPK', 'G', '391', 'Jumlah baki pinjaman yang dikeluarkan hendaklah ditunjukkan di bawah tajuk  ini. Bagi bahagian pinjaman yang sepatutnya sudah dibayar balik tetapi belum diterima, amaun yang berkenaan hendaklah ditunjukkan di bawah asset semasa sebagai pelbagai penghutang. Nota kepada Penyata Kewangan dan jadual sokongan hendaklah disediakan butir-butir berikut yang berkaitan dengan pinjaman tersebut :\r\n(a) jumlah belum berbayar pada permulaan tempoh perakaunan;\r\n(b) jumlah yang diberi di sepanjang tempoh perakaunan;\r\n(c) jumlah pembayaran balik sepanjang tahun;\r\n(d) jumlah baki belum berbayar di penghujung tempoh perakaunan; dan\r\n(e) catatan menyatakan syarat-syarat pembayaran balik dan jumlah   amaun yang sudah sepatutnya berbayar.'),
(1901, 'ATPK', 'G', '392', 'Liabiliti'),
(1902, 'ATPK', 'G', '392', 'Liabiliti jangka panjang hendaklah diasingkan di antara kelas utamanya yang mana termasuk pinjaman kerajaan dan pinjaman bank. Baki di bawah akaun ini mestilah disokong sama ada oleh jadual yang berasingan atau nota kepada Penyata Kewangan dengan butiran-butiran berikut bagi setiap pinjaman:\r\n(a) amaun dipinjam dan tujuannya;\r\n(b) punca pinjaman;\r\n(c) kadar faedah;\r\n(d) amaun pinjaman sudah dibayar;\r\n(e) baki belum dibayar; dan\r\nCatatan menyatakan syarat-syarat pembayaran balik dan amaun yang sudah sepatutnya dibayar. Sekiranya terdapat sebarang ansuran pinjaman sudah sampai tempoh untuk dibayar sepanjang tahun itu dan masih belum dibayar.\r\n'),
(1903, 'ATPK', 'G', '393', 'Suatu Nota Penjelasan mesti dibuat di dalam Nota kepada Penyata Kewangan ataupun Jadual.'),
(1904, 'ATPK', 'G', '394', 'Penyata Pendapatan hendaklah menggambarkan sumber pungutan hasil dan agihan perbelanjaan yang berlaku pada sesuatu tahun bagi Kumpulanwang Majlis. Kaedah pengkelasan hendaklah telah mengambil kira keperluan standard perakaunan yang diterima umum dan butiran tambahan, tajuk dan jumlah kecil perlu dibentangkan pada Penyata Pendapatan bagi membentangkan prestasi kewangan Majlis secara saksama. Kesan pelbagai aktiviti, urusniaga dan peristiwa Majlis berbeza dari segi kestabilan, risiko dan kebolehramalan, dan pendedahan elemen prestasi membantu pemahaman prestasi yang dicapai dalam menilai keputusan masa hadapan.'),
(1905, 'ATPK', 'G', '395', 'Majlis perlu membentangkan, sama ada pada muka Penyata Pendapatan atau dalam nota kepada Penyata Pendapatan, satu analisis belanja yang menggunakan klasifikasi berdasarkan samada pada sifat belanja atau fungsinya.'),
(1906, 'ATPK', 'G', '396', 'Penyata Aliran Tunai hendaklah disertakan bersama penyata-penyata yang lain yang mana menyenaraikan maklumat pengguna Penyata Kewangan bagi menilai kebolehan Majlis menjana tunai dan kesetaraan tunai dan keperluan Majlis menggunakan aliran tunai tersebut.'),
(1907, 'ATPK', 'G', '397', 'Nota Penyata Kewangan perlu :\r\n(a) membentangkan maklumat mengenai asas penyediaan Penyata Kewangan dan dasar perakaunan tertentu yang dipilih dan digunakan untuk sesuatu aktiviti dan butiran penting.\r\n(b) mendedahkan maklumat yang dikehendaki oleh standard perakaunan yang diterima umum yang tidak dibentangkan di mana-mana tempat dalam Penyata Kewangan; dan\r\n(c) menyediakan nota penjelasan bagi maklumat tambahan yang tidak dibentangkan pada muka Penyata Kewangan supaya menunjukkan pembentangan yang saksama.'),
(1908, 'ATPK', 'G', '398', 'Majlis hendaklah menyimpan atau menyebabkan disimpan akaun-akaun yang sempurna dan rekod-rekod sokongan lain mengenai perjalanannya selaras dengan prinsip-prinsip yang diterima umum dibawah standard perakaunan dan garis panduan yang wujud yang dikeluarkan dari semasa ke semasa.'),
(1909, 'ATPK', 'G', '399', 'Keperluan untuk menyediakan jadual atau kertas kerja bagi sebarang baki yang ditunjukkan di dalam Penyata Kewangan Tahunan bergantung kepada bentuk dan cara akaun itu diselenggara.'),
(1910, 'ATPK', 'G', '400', 'Senarai baki-baki bagi akaun-akaun individu dan subsidiari hendaklah disediakan untuk membuktikan kesahihan baki Akaun Kawalan yang ditunjukkan di dalam Penyata Kewangan Tahunan. Jadual dan kertas kerja hendaklah juga disediakan bagi baki tertentu yang mana lebih banyak maklumat diperlukan oleh pihak pengurusan.'),
(1911, 'ATPK', 'G', '401', 'Jadual sokongan dan kertas kerja juga perlu disediakan dan dikemukakan untuk audit dan pihak pengurusan Majlis. Pihak pengurusan Majlis bagaimanapun boleh berbincang dengan Juruaudit mengenai format dan kertas kerja berkaitan yang patut dikemukakan.'),
(1912, 'ATPK', 'G', '402', 'Jadual atau kertas kerja dengan tajuk-tajuk yang bersesuaian hendaklah ditaip, ditarikh dan ditandatangan oleh pegawai yang bertanggungjawab.'),
(1913, 'ATPK', 'G', '403', 'Baki-baki yang wujud dalam Penyata Kewangan Tahunan adalah menggambarkan pengumpulan amaun-amaun dalam akaun-akaun lejar yang berlainan. Jadual menyenaraikan berbagai baki individu di dalam akaun-akaun lejar hendaklah disediakan sebagai kertas kerja di mana praktik.   Di mana perlu, nota-nota juga patut diberi untuk menerangkan sebab-sebab bagi pengumpulan itu.'),
(1914, 'ATPK', 'G', '404', 'Sijil-sijil menunjukkan baki-baki bank, cagaran-cagaran jaminan, yang dipegangkan oleh bank untuk pihak Majlis pada tarikh Kunci Kira-kira hendaklah disediakan untuk juruaudit bagi tujuan verifikasi. Bank hendaklah diminta menghantar sijil-sijil tersebut kepada Juruaudit dalam masa satu bulan selepas berakhirnya tahun kewangan.'),
(1915, 'ATPK', 'G', '405', 'Penyata Penyesuaian Bank pada tarikh Kunci Kira-kira hendaklah disediakan dan dikemukakan untuk diaudit. Surat-surat pengesahan baki tunai di tangan daripada pemegang-pemegang tunai individu pada tarikh Kunci Kira-kira hendaklah juga disediakan untuk diaudit.');
